import { Component, h } from '@stencil/core';
/**
 * This component enhances the visual effects, when the `tiltFollowingTheCursor`
 * utility function from `3d-tilt-hover-effect.ts` is implemented in a component.
 *
 * This component should be added to the HTML structure of the consumer component.
 *
 * This component carries its own styles which are needed to create a glow effect on the
 * 3D element within the parent element, when the parent is hovered.
 *
 * The parent element must be using the `tiltFollowingTheCursor` utility function
 * imported from `3d-tilt-hover-effect.ts`. This function will dynamically
 * affect parts of the styles of this 3D glow effect.
 *
 * @private
 */
@Component({
    tag: 'limel-3d-hover-effect-glow',
    shadow: true,
    styleUrl: '3d-hover-effect-glow.scss',
})
export class HoverEffectGlowComponent {
    public render() {
        return <div />;
    }
}

:host(limel-3d-hover-effect-glow) {
    display: block;
    pointer-events: none;

    position: absolute;
    inset: 0;

    mix-blend-mode: plus-lighter;
}

div {
    position: absolute;
    inset: 0;

    transition: background, opacity;
    transition-timing-function: ease-out; // Must be the same as the 3D Element itself
    transition-duration: 0.8s; // Must be the same as the 3D Element itself

    border-radius: inherit;

    opacity: var(--limel-3d-hover-effect-glow-opacity, 0.1);

    background-image: radial-gradient(
        circle at var(--limel-3d-hover-effect-glow-position, 50% -20%),
        rgb(var(--color-white), 0.3),
        rgb(var(--color-white), 0)
    );
}

import {
    Component,
    Event,
    EventEmitter,
    h,
    Host,
    Prop,
    State,
    Element,
} from '@stencil/core';
import { ListSeparator } from '../list/list-item.types';
import { MenuItem, OpenDirection } from '../menu/menu.types';
import { ActionBarItem } from './action-bar.types';
import { Languages } from './../date-picker/date.types';
import translate from './../../global/translations';
import { isItem } from './is-item';
import { Icon } from '../../global/shared-types/icon.types';

/**
 * An action bar is a user interface element commonly found in software applications and websites.
 * It typically appears at the top of the screen or within a specific section
 * and serves as a centralized hub for accessing various actions and commands
 * relevant to the current context or page.
 *
 * The action bar often contains a set of clickable icons or buttons (icons + labels)
 * that represent specific actions, such as saving, deleting, editing, sharing,
 * or bulk operations for selected items.
 *
 * The purpose of an action bar is to provide quick and convenient access to
 * frequently used functionalities, enabling users to perform common tasks efficiently.
 * It enhances usability by organizing important actions in a visually prominent and easily accessible location.
 *
 * The action bar's design and layout can vary based on the platform or application,
 * but its primary goal remains consistentâ€”to
 * empower users to interact with the software and perform desired actions effortlessly.
 *
 * @exampleComponent limel-example-action-bar-basic
 * @exampleComponent limel-example-action-bar-overflow-menu
 * @exampleComponent limel-example-action-bar-selected-item
 * @exampleComponent limel-example-action-bar-colors
 * @exampleComponent limel-example-action-bar-floating
 * @exampleComponent limel-example-action-bar-floating-expand
 * @exampleComponent limel-example-action-bar-styling
 * @exampleComponent limel-example-action-bar-as-primary-component
 * @exampleComponent limel-example-action-bar-icon-title
 */
@Component({
    tag: 'limel-action-bar',
    shadow: true,
    styleUrl: 'action-bar.scss',
})
export class ActionBar {
    /**
     * Items that are placed in the action bar.
     * These represent primary actions.
     */
    @Prop()
    public actions: Array<ActionBarItem | ListSeparator> = [];

    /**
     * Defines the language for translations.
     */
    @Prop({ reflect: true })
    public language: Languages = document.documentElement.lang as Languages;

    /**
     * A label used to describe the purpose of the element to users
     * of assistive technologies, like screen readers.
     * Example value: "toolbar"
     */
    @Prop({ reflect: true })
    public accessibleLabel?: string;

    /**
     * - When set to `fullWidth`, the component will take the
     * entire width of its container.
     * - When set to `floating`, the component will get basic stylings
     * to visualize the floating state.
     * :::note
     * You should still properly position the component
     * according to the structure of your user interface.
     * For example, use an `absolute` or `fixed` position.
     * :::
     */
    @Prop({ reflect: true })
    public layout?: 'fullWidth' | 'floating';

    /**
     * When set to `true`, the action bar will be collapsible.
     */
    @Prop({ reflect: true })
    public collapsible = false;

    /**
     * Defines the location that the content of the overflow menu
     * appears, in relation to its trigger.
     */
    @Prop({ reflect: true })
    public openDirection: OpenDirection;

    /**
     * Fired when a action bar item has been clicked.
     * @public
     */
    @Event()
    public itemSelected: EventEmitter<ActionBarItem>;

    @Element()
    private readonly host: HTMLElement;

    @State()
    private overflowCutoff: number = this.actions.length;

    /**
     * Indicates whether the action bar is currently in a collapsed state.
     */
    @State()
    private actionBarIsShrunk = false;

    private hasRendered = false;
    private intersectionObserver: IntersectionObserver;
    private isFirstIntersectionCheck = true;
    private actionBarItems: HTMLLimelActionBarItemElement[] = [];

    public connectedCallback() {
        if (this.hasRendered) {
            this.createIntersectionObserver();
        }
    }

    public componentDidRender() {
        if (this.haveItemsChanged()) {
            this.intersectionObserver?.disconnect();
            this.createIntersectionObserver();
        }
    }

    public disconnectedCallback() {
        this.intersectionObserver?.disconnect();
        this.intersectionObserver = undefined;
        this.actionBarItems = [];
    }

    public render() {
        this.hasRendered = true;
        let overflowActions: Array<MenuItem | ListSeparator> = [];
        if (this.actions.length > 0) {
            overflowActions = this.actions.slice(this.overflowCutoff);
        }

        return (
            <Host
                aria-label={this.accessibleLabel}
                class={{
                    'is-full-width': this.layout === 'fullWidth',
                    'is-floating': this.layout === 'floating',
                    'is-shrunk': this.actionBarIsShrunk && this.collapsible,
                    'can-be-shrunk': !!this.collapsible,
                }}
                role="grid"
            >
                <div class="items" role="rowgroup">
                    {this.actions.map(this.renderActionBarItem)}
                </div>
                {this.renderOverflowMenu(overflowActions)}
                {this.renderCollapseExpandButton()}
            </Host>
        );
    }

    private readonly renderActionBarItem = (
        item: ActionBarItem,
        index: number
    ) => {
        return (
            <limel-action-bar-item
                item={item}
                onSelect={this.handleSelect}
                isVisible={this.isVisible(index)}
                role="gridcell"
            />
        );
    };

    private readonly renderOverflowMenu = (
        items: Array<MenuItem | ListSeparator>
    ) => {
        if (!(this.actions.length - this.overflowCutoff)) {
            return;
        }

        const shrunkOverFlowIcon: Icon = {
            name: 'more',
            color: 'rgb(var(--contrast-1000))',
            title: this.getTranslation('action-bar.actions'),
        };

        return (
            <limel-action-bar-overflow-menu
                openDirection={this.openDirection}
                items={items}
                onSelect={this.handleSelect}
                role="gridcell"
                overFlowIcon={
                    this.actionBarIsShrunk ? shrunkOverFlowIcon : undefined
                }
            />
        );
    };

    private renderCollapseExpandButton() {
        if (!this.collapsible || this.actions.length <= 1) {
            return;
        }

        return (
            <button
                class={{
                    'expand-shrink': true,
                }}
                aria-label={this.tooltipLabel}
                type="button"
                onClick={this.handleCollapseExpandClick}
            >
                <limel-icon
                    name="double_left"
                    id="tooltip-expand-shrink-button"
                />
                <limel-tooltip
                    label={this.tooltipLabel}
                    elementId="tooltip-expand-shrink-button"
                />
            </button>
        );
    }

    private handleCollapseExpandClick = () => {
        this.actionBarIsShrunk = !this.actionBarIsShrunk;
    };

    private isVisible(index: number) {
        return index < this.overflowCutoff;
    }

    private readonly handleSelect = (
        event: CustomEvent<ActionBarItem | ListSeparator>
    ) => {
        event.stopPropagation();
        if (isItem(event.detail)) {
            this.itemSelected.emit(event.detail);
        }
    };

    private get tooltipLabel() {
        let key = 'action-bar.collapse';
        if (this.actionBarIsShrunk) {
            key = 'action-bar.expand';
        }

        return this.getTranslation(key);
    }

    private getTranslation = (key: string) => {
        return translate.get(key, this.language);
    };

    private readonly handleIntersection = (
        entries: IntersectionObserverEntry[]
    ) => {
        const intersectingItems = entries.filter(
            (entry) => entry.isIntersecting
        );

        const notIntersectingItems = entries.filter(
            (entry) => !entry.isIntersecting
        );

        if (this.isFirstIntersectionCheck) {
            this.overflowCutoff = intersectingItems.length;
        } else {
            this.overflowCutoff =
                this.overflowCutoff +
                intersectingItems.length -
                notIntersectingItems.length;
        }

        this.isFirstIntersectionCheck = false;
    };

    private createIntersectionObserver() {
        const options = {
            root: this.host.shadowRoot.querySelector('.items'),
            rootMargin: '0px',
            threshold: 1,
        };

        this.overflowCutoff = this.actions.length;
        this.isFirstIntersectionCheck = true;

        this.actionBarItems = [];

        this.intersectionObserver = new IntersectionObserver(
            this.handleIntersection,
            options
        );

        for (const actionBarItem of this.host.shadowRoot.querySelectorAll(
            'limel-action-bar-item'
        )) {
            this.observe(actionBarItem);
        }
    }

    private observe(actionBarItem: HTMLLimelActionBarItemElement) {
        this.intersectionObserver.observe(actionBarItem);
        this.actionBarItems.push(actionBarItem);
    }

    private haveItemsChanged() {
        const someItemRemoved = this.actionBarItems.some(
            (actionBarItem: HTMLLimelActionBarItemElement) =>
                !this.host.shadowRoot.contains(actionBarItem)
        );

        const someItemAdded = [
            ...this.host.shadowRoot.querySelectorAll('limel-action-bar-item'),
        ].some(
            (actionBarItem: HTMLLimelActionBarItemElement) =>
                !this.actionBarItems.includes(actionBarItem)
        );

        return someItemRemoved || someItemAdded;
    }
}

/**
* @prop --action-bar-item-text-color: Text color of action bar items, defaults to `--contrast-1100`.
* @prop --action-bar-item-icon-color: Color of the icons displayed on each action bar item. Defaults to the text color. To specify a color for an individual item, use the `iconColor` prop instead.
* @prop --action-bar-item-max-width: Maximum width of a button in the action bar. Defaults to `10rem`. Keep in mind that the buttons should not appear too big.
* @prop --action-bar-border-radius: Defines the roundness of the corners of the action bar. Defaults to `0`.
* @prop --action-bar-background-color: Background color of the whole component. Defaults to `--contrast-100`.
* @prop --action-bar-shrink-icon-color: Color of the shrink icon. Defaults to `--contrast-1000`.

*/

@use '../../style/mixins';

:host(limel-action-bar) {
    --action-bar-item-height: 2rem;

    --limel-action-bar-item-text-color: var(
        --action-bar-item-text-color,
        rgb(var(--contrast-1100))
    );

    box-sizing: border-box;

    display: inline-flex;
    align-items: center;
    padding: 0.125rem 0.25rem;
    max-width: 100%;
    border-radius: var(--action-bar-border-radius);

    background-color: var(
        --action-bar-background-color,
        rgb(var(--contrast-100))
    );

    transition: max-width 0.3s ease;
}

:host(limel-action-bar),
.items {
    gap: 0.25rem;
    @media (pointer: coarse) {
        gap: 0.5rem;
    }
}

.items {
    display: inline-flex;
    max-width: 100%;
    min-width: 0;
}

:host(limel-action-bar.is-shrunk) .items {
    opacity: 0;
}

:host(limel-action-bar:not(.is-shrunk)) .items {
    opacity: 1;
}

:host(limel-action-bar.is-full-width) {
    width: 100%;
}

:host(limel-action-bar.is-floating) {
    --action-bar-border-radius: 100vw;
    border: 1px solid rgb(var(--contrast-400));

    padding-right: 0.125rem;
    padding-left: 0.125rem;

    max-width: calc(100% - 2rem);
    box-shadow: var(--shadow-depth-16), var(--shadow-depth-8);
}

:host(limel-action-bar.is-shrunk) {
    max-width: 5rem;
    transition: max-width 0.3s ease-in-out;

    .expand-shrink {
        transition: transform 0.3s ease;
        transform: rotateY(180deg);
    }
}

:host(limel-action-bar:not(.is-shrunk)) {
    max-width: 100%;
    transition: max-width 0.3s ease-in-out;

    .expand-shrink {
        transition: transform 0.3s ease;
        transform: rotateY(0deg);
    }
}

:host(limel-action-bar.can-be-shrunk.is-full-width) {
    .expand-shrink {
        margin-left: auto;
    }
}

.expand-shrink {
    all: unset;
    box-sizing: border-box;
    border-radius: 50%;

    @include mixins.is-flat-clickable();
    @include mixins.visualize-keyboard-focus;

    display: flex;
    justify-content: center;
    align-items: center;

    limel-icon {
        width: 1.5rem;
        height: 1.5rem;
        padding: 0.125rem;
        color: var(--action-bar-shrink-icon-color, rgb(var(--contrast-1000)));
    }
}

import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
} from '@stencil/core';
import { ActionBarItem } from '../../action-bar/action-bar.types';
import { ListSeparator } from '../../list/list-item.types';
import { createRandomString } from '../../../util/random-string';
import {
    makeEnterClickable,
    removeEnterClickable,
} from '../../../util/make-enter-clickable';
import {
    getIconColor,
    getIconName,
    getIconTitle,
} from '../../icon/get-icon-props';

/**
 * @private
 */
@Component({
    tag: 'limel-action-bar-item',
    shadow: false,
    styleUrl: 'action-bar-item.scss',
})
export class ActionBarButton {
    /**
     * Item that is placed in the action bar.
     */
    @Prop()
    public item!: ActionBarItem | ListSeparator;

    /**
     * Fired when a action bar item has been clicked.
     * @public
     */
    @Event()
    public select: EventEmitter<ActionBarItem | ListSeparator>;

    /**
     * When the item is displayed in the available width,
     * this will be `false`.
     */
    @Prop({ reflect: true })
    public isVisible: boolean = true;

    /**
     * When the item is selected, this will be `true`.
     */
    @Prop({ reflect: true })
    public selected: boolean = false;

    @Element()
    private readonly host: HTMLLimelActionBarItemElement;

    /**
     * Used to attach the right tooltip to the right button
     */
    private readonly tooltipId: string;

    constructor() {
        this.tooltipId = createRandomString();
    }

    public componentWillLoad() {
        makeEnterClickable(this.host);
    }

    public componentDidLoad() {
        this.triggerIconColorWarning();
    }

    public disconnectedCallback() {
        removeEnterClickable(this.host);
    }

    public render() {
        if (!this.isItem(this.item) && this.item.separator) {
            return <hr />;
        }

        return (
            <button
                id={this.tooltipId}
                type="button"
                onClick={this.handleClick}
                disabled={this.isDisabled()}
                class={{
                    'is-selected': this.isItem(this.item) && this.item.selected,
                }}
            >
                {this.renderIcon()}
                {this.renderLabel()}
                {this.renderTooltip()}
            </button>
        );
    }

    private readonly handleClick = (event: MouseEvent) => {
        event.stopPropagation();
        this.select.emit(this.item);
    };

    private isItem(item: ActionBarItem | ListSeparator): item is ActionBarItem {
        return !('separator' in item);
    }

    private isDisabled() {
        if (this.isItem(this.item) && this.item.disabled) {
            return true;
        }

        if (!this.isVisible) {
            return true;
        }
    }

    private renderIcon() {
        if (this.isItem(this.item) && !this.item.icon) {
            return;
        }

        if ('icon' in this.item) {
            const name = getIconName(this.item.icon);

            const color = getIconColor(this.item.icon, this.item.iconColor);
            const title = getIconTitle(this.item.icon);

            return (
                <limel-icon
                    name={name}
                    aria-label={title}
                    aria-hidden={title ? null : 'true'}
                    style={{
                        '--action-bar-item-icon-color': `${color}`,
                    }}
                />
            );
        }
    }

    private renderLabel() {
        if (!this.isItem(this.item) || this.item.iconOnly) {
            return;
        }

        return <span class="text">{this.item.text}</span>;
    }

    private renderTooltip() {
        if (!this.isItem(this.item)) {
            return;
        }

        return (
            <limel-tooltip
                elementId={this.tooltipId}
                label={this.getTooltipLabel(this.item)}
                helperLabel={this.item.commandText}
            />
        );
    }

    private getTooltipLabel(item: ActionBarItem): string {
        const iconTitle = getIconTitle(item.icon);
        const tooltipLabel = item.text;

        if (iconTitle && tooltipLabel) {
            return `${iconTitle} ${tooltipLabel}`;
        }

        return tooltipLabel;
    }

    private triggerIconColorWarning() {
        if (this.isItem(this.item) && this.item.iconColor) {
            console.warn(
                "The `iconColor` prop is deprecated now! Use the new `Icon` interface and instead of `iconColor: 'color-name'` write `icon {name: 'icon-name', color: 'color-name'}`."
            );
        }
    }
}

@use '../../../style/mixins';
@use '../../../style/functions';

limel-action-bar-item {
    transition: opacity 0.2s ease-in-out;
    position: relative;
    display: flex;
    align-items: center;

    &:not([is-visible]) {
        opacity: 0;
        pointer-events: none;
    }
}

button {
    all: unset;

    &:not([disabled]) {
        @include mixins.is-flat-inset-clickable(
            $color: var(--limel-action-bar-item-text-color),
            $color--hovered: var(--limel-action-bar-item-text-color),
            $background-color: var(--action-bar-background-color),
            $background-color--hovered: var(--action-bar-background-color),
            $background-color--inset: var(--action-bar-background-color)
        );
        @include mixins.visualize-keyboard-focus;
    }

    box-sizing: border-box;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.25rem;

    width: 100%;
    min-width: var(--action-bar-item-height);
    max-width: var(--action-bar-item-max-width, 10rem);
    height: var(--action-bar-item-height);
    color: var(--limel-action-bar-item-text-color);

    border-radius: var(--action-bar-item-height);
    font-size: functions.pxToRem(14);
    padding: 0 0.25rem;

    &:has(.text) {
        padding: 0 0.5rem;
    }

    &[disabled] {
        opacity: 0.4;
    }

    &.is-selected {
        &:not(:hover) {
            box-shadow: var(--button-shadow-inset);
        }
        color: var(--mdc-theme-primary) !important;
    }
}

.text {
    @include mixins.truncate-text();
    padding: 0 0.25rem;
}

limel-icon {
    flex-shrink: 0;
    width: calc(var(--action-bar-item-height) - 0.75rem);
    height: calc(var(--action-bar-item-height) - 0.75rem);
    color: var(
        --action-bar-item-icon-color,
        var(--limel-action-bar-item-text-color)
    );
}

hr {
    all: unset;
    width: 1px;
    height: 1.5rem;
    border-radius: var(--action-bar-item-height);
    background-color: var(--limel-action-bar-item-text-color);
    opacity: 0.2;

    @media (pointer: fine) {
        margin-right: 0.5rem;
        margin-left: 0.5rem;
    }
}

// ðŸ‘‡ Overflow menu

limel-menu {
    --notification-badge-background-color: rgb(var(--contrast-600));
    --notification-badge-text-color: rgb(var(--contrast-1200));

    &[open] {
        button {
            box-shadow: var(--button-shadow-inset);
        }
    }
}

button[slot='trigger'] {
    animation: fade-in ease-out 0.25s;
    font-size: 0.75rem;
    font-weight: bold;
    transform: translate3d(0, 0, 0);
}

@keyframes fade-in {
    0% {
        scale: 0.8;
        opacity: 0;
    }
    100% {
        scale: 1;
        opacity: 1;
    }
}

import { Component, Prop, h, Event, EventEmitter } from '@stencil/core';
import { ActionBarItem } from '../../action-bar/action-bar.types';
import { ListSeparator } from '../../list/list-item.types';
import { MenuItem, OpenDirection } from '../../menu/menu.types';
import { LimelMenuCustomEvent } from '../../../components';
import { Icon } from '../../../global/shared-types/icon.types';

/**
 * @private
 */
@Component({
    tag: 'limel-action-bar-overflow-menu',
    shadow: false,
})
export class ActionBarOverflowMenu {
    /**
     * List of the items that should be rendered in the overflow menu.
     */
    @Prop()
    public items: Array<MenuItem | ListSeparator>;

    /**
     * Defines the location that the content of the overflow menu
     * appears, in relation to its trigger.
     * It defaults to `bottom-end`, since in normal scenarios
     * (for example when the action bar is not floating at the bottom of the screen)
     * this menu is the right-most item in the user interface of the component.
     */
    @Prop({ reflect: true })
    public openDirection: OpenDirection = 'bottom-end';

    /**
     * Icon to display in the overflow menu trigger.
     * If not provided, the number of items in the overflow menu will be displayed.
     */
    @Prop()
    public overFlowIcon?: Icon;

    /**
     * Fired when an item in the action bar overflow menu has been clicked.
     * @public
     */
    @Event()
    public select: EventEmitter<ActionBarItem>;

    public render() {
        return [
            <limel-menu
                openDirection={this.openDirection}
                items={this.items}
                onSelect={this.handleSelect}
            >
                <button slot="trigger">
                    {this.getOverflowTriggerContent()}
                </button>
            </limel-menu>,
        ];
    }

    private getOverflowTriggerContent = () => {
        if (this.overFlowIcon) {
            const { color, name, title } = this.overFlowIcon;

            return (
                <limel-icon
                    style={{
                        color: color,
                    }}
                    name={name}
                    aria-label={title}
                />
            );
        }

        return `+${this.numberOfMenuItems}`;
    };

    private handleSelect = (event: LimelMenuCustomEvent<MenuItem>) => {
        event.stopPropagation();
        this.select.emit(event.detail);
    };

    private get numberOfMenuItems() {
        return this.items.filter((item) => this.isMenuItem(item)).length;
    }

    private isMenuItem(item: MenuItem | ListSeparator): item is MenuItem {
        return !('separator' in item);
    }
}

import { Component, h, Host, Prop } from '@stencil/core';
import { Languages } from './../date-picker/date.types';
import translate from './../../global/translations';

/**
 * This component displays an avatar, representing Lime AI assistants.
 *
 * :::warning
 * This is a private component used internally in the Lime's various applications,
 * which is the reason for having it in Lime Elements â€”to ease the distribution
 * of the component across all our apps.
 *
 * 3rd party developers are not allowed use this component directly.
 * :::
 *
 * @private
 * @exampleComponent limel-example-ai-avatar-basic
 * @exampleComponent limel-example-ai-avatar-colors
 */
@Component({
    tag: 'limel-ai-avatar',
    shadow: true,
    styleUrl: 'ai-avatar.scss',
})
export class AiAvatar {
    /**
     * Set to `true` to trigger animations that indicate that the AI is
     * "thinking" or processing something.
     */
    @Prop({ reflect: true })
    public isThinking = false;

    /**
     * Defines the language for translations.
     */
    @Prop({ reflect: true })
    public language: Languages = document.documentElement.lang as Languages;

    public render() {
        return (
            <Host role="img" aria-label={this.getHostAriaLabel()}>
                {this.renderCircle('red')}
                {this.renderCircle('green')}
                {this.renderCircle('blue')}
                {this.renderCircle('orange')}
                <div class="core" />
                <div class="orbitals" />
            </Host>
        );
    }

    private getHostAriaLabel(): string {
        let thinkingText = '';
        if (this.isThinking) {
            thinkingText = ` (${this.getTranslation('ai-avatar.thinking')})`;
        }

        return `${this.getTranslation('ai-avatar.label')}${thinkingText}`;
    }

    private renderCircle(className: string) {
        return (
            <svg
                class={className}
                viewBox="0 0 100 100"
                xmlns="http://www.w3.org/2000/svg"
                role="presentation"
            >
                <circle
                    cx="50"
                    cy="50"
                    r="40"
                    fill="none"
                    stroke="currentColor"
                    stroke-width="6"
                />
            </svg>
        );
    }

    private getTranslation = (key: string) => {
        return translate.get(key, this.language);
    };
}

/**
* @prop --ai-avatar-animation-play-state: Set it to `running` to start the animation.
*/
:host(limel-ai-avatar) {
    display: flex;
    justify-content: center;
    align-self: center;
    position: relative;

    aspect-ratio: 1;
    max-width: 10rem;
    max-height: 8rem;
    min-width: 1.75rem;
    min-height: 1.75rem;

    border-radius: 0.5rem;
}

* {
    box-sizing: border-box;
}

:host(limel-ai-avatar[is-thinking]:not([is-thinking='false'])) {
    --ai-avatar-animation-play-state: running;
    --ai-avatar-orbitals-opacity: 0.6;
    --ai-avatar-orbitals-animation-play-state: running;
}

.core,
.orbitals {
    position: absolute;
    inset: 0;
    margin: auto;

    display: flex;
    align-items: center;
    justify-content: center;

    aspect-ratio: 1;

    border-radius: 50%;
}

.core {
    opacity: 0.3;
    width: 70%;
    animation: breathe 3s ease infinite
        var(--ai-avatar-animation-play-state, paused);

    background-color: rgb(var(--color-glaucous-darker), 0.6);
    mix-blend-mode: plus-lighter;
}

.orbitals {
    mix-blend-mode: plus-lighter;
    width: clamp(0.375rem, 20%, 3.5rem);

    animation: rotate 5s linear infinite
        var(--ai-avatar-orbitals-animation-play-state, paused);

    transition: opacity 0.2s ease;
    opacity: var(--ai-avatar-orbitals-opacity, 0);

    &:after,
    &:before {
        content: '';
        display: block;
        position: absolute;
        inset: 0;
        margin: auto;

        width: clamp(0.125rem, 50%, 0.75rem);
        aspect-ratio: 1;
        border-radius: 50%;
        background-color: rgb(var(--color-glaucous-lighter));
    }
    &:before {
        animation: orbit 1s linear infinite
            var(--ai-avatar-orbitals-animation-play-state, paused);

        opacity: 0.6;
        transform-origin: -220% 0;
        margin-right: -70%;
    }
    &:after {
        animation: orbit 2s linear infinite
            var(--ai-avatar-orbitals-animation-play-state, paused);

        opacity: 0.8;
        scale: 0.7;
        transform-origin: 0% -250%;
        margin-bottom: -70%;
    }
}

@keyframes orbit {
    0% {
        transform: rotateZ(0deg) translate3d(0, 0, 0);
    }
    100% {
        transform: rotateZ(360deg) translate3d(0, 0, 0);
    }
}

@keyframes breathe {
    1%,
    100% {
        transform: scale(1);
    }
    50% {
        transform: scale(0.86);
    }
}

@keyframes rotate {
    0% {
        transform: rotate(0deg);
    }
    100% {
        transform: rotate(360deg);
    }
}

svg {
    position: absolute;
    margin: auto;
    mix-blend-mode: screen;

    animation-iteration-count: infinite;
    animation-play-state: var(--ai-avatar-animation-play-state, paused);
}

.red {
    rotate: 20deg;
    color: rgb(var(--color-red-default));
    animation-name: rotate, scale-circle-one;
    animation-duration: 5s;
}

.green {
    rotate: 36deg;
    color: rgb(var(--color-green-default));
    animation-name: rotate, scale-circle-two;
    animation-duration: 5.5s;
}

.blue {
    rotate: 100deg;
    color: rgb(var(--color-blue-default));
    animation-name: rotate, scale-circle-three;
    animation-duration: 4.5s;
}

.orange {
    rotate: 165deg;
    color: rgb(var(--color-orange-default));
    animation-name: rotate, scale-circle-four;
    animation-duration: 6.5s;
}

@keyframes scale-circle-one {
    0%,
    100% {
        transform: scaleX(1) scaleY(0.8);
    }
    25%,
    75% {
        transform: scaleX(0.8) scaleY(0.9);
    }
    50% {
        transform: scaleX(1) scaleY(0.7);
    }
}

@keyframes scale-circle-two {
    0%,
    100% {
        transform: scaleX(0.8) scaleY(1);
    }
    25%,
    75% {
        transform: scaleX(0.9) scaleY(0.75);
    }
    50% {
        transform: scaleX(0.72) scaleY(1);
    }
}

@keyframes scale-circle-three {
    0%,
    100% {
        transform: scaleX(0.9) scaleY(0.8);
    }
    50% {
        transform: scaleX(0.75) scaleY(0.9);
    }
}

@keyframes scale-circle-four {
    0%,
    100% {
        transform: scaleX(0.8) scaleY(0.95);
    }
    25%,
    75% {
        transform: scaleX(0.95) scaleY(0.75);
    }
    50% {
        transform: scaleX(0.75) scaleY(0.95);
    }
}

import { Component, Prop, h, Host } from '@stencil/core';
import { abbreviate } from './format';

/**
 * The Badge component can be used to display a notification badge,
 * optionally with a number or a text label.
 *
 * @exampleComponent limel-example-badge
 * @exampleComponent limel-example-badge-number
 * @exampleComponent limel-example-badge-string
 */
@Component({
    tag: 'limel-badge',
    styleUrl: 'badge.scss',
    shadow: true,
})
export class Badge {
    /**
     * Label to display in the badge.
     * Numeric labels larger than 999 will be rounded and abbreviated.
     * String labels get truncated if their length is longer than
     * six characters.
     */
    @Prop({ reflect: true })
    public label?: number | string;

    public render() {
        return (
            <Host
                title={this.labelIsLarge() ? this.label : ''}
                class={{
                    'has-large-label': this.labelIsLarge(),
                }}
            >
                <span>{this.renderLabel()}</span>
            </Host>
        );
    }

    private renderLabel() {
        if (typeof this.label === 'number') {
            return abbreviate(this.label);
        }

        return this.label;
    }

    private labelIsLarge() {
        const largeNumericLabel = 999;
        const largeStringLabel = 6;
        if (
            (typeof this.label === 'number' &&
                this.label > largeNumericLabel) ||
            (typeof this.label === 'string' &&
                this.label.length > largeStringLabel)
        ) {
            return true;
        }
    }
}

@use '../../style/functions';
@use '../../style/mixins';

/**
 * @prop --badge-background-color: badge background color
 * @prop --badge-text-color: badge text color
 * @prop --badge-max-width: maximum width of the badge, before its text gets truncated
 */

:host([hidden]) {
    display: none;
}

:host(limel-badge) {
    --limel-min-badge-size: 1rem;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    flex-shrink: 0;
    min-height: var(--limel-min-badge-size);
    min-width: var(--limel-min-badge-size);
}

span {
    cursor: default;
    box-sizing: border-box;

    @include mixins.truncate-text;
    text-align: center;
    font-size: functions.pxToRem(11);
    line-height: var(--limel-min-badge-size);

    color: var(--badge-text-color, rgb(var(--contrast-1200)));

    border-radius: var(--limel-min-badge-size);
    max-height: var(--limel-min-badge-size);
    min-width: 0.5rem;
    min-height: 0.5rem;

    background-color: var(--badge-background-color, rgb(var(--contrast-500)));

    &:not(:empty) {
        min-width: var(
            --limel-min-badge-size
        ); // ensures that a badge with only one character rendered as perfect circle
        max-width: var(--badge-max-width, 2.75rem);
        padding: 0 functions.pxToRem(4.5);
    }
}

:host(.has-large-label) {
    span {
        cursor: help;
    }
}

import { Component, h, Method, Prop, State } from '@stencil/core';

/**
 * @exampleComponent limel-example-banner
 * @slot buttons - Buttons to show in the banner
 */
@Component({
    tag: 'limel-banner',
    shadow: true,
    styleUrl: 'banner.scss',
})
export class Banner {
    /**
     * The text to show on the banner.
     */
    @Prop({ reflect: true })
    public message: string;

    /**
     * Set icon for the banner
     */
    @Prop({ reflect: true })
    public icon: string;

    @State()
    private isOpen = false;

    /**
     * Open the banner
     */
    @Method()
    public async open() {
        this.isOpen = true;
    }

    /**
     * Close the banner
     */
    @Method()
    public async close() {
        this.isOpen = false;
    }

    public render() {
        return (
            <div
                class={`lime-banner ${this.isOpen ? 'lime-banner--open' : ''}`}
            >
                <div class="lime-banner__surface">
                    <div class="lime-banner__content">
                        <div class="icon-message">
                            {this.renderIcon()}
                            <div>{this.message}</div>
                        </div>
                        <div class="lime-banner__actions">
                            <slot name="buttons" />
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    private renderIcon(): HTMLElement {
        if (!this.icon) {
            return;
        }

        return (
            <div class="lime-banner__icon">
                <limel-icon name={this.icon} badge={true} size="large" />
            </div>
        );
    }
}

@use '../../style/functions';

/**
