import {
    Component,
    Element,
    h,
    Event,
    EventEmitter,
    Prop,
} from '@stencil/core';
import { BreadcrumbsItem } from './breadcrumbs.types';
import {
    makeEnterClickable,
    removeEnterClickable,
} from '../../util/make-enter-clickable';
import { createRandomString } from '../../util/random-string';
import { getIconColor, getIconName } from '../icon/get-icon-props';

/**
 * A Breadcrumb consists of a list of distinct "places" that a user has gone through,
 * before ending up where they are right now, in a website or an application.
 *
 * These "places" can be for example _pages_ of a website, which are hierarchically
 * laid out before the current page that the user is looking at.
 * They could also be _steps_ which the user has gone through, which perhaps have no
 * hierarchical relation with each other, but has eventually led the user "here".
 *
 * :::note
 * - Where the user currently is, is always the last step of the breadcrumb.
 * - A breadcrumbs never shows where users can go after this place.
 * It only illustrates where user has been before ending up here.
 * If the path that a user can take is not changing and if next steps are clear,
 * you can use the [Progress flow component](#/component/limel-progress-flow) instead.
 * :::
 *
 * Breadcrumbs are often placed horizontally before the main content of the current screen.
 *
 * @exampleComponent limel-example-breadcrumbs-links
 * @exampleComponent limel-example-breadcrumbs-buttons
 * @exampleComponent limel-example-breadcrumbs-icons
 * @exampleComponent limel-example-breadcrumbs-divider
 * @exampleComponent limel-example-breadcrumbs-icon-color
 * @exampleComponent limel-example-breadcrumbs-styling
 */

@Component({
    tag: 'limel-breadcrumbs',
    shadow: true,
    styleUrl: 'breadcrumbs.scss',
})
export class Breadcrumbs {
    /**
     * List of items in the breadcrumbs,
     * each representing a step or a page.
     */
    @Prop()
    public items: BreadcrumbsItem[];

    /**
     * The visual divider that separates items.
     * It must be a single character such as `-` or `,`.
     */
    @Prop()
    public divider: string = 'â€º';

    /**
     * Fired when clicking on buttons (not links!)
     * inside the breadcrumbs.
     */
    @Event()
    public select: EventEmitter<BreadcrumbsItem>;

    @Element()
    private host: HTMLLimelBreadcrumbsElement;

    public render() {
        return (
            <ol
                role="navigation"
                aria-label="Breadcrumb"
                style={{ '--limel-breadcrumbs-divider': `'${this.divider}'` }}
            >
                {this.renderSteps()}
                {this.renderLastStep()}
            </ol>
        );
    }

    public componentWillLoad() {
        makeEnterClickable(this.host);
    }

    public disconnectedCallback() {
        removeEnterClickable(this.host);
    }

    private renderSteps = () => {
        const allStepsWithoutLast = this.items.slice(0, -1);

        if (this.areItemsLinks(this.items)) {
            return allStepsWithoutLast.map(this.renderAsLink);
        }

        return allStepsWithoutLast.map(this.renderAsButton);
    };

    private renderAsButton = (item: BreadcrumbsItem) => {
        const tooltipId = createRandomString();

        return [
            <button
                role="listitem"
                id={tooltipId}
                class="step"
                onClick={this.handleClick(item)}
            >
                {this.renderIcon(item)}
                {this.renderLabel(item)}
            </button>,
            this.renderTooltip(item, tooltipId),
        ];
    };

    private renderAsLink = (item: BreadcrumbsItem) => {
        const tooltipId = createRandomString();

        return [
            <a
                role="listitem"
                id={tooltipId}
                class="step"
                href={item.link.href}
                title={item.link.title}
            >
                {this.renderIcon(item)}
                {this.renderLabel(item)}
            </a>,
            this.renderTooltip(item, tooltipId),
        ];
    };

    private renderLastStep = () => {
        const lastItem = this.items.slice(-1);

        return (
            <li
                class="last step"
                tabindex="-1"
                aria-current={this.areItemsLinks(this.items) ? 'page' : 'step'}
            >
                {this.renderIcon(lastItem[0])}
                <span class="text">{lastItem[0].text}</span>
            </li>
        );
    };

    private renderIcon = (item: BreadcrumbsItem) => {
        const name = getIconName(item.icon);
        const color = getIconColor(item.icon);

        if (!name) {
            return;
        }

        return (
            <limel-icon
                style={{
                    color: `${color}`,
                }}
                name={name}
            />
        );
    };

    private renderLabel = (item: BreadcrumbsItem) => {
        if (item.type === 'icon-only') {
            return;
        }

        return <span class="text">{item.text}</span>;
    };

    private renderTooltip = (item: BreadcrumbsItem, tooltipId: string) => {
        if (item.type === 'icon-only') {
            return <limel-tooltip elementId={tooltipId} label={item.text} />;
        }
    };

    private areItemsLinks = (items: BreadcrumbsItem[]) => {
        return items.some((item) => 'link' in item);
    };

    private handleClick = (item: BreadcrumbsItem) => (event: MouseEvent) => {
        event.stopPropagation();
        this.select.emit(item);
    };
}

@use '../../style/mixins';

/**
* @prop --breadcrumbs-item-text-color: Text color of breadcrumbs items, defaults to `--contrast-1100`.
* @prop --breadcrumbs-item-max-width: Maximum width of a button in the breadcrumbs. Defaults to `10rem`. Keep in mind that the buttons should not appear too big.
*/

$padding: 0.5rem;

:host(limel-breadcrumbs) {
    --limel-breadcrumbs-item-height: 1.5rem;
    --limel-breadcrumbs-gap: 0.75rem;
    --limel-breadcrumbs-gap: 0.75rem;
    --limel-breadcrumbs-item-text-color: var(
        --breadcrumbs-item-text-color,
        rgb(var(--contrast-1500))
    );
    @include mixins.fade-out-overflowed-content-on-edges(horizontally);
    --limel-left-edge-fade-width: #{$padding};
    --limel-right-edge-fade-width: #{$padding};
}

ol,
li,
.step {
    all: unset;
}

* {
    box-sizing: border-box;
}

ol {
    padding: $padding; // needed for the focus and hover effects, since we are using overflow
    gap: var(--limel-breadcrumbs-gap);
}

.step {
    position: relative;

    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.25rem;

    max-width: var(--breadcrumbs-item-max-width, 10rem);
    height: var(--limel-breadcrumbs-item-height);

    color: var(--limel-breadcrumbs-item-text-color);
    border-radius: 100vw;
    font-size: 0.875rem;
    padding: 0 0.25rem;

    &:not(:has(.text)) {
        padding: 0 0.125rem;
    }

    &:not(.last) {
        @include mixins.visualize-keyboard-focus;

        &:after {
            content: var(--limel-breadcrumbs-divider);
            display: flex;
            align-items: center;
            justify-content: center;

            width: var(--limel-breadcrumbs-gap);

            position: absolute;
            top: 0;
            right: calc(var(--limel-breadcrumbs-gap) * -1);
            bottom: 0;
            left: auto;

            text-align: center;
        }
    }
}

a.step {
    @include mixins.hyperlink($color: var(--limel-breadcrumbs-item-text-color));
}

button.step {
    &:not(.last) {
        @include mixins.is-flat-clickable(
            $color: var(--limel-breadcrumbs-item-text-color)
        );
    }
}

limel-icon {
    flex-shrink: 0;
    width: calc(var(--limel-breadcrumbs-item-height) - 0.25rem);
    height: calc(var(--limel-breadcrumbs-item-height) - 0.25rem);
}

.text {
    @include mixins.truncate-text;
    padding: 0 0.25rem;
}

.last {
    opacity: 0.7;
}

// LAYOUT:
// The code below makes sure that the last item is always visible,
// and previous items overflow out of the container
// while keeping the container scrollable.
// All without any additional scripts in the component code.

:host(limel-breadcrumbs) {
    box-sizing: border-box;

    display: flex;
    width: 100%;
    overflow-x: auto;
    scrollbar-width: none; // This hides the scrollbars appearing under in Firefox
    -ms-overflow-style: none; // Same as above for IE 11
    &::-webkit-scrollbar {
        display: none; // This hides the scrollbars appearing under in Chrome
    }

    direction: rtl;
}

ol {
    display: flex;
    flex-direction: row-reverse;
    justify-content: flex-end;
    margin-right: auto;
}

.step {
    direction: ltr;
}

import { Component, h, Prop, State, Watch, Element, Host } from '@stencil/core';
import {
    makeEnterClickable,
    removeEnterClickable,
} from '../../util/make-enter-clickable';
import { Icon } from '../../global/shared-types/icon.types';
import { getIconName, getIconTitle } from '../icon/get-icon-props';

/**
 * @exampleComponent limel-example-button-basic
 * @exampleComponent limel-example-button-primary
 * @exampleComponent limel-example-button-outlined
 * @exampleComponent limel-example-button-disabled
 * @exampleComponent limel-example-button-icon
 * @exampleComponent limel-example-button-loading
 * @exampleComponent limel-example-button-click-success
 * @exampleComponent limel-example-button-click-fail
 * @exampleComponent limel-example-button-reduce-presence
 * @exampleComponent limel-example-button-colors
 * @exampleComponent limel-example-button-composite
 */
@Component({
    tag: 'limel-button',
    shadow: { delegatesFocus: true },
    styleUrl: 'button.scss',
})
export class Button {
    /**
     * The text to show on the button.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Set to `true` to make the button primary.
     */
    @Prop({ reflect: true })
    public primary = false;

    /**
     * Set to `true` to make the button outlined.
     */
    @Prop({ reflect: true })
    public outlined = false;

    /**
     * Set icon for the button
     */
    @Prop({ reflect: true })
    public icon: string | Icon;

    /**
     * Set to `true` to disable the button.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Set to `true` to put the button in the `loading` state.
     * This also disables the button.
     */
    @Prop({ reflect: true })
    public loading = false;

    /**
     * Set to `true` to indicate failure instead of success when the button is
     * no longer in the `loading` state.
     */
    @Prop({ reflect: true })
    public loadingFailed = false;

    @State()
    private justLoaded = false;

    @Element()
    private host: HTMLElement;

    private justLoadedTimeout?: number;

    public componentWillLoad() {
        makeEnterClickable(this.host);
    }

    public disconnectedCallback() {
        removeEnterClickable(this.host);
    }

    public render() {
        return (
            <Host onClick={this.filterClickWhenDisabled}>
                <button
                    class={{
                        loading: this.loading,
                        'just-loaded': this.justLoaded && !this.loadingFailed,
                        'just-failed': this.justLoaded && this.loadingFailed,
                        outlined: this.outlined,
                    }}
                    disabled={this.disabled || this.loading}
                    aria-busy={this.loading ? 'true' : 'false'}
                    aria-live="polite"
                >
                    {this.renderIcon(this.icon)}
                    {this.renderLabel()}
                    {this.renderSpinner()}
                    <svg viewBox="0 0 30 30">{this.renderLoadingIcons()}</svg>
                </button>
            </Host>
        );
    }

    @Watch('loading')
    protected loadingWatcher(newValue: boolean, oldValue: boolean) {
        const hasFinishedLoading = this.hasFinishedLoading(newValue, oldValue);
        if (hasFinishedLoading) {
            this.handleLoadingFinished();
        } else if (newValue) {
            this.handleLoadingStarted();
        }
    }

    private hasFinishedLoading(newValue: boolean, oldValue: boolean) {
        return oldValue && !newValue;
    }

    private handleLoadingFinished() {
        this.justLoaded = true;
        const TIMEOUT = 2000;
        this.justLoadedTimeout = window.setTimeout(() => {
            this.justLoaded = false;
        }, TIMEOUT);
    }

    private handleLoadingStarted() {
        this.justLoaded = false;
        window.clearTimeout(this.justLoadedTimeout);
    }

    private renderLoadingIcons() {
        if (this.loadingFailed) {
            return [
                <line x1="9" y1="9" x2="21" y2="21" />,
                <line x1="21" y1="9" x2="9" y2="21" />,
            ];
        }

        return [
            <line x1="8" y1="14" x2="15" y2="20" />,
            <line x1="23" y1="9" x2="14" y2="20" />,
        ];
    }

    private renderIcon(icon?: string | Icon) {
        const iconName = getIconName(icon);
        if (!iconName) {
            return;
        }

        const title = getIconTitle(icon);

        if (!this.label && !title) {
            const WARNING_MESSAGE =
                'âš ï¸ Accessibility warning: `limel-button` has no `label` and its `icon` has no `title`. ' +
                'This creates an inaccessible button for screen reader users. ' +
                'Please add either a `label`, a `title` to the `icon`, or use a `limel-tooltip`. ' +
                'See https://lundalogik.github.io/lime-elements/versions/latest/#/component/limel-button/ ' +
                'for more information.';

            console.warn(WARNING_MESSAGE);
        }

        let iconColor: string | undefined;

        if (typeof icon === 'object') {
            iconColor = icon.color;
        }

        const iconProps = {
            role: 'presentation',
            name: iconName,
            'aria-label': title,
            'aria-hidden': title ? null : 'true', // Use null instead of 'false' to remove attribute
            style: {
                color: iconColor,
            },
        };

        return <limel-icon {...iconProps} />;
    }

    private renderLabel() {
        if (!this.label) {
            return;
        }

        return <span class="label">{this.label}</span>;
    }

    private renderSpinner() {
        if (!this.loading) {
            return;
        }

        return <limel-spinner limeBranded={false} />;
    }

    private filterClickWhenDisabled = (e) => {
        if (this.disabled) {
            e.preventDefault();
        }
    };
}

@use '../../style/functions';
@use '../../style/mixins';

:host {
    display: inline-block;
}

:host([hidden]) {
    display: none;
}

@include mixins.visualize-aria-expanded('button');

button.mdc-button {
    // As long as this component is depended on MDC,
    // we need to force it to be font-agnostic.
    // When MDC-dependency is removed, this block can also be removed.
    // However, on removal of MDC-dependency, we should also make sure to check
    // other font-related styles that might be set by MDC,
    // such as `letter-spacing` or `font-size`.
    font-family: inherit;
}

button {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.125rem;

    border: none;
    border-radius: 0.4rem;
    padding: {
        top: 0;
        right: var(--button-padding-right, 0.5rem);
        bottom: 0;
        left: var(--button-padding-left, 0.5rem);
    }

    min-height: 2.25rem;
    width: 100%;

    &:disabled {
        cursor: not-allowed;

        &.outlined {
            border-color: rgba(var(--contrast-1700), 0.2);
        }
    }

    &:not(:disabled) {
        @include mixins.visualize-keyboard-focus;
        @include mixins.is-elevated-clickable();
    }

    :host(limel-button[primary]) & {
        &:not(:disabled) {
            color: var(--mdc-theme-on-primary, rgb(var(--color-white)));
            background-color: var(
                --mdc-theme-primary,
                rgb(var(--color-teal-default))
            );
        }
        &:disabled {
            background-color: rgba(var(--contrast-1700), 0.15);
        }
    }

    :host(limel-button:not([primary])) & {
        &:not(:disabled) {
            color: var(--mdc-theme-primary, rgb(var(--color-teal-default)));
        }
        &:disabled {
            color: rgba(var(--contrast-1600), 0.37);
            background-color: rgba(var(--contrast-1600), 0.1);
        }
    }
}

.label {
    font-size: 0.875rem;
    font-weight: 600;
    letter-spacing: functions.pxToRem(0.5);
    padding: 0 0.25rem;
}

limel-spinner {
    position: absolute;
}

limel-icon {
    width: 1.25rem;
    flex-shrink: 0;
    vertical-align: top;
}

svg {
    height: functions.pxToRem(30);
    opacity: 0;
    pointer-events: none;
    position: absolute;
    width: functions.pxToRem(30);

    line {
        stroke: rgb(var(--contrast-600));
        stroke-width: 2;
    }
}

limel-icon,
.label,
limel-spinner,
svg {
    transition: opacity 300ms ease-in-out;
}

.loading {
    limel-icon,
    .label {
        opacity: 0;
    }
    limel-spinner {
        opacity: 1;
    }
}

.just-loaded,
.just-failed {
    limel-icon,
    .label {
        opacity: 0;
    }
    svg {
        opacity: 1;
    }
}

.just-failed {
    background-color: var(--lime-error-text-color) !important;
    animation: shake 0.82s cubic-bezier(0.36, 0.07, 0.19, 0.97) both;
}

.outlined {
    border: 1px solid;
    border-color: var(--mdc-theme-primary);
}

@keyframes shake {
    10%,
    90% {
        transform: translate3d(#{functions.pxToRem(-1)}, 0, 0);
    }

    20%,
    80% {
        transform: translate3d(#{functions.pxToRem(2)}, 0, 0);
    }

    30%,
    50%,
    70% {
        transform: translate3d(#{functions.pxToRem(-3)}, 0, 0);
    }

    40%,
    60% {
        transform: translate3d(#{functions.pxToRem(3)}, 0, 0);
    }
}

@import './partial-styles/_has-reduced-presence';

import {
    Component,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { Button } from '../button/button.types';
import { createRandomString } from '../../util/random-string';

/**
 * A button group control is a linear set of two or more buttons.
 *
 * ## Usage
 *
 * Button groups are often used to display different views of the same thing. A
 * common example of this component is when you switch between [ Map | Transit
 * | Satellite ] views to look at an area on the map.
 *
 * In some cases, button groups may serve as quick filters as well. For example
 * a list of contacts, in which the user can switch to [ All | Favorites
 * | Frequently contacted ] can incorporate a button group to quickly filter out
 * items and display subsets of them.
 *
 * ## Layout
 *
 * The button groups are usually placed in top headers and action bars,
 * sometimes with other elements. Since the group items will always be rendered
 * in a row, you must make sure not to have too many buttons in the group.
 * Because if the container of your button group does not get enough space to
 * fit in all its buttons, they will have to truncate their text and may appear
 * very cramped together. Always think about how your button group will appear
 * on a small screen such as phones.
 * :::note
 * Button can contain text or icons, but not both simultaneously!
 * :::
 *
 * Within the group, icon buttons will all have the same width, while each text button
 * inherits its width from its content.
 *
 * @exampleComponent limel-example-button-group-icons
 * @exampleComponent limel-example-button-group
 * @exampleComponent limel-example-button-group-mix
 * @exampleComponent limel-example-button-group-badges
 * @exampleComponent limel-example-button-group-composite
 */
@Component({
    tag: 'limel-button-group',
    shadow: true,
    styleUrl: 'button-group.scss',
})
export class ButtonGroup {
    /**
     * List of buttons for the group
     */
    @Prop()
    public value: Button[] = [];

    /**
     * True if the button-group should be disabled
     */
    @Prop({ reflect: true })
    public disabled: boolean = false;

    /**
     * Dispatched when a button is selected/deselected
     */
    @Event()
    private change: EventEmitter<Button>;

    @State()
    private selectedButtonId: string;

    private radioGroupName = createRandomString();

    constructor() {
        this.renderButton = this.renderButton.bind(this);
        this.onChange = this.onChange.bind(this);
        this.renderContent = this.renderContent.bind(this);
    }

    public componentWillLoad() {
        this.setSelectedButton();
    }

    public render() {
        const classes = {
            'mdc-chip-set': true,
            disabled: this.disabled,
            'mdc-chip-set--choice': true,
        };

        return (
            <div class={classes} role="grid">
                {this.value.map(this.renderButton)}
            </div>
        );
    }

    private renderButton(button: Button) {
        // Prefix with 'b' because html IDs cannot start with a digit,
        // and we need to differentiate from the ID on the limel-icon. /Ads
        const buttonId = `b${button.id}`;

        const classes = {
            'mdc-chip': true,
            'mdc-chip--selected': this.isButtonChecked(button),
        };

        return (
            <div class={classes} role="row">
                <span role="gridcell">
                    <input
                        type="radio"
                        name={this.radioGroupName}
                        checked={this.isButtonChecked(button)}
                        id={buttonId}
                        onChange={this.onChange}
                    />
                    <label htmlFor={buttonId}>
                        {this.renderContent(button)}
                        {this.renderBadge(button)}
                    </label>
                </span>
            </div>
        );
    }

    private renderContent(button: Button) {
        if (button.icon) {
            return this.renderIcon(button);
        }

        return this.renderLabel(button);
    }

    private isButtonChecked(button: Button) {
        return button.id === this.selectedButtonId;
    }

    private renderLabel(button: Button) {
        return <span class="mdc-chip__text">{button.title}</span>;
    }

    private renderIcon(button: Button) {
        // Prefix with 'i' because html IDs cannot start with a digit,
        // and we need to differentiate from the "buttonId". /Ads
        const iconId = `i${button.id}`;

        return [
            <limel-icon
                id={iconId}
                class="mdc-chip__icon"
                aria-label={button.title}
                name={button.icon}
                size="small"
                badge={true}
            />,
            <limel-tooltip elementId={iconId} label={button.title} />,
        ];
    }

    private renderBadge(button: Button) {
        if (!button.badge) {
            return;
        }

        return <limel-badge label={button.badge} />;
    }

    private onChange(event: Event) {
        event.stopPropagation();
        const target = event.target as HTMLInputElement;
        // The ID is prefixed with `b` in the HTML, remember? /Ads
        this.selectedButtonId = target.id.slice(1);
        const button = this.value.find((item) => {
            return item.id === this.selectedButtonId;
        });
        this.change.emit(button);
    }

    private setSelectedButton = () => {
        this.selectedButtonId = this.value.find((button) => {
            return button.selected;
        })?.id;
    };

    @Watch('value')
    protected valueChanged() {
        this.setSelectedButton();
    }
}

@use '../../style/internal/z-index';
@use '../../style/functions';
@use '../../style/internal/shared_input-select-picker';
@use '../../style/mixins';

@use '@material/chips/deprecated/mdc-chips';
/**
* @prop --button-group-background-color: Background color of the component. Defaults to `--contrast-400`
*/

.mdc-chip {
    // As long as this component is depended on MDC,
    // we need to force it to be font-agnostic.
    // When MDC-dependency is removed, this block can also be removed.
    // However, on removal of MDC-dependency, we should also make sure to check
    // other font-related styles that might be set by MDC,
    // such as `letter-spacing` or `font-size`.
    font-family: inherit;
}

.mdc-chip {
    @include mixins.is-flat-inset-clickable($background-color: transparent);
    max-width: 100%;
    min-width: functions.pxToRem(32);
    padding: 0 functions.pxToRem(1);
    display: inline-grid;
    grid-auto-flow: column;
    margin: functions.pxToRem(2) !important;
    font-size: functions.pxToRem(14);

    &:not(:last-child) {
        &:after {
            content: '';
            display: block;

            height: functions.pxToRem(16);
            width: functions.pxToRem(2);
            border-radius: functions.pxToRem(4);
            background-color: var(
                --button-group-text-color,
                rgb(var(--contrast-1200))
            );
            opacity: 0.1;

            position: absolute;
            right: functions.pxToRem(-3);
            top: 0;
            bottom: 0;
            margin: auto;
        }
    }

    label {
        cursor: pointer;
        transition: color 0.2s ease;
        display: flex;
        align-items: center;
        &:has(> limel-badge) {
            .mdc-chip__text {
                padding-right: 0.25rem;
            }
        }
    }

    input[type='radio'] {
        width: 0;
        position: absolute;
        opacity: 0; // This removes some visual defects on iOS and Firefox, created by "user agent styles"

        &:focus-visible {
            + label {
                &:after {
                    // visualizes keyboard navigation on Chrome & Firefox
                    // only when non-pointer input is being used,
                    // e.g. tabbed into using keyboard
                    content: '';
                    display: block;
                    position: absolute;
                    top: 0;
                    right: 0;
                    bottom: 0;
                    left: 0;
                    border-radius: functions.pxToRem(60);
                    box-shadow: var(--shadow-depth-8-focused);
                    z-index: z-index.$button-group-radio-button-keyboard-focused;
                }
            }
        }
    }

    span[role='gridcell'] {
        min-width: 0; // This is needed to force mdc-chip__text (which is inside this span) to truncate

        &:focus-within {
            // Cannot use `:focus-visible` here, since it is the radio button
            // which gets focused, which is inside this elementâ€¦
            // and we are removing or hiding the visual appearance of the button
            outline: none;
            color: var(--mdc-theme-primary);
        }

        &:only-child {
            .mdc-chip__text {
                padding-left: functions.pxToRem(12);
            }
        }

        &:first-child {
            .mdc-chip__text {
                padding-left: functions.pxToRem(12);
            }
        }
    }

    &.mdc-chip--selected {
        background-color: var(--mdc-theme-surface);
        box-shadow: var(--button-shadow-inset);

        &:active {
            box-shadow: var(--button-shadow-inset-pressed);
        }
    }
}

.mdc-chip__text {
    font-size: functions.pxToRem(13);
    overflow: hidden;
    text-overflow: ellipsis;
    display: block;
    padding: 0 functions.pxToRem(12) 0 functions.pxToRem(4);
}

.mdc-chip-set {
    align-items: center;
    border-radius: functions.pxToRem(60);
    background-color: var(
        --button-group-background-color,
        rgb(var(--contrast-400))
    );
    padding: 0;

    width: max-content;
    max-width: 100%;

    flex-wrap: nowrap; // Not sure about this. It'll put items on one row, but also can look bad on action bars. Kia

    &.disabled {
        @include shared_input-select-picker.looks-disabled;
        pointer-events: none;
    }
}

limel-badge {
    margin-right: 0.25rem;
    pointer-events: none;
}

.mdc-chip:not(.mdc-chip--selected) {
    --badge-background-color: rgb(var(--contrast-200));
}

import { Component, h, Prop } from '@stencil/core';
import { CalloutType } from '../callout/callout.types';
import { Languages } from '../date-picker/date.types';
import { getHeading, getIcon } from './callout.helpers';

/**
 * Calloutsâ€”also known as Admonitionsâ€”are useful for including supportive or
 * special content within a large piece of text, or even inside a user
 * interface.
 *
 * When used in a document or text based user interface, the callout attracts
 * the reader's attention to a particular piece of information, without
 * significantly interrupting their flow of reading the document.
 *
 * In a user interface, a callout is more intrusive to the end-user. Still, it
 * could be a good choice when you intend to slightly disturb the user's
 * attention, and challenge them to pay extra attention to the information
 * presented. In such cases, a callout should not be used as a static and
 * constantly present element of the UI. Rather, it should be displayed when
 * something unusual or remarkable demands the user's attention.
 *
 * @exampleComponent limel-example-callout-note
 * @exampleComponent limel-example-callout-important
 * @exampleComponent limel-example-callout-tip
 * @exampleComponent limel-example-callout-caution
 * @exampleComponent limel-example-callout-warning
 * @exampleComponent limel-example-callout-rich-content
 * @exampleComponent limel-example-callout-custom-heading
 * @exampleComponent limel-example-callout-custom-icon
 * @exampleComponent limel-example-callout-styles
 * @exampleComponent limel-example-custom-type
 * @exampleComponent limel-example-callout-composite
 */
@Component({
    tag: 'limel-callout',
    shadow: true,
    styleUrl: 'callout.scss',
})
export class Callout {
    /**
     * Heading of the callout, which can be used to override the
     * default heading which is displayed based on the chosen `type`.
     */
    @Prop({ reflect: true })
    public heading?: string;

    /**
     * Icon of the callout, which can be used to override the
     * default icon which is displayed based on the chosen `type`.
     */
    @Prop({ reflect: true })
    public icon?: string;

    /**
     * Defines how the component is visualized, for example
     * which heading, color or icon is used in its user interface.
     */
    @Prop({ reflect: true })
    public type?: CalloutType = 'note';

    /**
     * Defines the language for translations.
     * Will translate the default headings for supported languages.
     */
    @Prop()
    public language: Languages = 'en';

    public render() {
        return [
            <div class="side" role="presentation">
                <limel-icon name={getIcon(this.icon, this.type)} />
            </div>,
            <div class="main">
                <h1 class="heading">
                    {getHeading(this.heading, this.type, this.language)}
                </h1>
                <slot />
            </div>,
        ];
    }
}

/**
 * @prop --callout-text-color: Text color of the component. Defaults to `--contrast-1100`.
 * @prop --callout-background-color: Background color of the component. Defaults to `--contrast-300`.
 * @prop --callout-color: Color used in the UI to add more contextual meaning about the type of the information. This color is different based on the chosen `type`, but you can override it using this prop.
*/

:host(limel-callout) {
    display: flex;
    border-radius: 0.5rem;
    overflow: hidden;

    --limel-callout-tint-color: var(
        --callout-color,
        rgb(var(--color-contrast-100))
    );
    color: var(--callout-text-color, rgb(var(--contrast-1100)));
}
:host([type='note']) {
    --limel-callout-tint-color: var(
        --callout-color,
        rgb(var(--color-gray-default))
    );
}
:host([type='important']) {
    --limel-callout-tint-color: var(
        --callout-color,
        rgb(var(--color-sky-default))
    );
}
:host([type='tip']) {
    --limel-callout-tint-color: var(
        --callout-color,
        rgb(var(--color-green-default))
    );
}
:host([type='caution']) {
    --limel-callout-tint-color: var(
        --callout-color,
        rgb(var(--color-orange-light))
    );
}
:host([type='warning']) {
    --limel-callout-tint-color: var(
        --callout-color,
        rgb(var(--color-red-dark))
    );
}

.side {
    padding: 0.25rem;
    position: relative;

    &:before {
        content: '';
        position: absolute;
        inset: 0;
        opacity: 0.2;
        background-color: var(--limel-callout-tint-color);
    }

    limel-icon {
        width: 1.5rem;
        color: var(--limel-callout-tint-color);
        margin-top: 0.0625rem; // ensures vertical alignment with the `heading`
    }
}

.main {
    display: flex;
    flex: 1;
    flex-direction: column;
    gap: 0.5rem;
    padding: 0.25rem 0.5rem 0.5rem 0.5rem;
    background-color: var(--callout-background-color, rgb(var(--contrast-300)));
}

.heading {
    margin: 0;
    font-size: 1rem;
    font-weight: 600;
}

import {
    Component,
    h,
    Prop,
    Event,
    EventEmitter,
    Element,
    Host,
} from '@stencil/core';
import { Image } from '../../global/shared-types/image.types';
import { Icon } from '../../global/shared-types/icon.types';
import { isItem } from '../action-bar/is-item';
import { getIconName } from '../icon/get-icon-props';
import { ListSeparator } from '../../global/shared-types/separator.types';
import { ActionBarItem } from '../action-bar/action-bar.types';
import { getMouseEventHandlers } from '../../util/3d-tilt-hover-effect';

/**
 * Card is a component that displays content about a single topic,
 * in a structured way. It can contain a header, and some supporting media such
 * as an image or an icon, a body of text, or optional actions.
 *
 * @exampleComponent limel-example-card-basic
 * @exampleComponent limel-example-card-image
 * @exampleComponent limel-example-card-actions
 * @exampleComponent limel-example-card-clickable
 * @exampleComponent limel-example-card-orientation
 * @exampleComponent limel-example-card-slot
 * @exampleComponent limel-example-card-styling
 * @beta
 */
@Component({
    tag: 'limel-card',
    shadow: true,
    styleUrl: 'card.scss',
})
export class Card {
    /**
     * Heading of the card,
     * to provide a short title about the context.
     */
    @Prop({ reflect: true })
    public heading?: string;

    /**
     * Subheading of the card
     * to provide a short description of the context.
     */
    @Prop({ reflect: true })
    public subheading?: string;

    /**
     * A hero image to display in the card,
     * to enrich the content with visual information.
     */
    @Prop()
    public image?: Image;

    /**
     * An icon, to display along with the heading and subheading.
     */
    @Prop({ reflect: true })
    public icon?: string | Icon;

    /**
     * The content of the card.
     * Supports markdown, to provide a rich text experience.
     */
    @Prop()
    public value?: string;

    /**
     * Actions to display in the card,
     * to provide the user with options to interact with the content.
     */
    @Prop()
    public actions?: Array<ActionBarItem | ListSeparator> = [];

    /**
     * When true, improve the accessibility of the component and hints the user
     * that the card can be interacted width.
     */
    @Prop({ reflect: true })
    public clickable: boolean = false;

    /**
     * The orientation of the card,
     * specially useful when the card has an image.
     */
    @Prop({ reflect: true })
    public orientation: 'landscape' | 'portrait' = 'portrait';

    /**
     * Fired when a action bar item has been clicked.
     */
    @Event()
    public actionSelected: EventEmitter<ActionBarItem>;

    @Element()
    private host: HTMLElement;

    private handleMouseEnter: () => void;
    private handleMouseLeave: () => void;

    public componentWillLoad() {
        const { handleMouseEnter, handleMouseLeave } = getMouseEventHandlers(
            this.host
        );
        this.handleMouseEnter = handleMouseEnter;
        this.handleMouseLeave = handleMouseLeave;
    }

    public render() {
        return (
            <Host
                onMouseEnter={this.handleMouseEnter}
                onMouseLeave={this.handleMouseLeave}
            >
                <section tabindex={this.clickable ? 0 : ''}>
                    {this.renderImage()}
                    <div class="body">
                        {this.renderHeader()}
                        {this.renderSlot()}
                        {this.renderValue()}
                        {this.renderActionBar()}
                    </div>
                    <limel-3d-hover-effect-glow />
                </section>
            </Host>
        );
    }

    private renderImage() {
        if (!this.image) {
            return;
        }

        return <img src={this.image.src} alt={this.image.alt} loading="lazy" />;
    }

    private renderHeader() {
        if (!this.heading && !this.subheading && !this.icon) {
            return;
        }

        return (
            <header>
                {this.renderIcon()}
                <div class="headings">
                    {this.renderHeading()}
                    {this.renderSubheading()}
                </div>
            </header>
        );
    }

    private renderIcon() {
        const icon = getIconName(this.icon);
        const color =
            typeof this.icon === 'string' ? undefined : this.icon?.color;

        if (!icon) {
            return;
        }

        return (
            <limel-icon
                style={{
                    color: `${color}`,
                }}
                badge={true}
                name={icon}
            />
        );
    }

    private renderHeading() {
        if (!this.heading) {
            return;
        }

        return <h1 class="title">{this.heading}</h1>;
    }

    private renderSubheading() {
        if (!this.subheading) {
            return;
        }

        return <h2>{this.subheading}</h2>;
    }

    private renderSlot() {
        return <slot name="component" />;
    }

    private renderValue() {
        if (!this.value) {
            return;
        }

        return <limel-markdown value={this.value} />;
    }

    private handleActionSelect = (
        event: CustomEvent<ActionBarItem | ListSeparator>
    ) => {
        event.stopPropagation();
        if (isItem(event.detail)) {
            this.actionSelected.emit(event.detail);
        }
    };

    private renderActionBar() {
        if (this.actions.length === 0) {
            return;
        }

        return (
            <limel-action-bar
                actions={this.actions}
                onItemSelected={this.handleActionSelect}
            />
        );
    }
}

/**
* @prop --card-heading-color: color of the heading. Defaults to `--contrast-1100`;
