import { Component, Event, EventEmitter, h, Prop, Watch } from '@stencil/core';
import { Languages } from '../date-picker/date.types';
import translate from '../../global/translations';
import { createRandomString } from '../../util/random-string';
import { ChartItem } from './chart.types';

const PERCENT = 100;
const DEFAULT_INCREMENT_SIZE = 10;

/**
 * A chart is a graphical representation of data, in which
 * visual symbols such as such bars, dots, lines, or slices, represent
 * each data point, in comparison to others.
 *
 * @exampleComponent limel-example-chart-stacked-bar
 * @exampleComponent limel-example-chart-orientation
 * @exampleComponent limel-example-chart-max-value
 * @exampleComponent limel-example-chart-type-bar
 * @exampleComponent limel-example-chart-type-dot
 * @exampleComponent limel-example-chart-type-area
 * @exampleComponent limel-example-chart-type-line
 * @exampleComponent limel-example-chart-type-pie
 * @exampleComponent limel-example-chart-type-doughnut
 * @exampleComponent limel-example-chart-type-ring
 * @exampleComponent limel-example-chart-type-gantt
 * @exampleComponent limel-example-chart-type-nps
 * @exampleComponent limel-example-chart-multi-axis
 * @exampleComponent limel-example-chart-multi-axis-with-negative-start-values
 * @exampleComponent limel-example-chart-multi-axis-area-with-negative-start-values
 * @exampleComponent limel-example-chart-axis-increment
 * @exampleComponent limel-example-chart-clickable-items
 * @exampleComponent limel-example-chart-accessibility
 * @exampleComponent limel-example-chart-styling
 * @exampleComponent limel-example-chart-creative-styling
 * @beta
 */

@Component({
    tag: 'limel-chart',
    shadow: true,
    styleUrl: 'chart.scss',
})
export class Chart {
    /**
     * Defines the language for translations.
     * Will translate the translatable strings on the components.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * Helps users of assistive technologies to understand
     * the context of the chart, and what is being displayed.
     */
    @Prop({ reflect: true })
    public accessibleLabel?: string;

    /**
     * Helps users of assistive technologies to understand
     * what the items in the chart represent.
     */
    @Prop({ reflect: true })
    public accessibleItemsLabel?: string;

    /**
     * List of items in the chart,
     * each representing a data point.
     */
    @Prop()
    public items!: ChartItem[];

    /**
     * Defines how items are visualized in the chart.
     */
    @Prop({ reflect: true })
    public type?:
        | 'area'
        | 'bar'
        | 'doughnut'
        | 'line'
        | 'nps'
        | 'pie'
        | 'ring'
        | 'dot'
        | 'stacked-bar' = 'stacked-bar';

    /**
     * Defines whether the chart is intended to be displayed wide or tall.
     * Does not have any effect on chart types which generate circular forms.
     */
    @Prop({ reflect: true })
    public orientation?: 'landscape' | 'portrait' = 'landscape';

    /**
     * Specifies the range that items' values could be in.
     * This is used in calculation of the size of the items in the chart.
     * When not provided, the sum of all values in the items will be considered as the range.
     */
    @Prop({ reflect: true })
    public maxValue?: number;

    /**
     * Specifies the increment for the axis lines.
     */
    @Prop({ reflect: true })
    public axisIncrement?: number;

    /**
     * Indicates whether the chart is in a loading state.
     */
    @Prop({ reflect: true })
    public loading: boolean = false;

    private range: {
        minValue: number;
        maxValue: number;
        totalRange: number;
    };

    /**
     * Fired when a chart item with `clickable` set to `true` is clicked
     */
    @Event()
    public interact: EventEmitter<ChartItem>;

    public componentWillLoad() {
        this.recalculateRangeData();
    }

    public render() {
        if (this.loading) {
            return <limel-spinner limeBranded={false} />;
        }

        return (
            <table
                aria-busy={this.loading ? 'true' : 'false'}
                aria-live="polite"
                style={{
                    '--limel-chart-number-of-items':
                        this.items.length.toString(),
                }}
            >
                {this.renderCaption()}
                {this.renderTableHeader()}
                {this.renderAxises()}
                <tbody class="chart">{this.renderItems()}</tbody>
            </table>
        );
    }

    private renderCaption() {
        if (!this.accessibleLabel) {
            return;
        }

        return <caption>{this.accessibleLabel}</caption>;
    }

    private renderTableHeader() {
        return (
            <thead>
                <tr>
                    <th scope="col">{this.accessibleItemsLabel}</th>
                    <th scope="col">{translate.get('value', this.language)}</th>
                </tr>
            </thead>
        );
    }

    private renderAxises() {
        if (!['bar', 'dot', 'area', 'line'].includes(this.type)) {
            return;
        }

        const { minValue, maxValue } = this.range;
        const lines = [];
        const adjustedMinRange =
            Math.floor(minValue / this.axisIncrement) * this.axisIncrement;
        const adjustedMaxRange =
            Math.ceil(maxValue / this.axisIncrement) * this.axisIncrement;

        for (
            let value = adjustedMinRange;
            value <= adjustedMaxRange;
            value += this.axisIncrement
        ) {
            lines.push(
                <div
                    class={{
                        'axis-line': true,
                        'zero-line': value === 0,
                    }}
                    role="presentation"
                >
                    <limel-badge label={value} />
                </div>
            );
        }

        return (
            <div class="axises" role="presentation">
                {lines}
            </div>
        );
    }

    private renderItems() {
        if (!this.items?.length) {
            return;
        }

        let cumulativeOffset = 0;

        return this.items.map((item, index) => {
            const itemId = createRandomString();
            const sizeAndOffset = this.calculateSizeAndOffset(item);
            const size = sizeAndOffset.size;
            let offset = sizeAndOffset.offset;

            if (this.type === 'pie' || this.type === 'doughnut') {
                offset = cumulativeOffset;
                cumulativeOffset += size;
            }

            return (
                <tr
                    style={this.getItemStyle(item, index, size, offset)}
                    class={this.getItemClass(item)}
                    key={itemId}
                    id={itemId}
                    data-index={index}
                    tabIndex={0}
                    role={item.clickable ? 'button' : null}
                    onClick={this.handleClick}
                    onKeyDown={this.handleKeyDown}
                >
                    <th>{this.getItemText(item)}</th>
                    <td>{this.getFormattedValue(item)}</td>
                    {this.renderTooltip(item, itemId, size)}
                </tr>
            );
        });
    }

    private getItemStyle(
        item: ChartItem,
        index: number,
        size: number,
        offset: number
    ): Record<string, string> {
        const style: Record<string, string> = {
            '--limel-chart-item-offset': `${offset}`,
            '--limel-chart-item-size': `${size}`,
            '--limel-chart-item-index': `${index}`,
            '--limel-chart-item-value': `${item.value}`,
        };

        if (item.color) {
            style['--limel-chart-item-color'] = item.color;
        }

        if (this.type === 'line' || this.type === 'area') {
            const nextItem = this.calculateSizeAndOffset(this.items[index + 1]);

            style['--limel-chart-next-item-size'] = `${nextItem.size}`;
            style['--limel-chart-next-item-offset'] = `${nextItem.offset}`;
        }

        return style;
    }

    private getItemClass(item: ChartItem) {
        return {
            item: true,
            'has-start-value': Array.isArray(item.value),
            'has-negative-value-only':
                this.getMaximumValue(item) < 0 && !this.isRangeItem(item),
        };
    }

    private calculateSizeAndOffset(item?: ChartItem) {
        const { minValue, totalRange } = this.range;
        if (!item) {
            return {
                size: 0,
                offset: 0,
            };
        }

        let startValue = 0;
        if (this.isRangeItem(item)) {
            startValue = this.getMinimumValue(item);
        }

        const normalizedStart =
            ((startValue - minValue) / totalRange) * PERCENT;
        const normalizedEnd =
            ((this.getMaximumValue(item) - minValue) / totalRange) * PERCENT;

        return {
            size: normalizedEnd - normalizedStart,
            offset: normalizedStart,
        };
    }

    private getFormattedValue(item: ChartItem): string {
        const { value, formattedValue } = item;

        if (formattedValue) {
            return formattedValue;
        }

        if (Array.isArray(value)) {
            return `${value[0]} â€” ${value[1]}`;
        }

        return `${value}`;
    }

    private getItemText(item: ChartItem): string {
        return item.text;
    }

    private renderTooltip(item: ChartItem, itemId: string, size: number) {
        const text = this.getItemText(item);
        const PERCENT_DECIMAL = 2;
        const formattedValue = this.getFormattedValue(item);

        const tooltipProps: any = {
            label: text,
            helperLabel: formattedValue,
            elementId: itemId,
        };

        if (this.type !== 'bar' && this.type !== 'dot' && this.type !== 'nps') {
            tooltipProps.label = `${text} (${size.toFixed(PERCENT_DECIMAL)}%)`;
        }

        return (
            <limel-tooltip
                {...tooltipProps}
                openDirection={
                    this.orientation === 'portrait' ? 'right' : 'top'
                }
            />
        );
    }

    private calculateRange() {
        if (this.range) {
            return this.range;
        }

        const minRange = Math.min(0, ...this.items.map(this.getMinimumValue));
        const maxRange = Math.max(...this.items.map(this.getMaximumValue));
        const totalSum = this.items.reduce(
            (sum, item) => sum + this.getMaximumValue(item),
            0
        );

        let finalMaxRange = this.maxValue ?? maxRange;
        if (
            (this.type === 'pie' || this.type === 'doughnut') &&
            !this.maxValue
        ) {
            finalMaxRange = totalSum;
        }

        if (!this.axisIncrement) {
            this.axisIncrement = this.calculateAxisIncrement(this.items);
        }

        const visualMaxValue =
            Math.ceil(finalMaxRange / this.axisIncrement) * this.axisIncrement;
        const visualMinValue =
            Math.floor(minRange / this.axisIncrement) * this.axisIncrement;
        const totalRange = visualMaxValue - visualMinValue;

        return {
            minValue: visualMinValue,
            maxValue: visualMaxValue,
            totalRange: totalRange,
        };
    }

    private calculateAxisIncrement(
        items: ChartItem[],
        steps: number = DEFAULT_INCREMENT_SIZE
    ) {
        const maxValue = Math.max(
            ...items.map((item) => {
                const value = item.value;
                if (Array.isArray(value)) {
                    return Math.max(...value);
                }

                return value;
            })
        );

        const roughStep = maxValue / steps;

        const magnitude = 10 ** Math.floor(Math.log10(roughStep));

        return Math.ceil(roughStep / magnitude) * magnitude;
    }

    private getMinimumValue(item: ChartItem): number {
        const value = item.value;

        return Array.isArray(value) ? Math.min(...value) : value;
    }

    private getMaximumValue(item: ChartItem): number {
        const value = item.value;

        return Array.isArray(value) ? Math.max(...value) : value;
    }

    private isRangeItem(item: ChartItem): item is ChartItem<[number, number]> {
        return Array.isArray(item.value);
    }

    @Watch('items')
    @Watch('axisIncrement')
    @Watch('maxValue')
    handleChange() {
        this.range = null;
        this.recalculateRangeData();
    }

    private recalculateRangeData() {
        this.range = this.calculateRange();
    }

    private getClickableItem(target: HTMLElement): ChartItem | undefined {
        const index = target.dataset.index;
        if (index === undefined) {
            return;
        }

        const item = this.items[Number(index)];
        if (!item.clickable) {
            return;
        }

        return item;
    }

    private readonly handleClick = (event: MouseEvent) => {
        const item = this.getClickableItem(event.currentTarget as HTMLElement);
        if (!item) {
            return;
        }

        event.stopPropagation();
        this.interact.emit(item);
    };

    private readonly handleKeyDown = (event: KeyboardEvent) => {
        if (event.key !== 'Enter' && event.key !== ' ') {
            return;
        }

        const item = this.getClickableItem(event.currentTarget as HTMLElement);
        if (!item) {
            return;
        }

        event.preventDefault();
        this.interact.emit(item);
    };
}

@use '../../style/mixins';
$min-item-size: 0.5rem;
$default-item-color: var(--chart-item-color, rgb(var(--contrast-1100), 0.8));

/**
* @prop --chart-background-color: Defines the background color of the chart. Defaults to `transparent` for _most_ chart types.
* @prop --chart-item-color: If no color is defined for chart items, this color will be use. Defaults to `rgb(var(--contrast-1100), 0.8)`.
* @prop --chart-item-divider-color: Defines the color that visually separates items in some charts, such as `stacked-bar` chart items. Defaults to `rgb(var(--color-white), 0.6)`.
* @prop --chart-axis-line-color: Defines color of the axis lines. Defaults to `--contrast-900`. Note that lines have opacity as well, and get opaque on hover.
* @prop --chart-item-border-radius: Defines the roundness of corners of items in a chart. Defaults to different values depending on the chart type. Does not have any effect on `pie` and `doughnut` types.
*/

:host(limel-chart) {
    --chart-axis-line-color: var(
        --limel-chart-axis-line-color,
        rgb(var(--contrast-900))
    );
    box-sizing: border-box;
    isolation: isolate;

    display: flex;
    width: 100%;
    height: 100%;
    min-width: 0;
    min-height: 0;
    padding: var(--limel-chart-padding);
}

table {
    // Since these are mainly "resets", no styles should be before them.
    all: unset;
    border-collapse: collapse;
    border-spacing: 0;
    empty-cells: show;

    position: relative;
    display: flex;
    width: 100%;
    height: 100%;
    min-width: 0;
    min-height: 0;

    colgroup,
    thead,
    tbody,
    tr,
    th,
    td {
        all: unset;
    }

    caption,
    colgroup,
    thead,
    tfoot,
    th,
    td {
        @include mixins.visually-hidden;
    }
}

*,
*:before,
*:after {
    box-sizing: border-box;
}

.chart {
    position: relative;
    flex-grow: 1;
    width: 100%;
    height: 100%;
    min-height: 0;
    min-width: 0;

    &:has(.item:hover),
    &:has(.item:focus-visible) {
        .item {
            opacity: 0.4;
        }
    }
}

.item {
    @include mixins.visualize-keyboard-focus;
    transition:
        background-color 0.2s ease,
        box-shadow 0.2s ease,
        filter 0.2s ease,
        opacity 0.4s ease;
    cursor: help;

    &:focus-visible,
    &:hover {
        opacity: 1 !important;
    }

    &[role='button'] {
        cursor: pointer;
    }
}

limel-spinner {
    margin: auto;
}

@mixin line(
    $direction: vertical,
    $color: rgb(var(--contrast-800), 0.4),
    $position: center
) {
    @if $direction == vertical {
        background: linear-gradient(to bottom, $color 0%, $color 100%)
            $position/1px
            100%
            no-repeat;
    } @else if $direction == horizontal {
        background: linear-gradient(to right, $color 0%, $color 100%)
            $position/100%
            1px
            no-repeat;
    }
}

@import './partial-styles/_layout-for-charts-with-x-y-axises';
@import './partial-styles/_layout-for-charts-with-circular-shape';
@import './partial-styles/_bar-gantt-dot';
@import './partial-styles/_area_line';
@import './partial-styles/_pie-doughnut';
@import './partial-styles/_ring';
@import './partial-styles/_stacked-bar';
@import './partial-styles/_nps';
@import './partial-styles/_axises';

import { MDCCheckbox, cssClasses } from '@material/checkbox';
import { MDCFormField } from '@material/form-field';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { CheckboxTemplate } from './checkbox.template';
import { Label } from '../dynamic-label/label.types';

/**
 * The Checkbox component is a classic and essential element in UI design that allows
 * users to make multiple selections from a predefined list of options. The Checkbox component is commonly used in forms and settings interfaces to enable users to
 * select one or more items from a list of choices.
 *
 * ## States of a Checkbox
 * When a user clicks or taps on the box, it toggles between two states:
 * Checked and Unchecked.
 *
 * However, a Checkbox can visualize a third state called the "Indeterminate" state.
 * In this state, the checkbox appears as a filled box with a horizontal line or dash inside it.
 *
 * The Indeterminate state is typically used when dealing with checkbox groups
 * that have hierarchical relationships or when the group contains sub-items.
 * This state is used to indicate that that some, but not all, of the items in a group are selected.
 *
 * :::important
 * Checkboxes are sometimes used interchangeably with switches in user interfaces.
 * But there is an important difference between the two! Please read our guidelines about
 * [Switch vs. Checkbox](/#/DesignGuidelines/switch-vs-checkbox.md/).
 *
 * @exampleComponent limel-example-checkbox
 * @exampleComponent limel-example-checkbox-helper-text
 * @exampleComponent limel-example-checkbox-readonly
 */
@Component({
    tag: 'limel-checkbox',
    shadow: true,
    styleUrl: 'checkbox.scss',
})
export class Checkbox {
    /**
     * Disables the checkbox when `true`. Works exactly the same as `readonly`.
     * If either property is `true`, the checkbox will be disabled.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Disables the checkbox when `true`. This visualizes the checkbox slightly differently.
     * But shows no visual sign indicating that the checkbox is disabled
     * or can ever become interactable.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to indicate that the current value is invalid.
     */
    @Prop({ reflect: true })
    public invalid: boolean;

    /**
     * The checkbox label.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Optional helper text to display below the checkbox
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * The value of the checkbox. Set to `true` to make the checkbox checked.
     */
    @Prop({ reflect: true })
    public checked = false;

    /**
     * Enables indeterminate state. Set to `true` to signal indeterminate check.
     */
    @Prop({ reflect: true })
    public indeterminate = false;

    /**
     * Set to `true` to indicate that the checkbox must be checked.
     */
    @Prop({ reflect: true })
    public required: boolean = false;

    /**
     * The labels to use to clarify what kind of data is being visualized,
     * when the component is `readonly`.
     * @beta
     */
    @Prop()
    public readonlyLabels?: Array<Label<boolean>> = [];

    @State()
    private modified = false;
    private shouldReinitialize = false;

    /**
     * Emitted when the input value is changed.
     */
    @Event()
    private change: EventEmitter<boolean>;

    @Element()
    private limelCheckbox: HTMLLimelCheckboxElement;

    private formField: MDCFormField;
    private mdcCheckbox: MDCCheckbox;
    private id: string = createRandomString();
    private helperTextId: string = createRandomString();

    @Watch('checked')
    protected handleCheckedChange(newValue: boolean) {
        if (!this.mdcCheckbox) {
            return;
        }

        this.mdcCheckbox.checked = newValue;
    }

    @Watch('indeterminate')
    protected handleIndeterminateChange(newValue: boolean) {
        this.mdcCheckbox.checked = this.checked;
        this.mdcCheckbox.indeterminate = newValue;
    }

    @Watch('readonly')
    protected handleReadonlyChange() {
        this.destroyMDCInstances();
        this.shouldReinitialize = true;
    }

    componentDidRender() {
        if (this.shouldReinitialize) {
            this.initialize();
            this.shouldReinitialize = false;
        }
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private destroyMDCInstances = () => {
        this.mdcCheckbox?.destroy();
        this.formField?.destroy();

        const checkboxElement = this.getCheckboxElement();
        if (checkboxElement) {
            checkboxElement.classList.remove(
                cssClasses.ANIM_CHECKED_INDETERMINATE,
                cssClasses.ANIM_CHECKED_UNCHECKED,
                cssClasses.ANIM_INDETERMINATE_CHECKED,
                cssClasses.ANIM_INDETERMINATE_UNCHECKED,
                cssClasses.ANIM_UNCHECKED_CHECKED,
                cssClasses.ANIM_UNCHECKED_INDETERMINATE
            );
        }
    };

    public disconnectedCallback() {
        this.destroyMDCInstances();
    }

    public render() {
        return (
            <CheckboxTemplate
                disabled={this.disabled || this.readonly}
                label={this.label}
                readonlyLabels={this.readonlyLabels}
                helperText={this.helperText}
                helperTextId={this.helperTextId}
                checked={this.checked || this.indeterminate}
                indeterminate={this.indeterminate}
                required={this.required}
                readonly={this.readonly}
                invalid={this.isInvalid()}
                onChange={this.onChange}
                id={this.id}
            />
        );
    }

    private isInvalid = () => {
        if (this.invalid) {
            return true;
        }

        if (this.required && this.modified && !this.checked) {
            return true;
        }
    };

    private initialize = () => {
        const element =
            this.limelCheckbox.shadowRoot.querySelector('.mdc-form-field');
        if (!element) {
            return;
        }

        this.formField = new MDCFormField(element);
        this.mdcCheckbox = new MDCCheckbox(this.getCheckboxElement());
        this.formField.input = this.mdcCheckbox;
    };

    private getCheckboxElement = () => {
        return this.limelCheckbox.shadowRoot.querySelector('.mdc-checkbox');
    };

    private onChange = (event: Event) => {
        event.stopPropagation();
        this.change.emit(this.mdcCheckbox.checked);
        this.modified = true;
    };
}

@use '@material/checkbox';
@use '@material/form-field';

/**
* @prop --checkbox-unchecked-border-color: Affects the border color of the default state of the checkbox (when it is not checked). Defaults to `--contrast-900`.
*/

@mixin custom-checkbox-styles {
    // This is used in other components too, such as `limel-list`
    --mdc-checkbox-checked-color: var(--mdc-theme-primary);
    --mdc-checkbox-unchecked-color: var(
        --checkbox-unchecked-border-color,
        rgb(var(--contrast-900))
    );
    --lime-checkbox-unchecked-color: rgb(var(--contrast-300));
}

:host(limel-checkbox) {
    @include custom-checkbox-styles;
    min-height: 2.5rem;
}

limel-dynamic-label {
    margin-top: 0.375rem;
    margin-left: 0.25rem;
}

@include checkbox.core-styles;
@include form-field.core-styles;

.mdc-form-field {
    // As long as this component is depended on MDC,
    // we need to force it to be font-agnostic.
    // When MDC-dependency is removed, this block can also be removed.
    // However, on removal of MDC-dependency, we should also make sure to check
    // other font-related styles that might be set by MDC,
    // such as `letter-spacing` or `font-size`.
    font-family: inherit;
}

.mdc-form-field {
    display: flex;
    align-items: flex-start;

    .mdc-checkbox {
        .mdc-checkbox__native-control {
            &[disabled],
            &:enabled {
                &:not(:checked):not(:indeterminate) {
                    ~ .mdc-checkbox__background {
                        background-color: var(--lime-checkbox-unchecked-color);
                    }
                }
            }
        }

        &.mdc-checkbox--invalid {
            .mdc-checkbox__native-control:enabled:not(:checked):not(
                    :indeterminate
                )
                ~ .mdc-checkbox__background {
                border-color: var(--lime-error-text-color);
            }
        }
        .mdc-checkbox__native-control {
            &:focus-visible {
                + .mdc-checkbox__background {
                    &:after {
                        content: '';
                        display: block;
                        position: absolute;
                        inset: -0.25rem;
                        border-radius: 0.25rem;
                        box-shadow: var(--shadow-depth-8-focused);
                    }
                }
            }
        }
    }
    .mdc-checkbox--disabled {
        opacity: 0.5;
    }

    label {
        cursor: pointer;
        line-height: normal;
        letter-spacing: normal;

        padding-top: 0.75rem;
        padding-left: 0;

        &.mdc-checkbox--required::after {
            margin-left: 0.0625rem;
            content: '*';
        }

        &.mdc-checkbox--invalid {
            color: var(--lime-error-text-color);
        }
    }
}

@import './partial-styles/_helper-text.scss';

import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Host,
    Prop,
} from '@stencil/core';
import { Icon } from '../../global/shared-types/icon.types';
import { Languages } from '../date-picker/date.types';
import { Link } from '../../global/shared-types/link.types';
import { getRel } from '../../util/link-helper';
import { getIconName } from '../icon/get-icon-props';
import {
    makeEnterClickable,
    removeEnterClickable,
} from '../../util/make-enter-clickable';
import translate from '../../global/translations';
import { BACKSPACE, DELETE } from '../../util/keycodes';
import { ChipType, Chip as OldChipInterface } from '../chip-set/chip.types';
import { Image } from '../../global/shared-types/image.types';
import { isEmpty } from 'lodash-es';

import { ListSeparator } from '../list/list-item.types';
import { LimelMenuCustomEvent, MenuItem } from '../../components';

interface ChipInterface extends Omit<OldChipInterface, 'id' | 'badge'> {
    /**
     * Identifier for the chip. Must be unique.
     */
    identifier?: number | string;

    /**
     * The value of the badge.
     */
    badge?: string | number;
}

/**
 * Chips and buttons are both interactive elements in UI design,
 * but they serve different purposes and are used in different contexts.
 *
 * :::warning
 * Do not use the chip component carelessly, as an alternative for
 * [`limel-button`](#/component/limel-button/) in the UI design!
 *
 * **Buttons:**
 * Buttons are used to trigger actions. They are typically used to
 * submit forms, open dialogs, initiate a process, or perform any action
 * that changes the state of the application.
 * Buttons' labels usually contain action words, in other words, the labels is
 * a _verb in imperative mood_ such as "Submit" or "Delete".
 * Buttons are placed in areas where it's clear they will initiate
 * an action when clicked.
 *
 * **Chips:**
 * Chips however are elements which may look like buttons, but they are
 * representing choices, filters, or tags, in a small block
 * or clearly bundled into a group. Chips are rarely used alone in the
 * user interface.
 * They are often used in a so called "chip-set", or placed together in
 * a section of the UI, where the user can expect more than one chip to be present.
 *
 * For example, a chip may represent a filter in a filter bar, or a tag in a tag list,
 * or an item in a shopping list.
 * Clicking a chip can also trigger an action, for example toggling a filter ON or OFF,
 * or opening a page with all posts tagged with the tag represented by the chip,
 * or navigating to a page with more information about the item in the shopping list.
 * :::
 *
 * @exampleComponent limel-example-chip-button
 * @exampleComponent limel-example-chip-link
 * @exampleComponent limel-example-chip-icon-colors
 * @exampleComponent limel-example-chip-image
 * @exampleComponent limel-example-chip-badge
 * @exampleComponent limel-example-chip-filter
 * @exampleComponent limel-example-chip-removable
 * @exampleComponent limel-example-chip-menu
 * @exampleComponent limel-example-chip-loading
 * @exampleComponent limel-example-chip-progress
 * @exampleComponent limel-example-chip-size
 * @exampleComponent limel-example-chip-readonly-border
 * @exampleComponent limel-example-chip-aria-role
 */
@Component({
    tag: 'limel-chip',
    shadow: { delegatesFocus: true },
    styleUrl: 'chip.scss',
})
export class Chip implements ChipInterface {
    /**
     * Defines the language for translations.
     * Will translate the translatable strings on the components.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * Label displayed on the chip
     */
    @Prop({ reflect: true })
    public text: string;

    /**
     * Icon of the chip.
     */
    @Prop()
    public icon?: string | Icon;

    /**
     * A picture to be displayed instead of the icon on the chip.
     */
    @Prop()
    public image?: Image;

    /**
     * If supplied, the chip will become a clickable link.
     */
    @Prop()
    public link?: Omit<Link, 'text'>;

    /**
     * The value of the badge, displayed on the chip.
     */
    @Prop({ reflect: true })
    public badge?: string | number;

    /**
     * Set to `true` to disable the chip.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Set to `true` to render the chip as a static UI element.
     * Useful when the parent component has a `readonly` state.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to visualize the chip in a "selected" state.
     * This is typically used when the chip is used in a chip-set
     * along with other chips.
     */
    @Prop({ reflect: true })
    public selected = false;

    /**
     * Set to `true` to visualize the chip in an "invalid" or "error" state.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * Set to `true` to render a remove button on the chip.
     */
    @Prop({ reflect: true })
    public removable = false;

    /**
     * Set to `filter` to render the chip with a distinct style
     * suitable for visualizing filters.
     *
     * @beta
     */
    @Prop({ reflect: true })
    public type?: ChipType = 'default';

    /**
     * Set to `true` to put the component in the `loading` state,
     * and render an indeterminate progress indicator inside the chip.
     * This does _not_ disable the interactivity of the chip!
     */
    @Prop({ reflect: true })
    public loading? = false;

    /**
     * Reflects the current value of a progress bar on the chip,
     * visualizing the percentage of an ongoing process.
     * Must be a number between `0` and `100`.
     */
    @Prop({ reflect: true })
    public progress?: number;

    /**
     * Identifier for the chip. Must be unique.
     */
    @Prop({ reflect: true })
    public identifier?: number | string = crypto.randomUUID();

    /**
     * Defines the size of the chip.
     */
    @Prop({ reflect: true })
    public size: 'small' | 'default' = 'default';

    /**
     * When provided, the chip will render an ellipsis menu with the supplied items.
     * Also, this will hide the "remove button" when `removable={true}`, as
     * the remove button will automatically become the last item in the menu.
     */
    @Prop()
    public menuItems?: Array<MenuItem | ListSeparator> = [];

    /**
     * Fired when clicking on the remove button of a `removable` chip.
     * The value of `identifier` is emitted as the event detail.
     */
    @Event()
    public remove: EventEmitter<number | string>;

    /**
     * Emitted when a menu item is selected from the actions menu.
     */
    @Event()
    public menuItemSelected: EventEmitter<MenuItem>;

    @Element()
    private host: HTMLLimelChipElement;

    public componentWillLoad() {
        makeEnterClickable(this.host);
    }

    public disconnectedCallback() {
        removeEnterClickable(this.host);
    }

    public render() {
        return (
            <Host onClick={this.filterClickWhenDisabled}>
                {this.link ? this.renderAsLink() : this.renderAsButton()}
            </Host>
        );
    }

    private renderAsButton = () => {
        return [
            <button
                id={'chip-' + this.identifier}
                class="chip"
                role="button"
                disabled={this.disabled || this.readonly}
                aria-busy={this.loading ? 'true' : 'false'}
                aria-live="polite"
                onKeyDown={this.handleDeleteKeyDown}
            >
                {this.renderSpinner()}
                {this.renderPicture()}
                {this.renderLabel()}
                {this.renderBadge()}
                {this.renderProgressBar()}
            </button>,
            this.renderRemoveButton(),
            this.renderActionsMenu(),
        ];
    };

    private renderAsLink = () => {
        const rel = getRel(this.link?.target, this.link?.rel);

        return [
            <a
                id={'chip-' + this.identifier}
                class="chip"
                href={this.link.href}
                title={this.link.title}
                target={this.link.target}
                rel={rel}
                aria-disabled={this.disabled || this.readonly}
                tabindex={this.disabled || this.readonly ? -1 : 0}
                onKeyDown={this.handleDeleteKeyDown}
            >
                {this.renderSpinner()}
                {this.renderPicture()}
                {this.renderLabel()}
                {this.renderBadge()}
                {this.renderProgressBar()}
            </a>,
            this.renderRemoveButton(),
            this.renderActionsMenu(),
        ];
    };

    private renderLabel = () => {
        return <span class="text">{this.text}</span>;
    };

    private renderPicture() {
        const icon = getIconName(this.icon);

        if (!icon && !this.image) {
            return;
        }

        if (!isEmpty(this.image)) {
            return (
                <img src={this.image.src} alt={this.image.alt} loading="lazy" />
            );
        }

        return (
            <limel-icon
                badge={true}
                name={icon}
                style={{
                    color: `${(this.icon as Icon)?.color}`,
                    'background-color': `${
                        (this.icon as Icon)?.backgroundColor
                    }`,
                }}
            />
        );
    }

    private renderBadge() {
        if (!this.badge) {
            return;
        }

        return <limel-badge label={this.badge} />;
    }

    private renderRemoveButton() {
        if (
            !this.removable ||
            this.readonly ||
            this.disabled ||
            !!this.menuItems?.length
        ) {
            return;
        }

        const svgData =
            '<svg viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg"><path fill="none" stroke="currentColor" stroke-width="2" d="m8 8 16 16M24 8 8 24"/></svg>';

        return (
            <button
                class="trailing-button remove-button"
                tabIndex={-1}
                aria-label={this.removeChipLabel}
                aria-controls={'chip-' + this.identifier}
                innerHTML={svgData}
                onClick={this.handleRemoveClick}
            />
        );
    }

    private renderActionsMenu() {
        if (!this.menuItems?.length) {
            return;
        }

        const svgData =
            '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 32 32" xml:space="preserve"><circle fill="currentColor" cx="16" cy="16" r="2"/><circle fill="currentColor" cx="16" cy="24" r="2"/><circle fill="currentColor" cx="16" cy="8" r="2"/></svg>';

        const menuItems = this.getMenuItems();

        return (
            <limel-menu
                items={menuItems}
                onSelect={this.handleActionMenuSelect}
                openDirection="bottom-end"
                onCancel={this.handleActionMenuCancel}
            >
                <button
                    slot="trigger"
                    disabled={this.disabled}
                    class="trailing-button"
                    aria-label={this.actionMenuLabel}
                    innerHTML={svgData}
                />
            </limel-menu>
        );
    }

    private getMenuItems() {
        let menuItems = [...this.menuItems];

        if (this.removable) {
            menuItems = [
                ...menuItems,
                { separator: true },
                {
                    text: this.removeChipLabel(),
                    icon: {
                        name: 'delete_sign',
                        color: 'rgb(var(--color-red-default))',
                    },
                    value: '_remove',
                },
            ];
        }

        return menuItems;
    }

    private filterClickWhenDisabled = (e) => {
        if (this.disabled || this.readonly) {
            e.preventDefault();
        }
    };

    private handleRemoveClick = (event: MouseEvent | KeyboardEvent) => {
        event.stopPropagation();
        this.remove.emit(this.identifier);
    };

    private handleDeleteKeyDown = (event: KeyboardEvent) => {
        if (!this.removable) {
            return;
        }

        const keys = [DELETE, BACKSPACE];

        if (keys.includes(event.key)) {
            this.handleRemoveClick(event);
        }
    };

    private removeChipLabel = (): string => {
        return translate.get('remove', this.language) + ' ' + this.text;
    };

    private actionMenuLabel = (): string => {
        return translate.get('file-viewer.more-actions', this.language);
    };

    private renderSpinner() {
        if (!this.loading) {
            return;
        }

        return <limel-linear-progress indeterminate={true} />;
    }

    private renderProgressBar() {
        if (!this.progress) {
            return;
        }

        const currentPercentage = this.progress + '%';

        return (
            <div
                role="progressbar"
                aria-label="%"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={this.progress}
                style={{
                    '--limel-chip-progress-percentage': currentPercentage,
                }}
            />
        );
    }

    private handleActionMenuSelect = (
        event: LimelMenuCustomEvent<MenuItem>
    ) => {
        const menuItem = event.detail;

        if (!menuItem) {
            return;
        }

        if (menuItem.value === '_remove') {
            this.remove.emit(this.identifier);

            return;
        }

        this.menuItemSelected.emit(menuItem);
    };

    private handleActionMenuCancel = (event: LimelMenuCustomEvent<void>) => {
        event.stopPropagation();
    };
}

@use '../../style/mixins';

/**
* @prop --chip-max-width: Maximum width of the chip. Defaults to `10rem`. Keep in mind that the chips should not appear too big.
* @prop --chip-progress-color: Color of the progress bar. Defaults to `rgb(var(--contrast-700))`.
* @prop --chip-readonly-border-color: Color of the border in readonly state. Defaults to `rgb(var(--contrast-800), 0.5)`.
*/

:host(limel-chip) {
    --limel-chip-height: var(--limel-chip-size, 1.75rem);
    --limel-chip-gap: 0.5rem;
    isolation: isolate;
    display: inline-flex;
    align-items: center;
    min-width: 0;
}

:host(limel-chip[size='small']) {
    --limel-chip-height: 1.5rem;
    --limel-chip-gap: 0.25rem;
}

* {
    box-sizing: border-box;
}

.chip {
    all: unset;
    position: relative;

    min-width: 0;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.25rem;

    max-width: min(var(--chip-max-width, 100%), 25rem);
    height: var(--limel-chip-height);

    border-radius: var(--limel-chip-height);
    font-size: clamp(
        0.8125rem /* 13px */,
        calc(var(--limel-chip-height) - 0.875rem /* 14px */),
        1rem
    );
    padding: 0 0.125rem;

    &:has(limel-icon),
    &:has(img) {
        .text {
            padding-left: 0;
        }
    }

    &:not([disabled]) {
        @include mixins.visualize-keyboard-focus;
        @include mixins.is-elevated-inset-clickable;
    }

    &:has(limel-badge) {
        padding-right: 0.375rem;

        .text {
            padding-right: 0;
        }
    }

    &:has(+ .remove-button:hover) {
        box-shadow: var(--shadow-depth-8-error);
    }

    &:has(+ limel-menu),
    &:has(+ .trailing-button) {
        padding-right: calc(var(--limel-chip-height) + 0.125rem);

        .text {
            padding-right: 0;
        }
    }
}

:host(limel-chip[disabled]:not([disabled='false'])) {
    .chip {
        // Similar to `limel-button[disabled]`
        color: rgba(var(--contrast-1600), 0.37);
        background-color: rgba(var(--contrast-1600), 0.1);
        box-shadow: none;
    }
}

:host(limel-chip[readonly]:not([readonly='false'])) {
    .chip {
        box-shadow: 0 0 0 1px
            var(--chip-readonly-border-color, rgb(var(--contrast-800), 0.5));
    }
}

:host(limel-chip[selected]:not([selected='false'])) {
    .chip {
        box-shadow: var(--button-shadow-inset);

        &:focus-visible {
            box-shadow:
                var(--button-shadow-inset), var(--shadow-depth-8-focused);
        }

        &:active {
            box-shadow: var(--button-shadow-inset-pressed);
        }
    }
    .text {
        color: var(--mdc-theme-primary);
    }
}

:host(limel-chip[type='filter']) {
    .chip {
        border-top-left-radius: 0.5rem;
        border-bottom-left-radius: 0.5rem;

        &:after {
            content: '';
            transition:
                background-color 0.4s ease 0.2s,
                box-shadow 0.6s ease 0.2s;
            box-sizing: border-box;
            position: absolute;
            bottom: 0.125rem;
            left: 0.125rem;
            width: 0.5rem;
            height: 0.5rem;
            border-radius: 50%;
            background-color: rgb(var(--contrast-800), 0.8);
            box-shadow: 0 0 0 1px rgb(var(--color-white)) inset;
        }
    }
}

:host(limel-chip[type='filter'][selected]) {
    .chip {
        &:after {
            background-color: rgb(var(--color-green-default));
            box-shadow:
                0 0 0.375rem 0 rgb(var(--color-green-light)),
                0 0 0 1px rgb(var(--color-white)) inset;
        }
    }
}

:host(limel-chip[invalid]) {
    .chip {
        background: url("data:image/svg+xml;charset=utf-8, <svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 8 8' style='fill-rule:evenodd;'><path fill='rgba(186,186,192,0.16)' d='M0 0h4v4H0zM4 4h4v4H4z'/></svg>");
        background-size: 0.5rem;
        background-color: rgb(var(--color-red-default));
    }

    .text {
        color: rgb(var(--color-white));
    }
}

img,
limel-icon {
    flex-shrink: 0;
    width: calc(var(--limel-chip-height) - 0.25rem);
    height: calc(var(--limel-chip-height) - 0.25rem);
}

img {
    object-fit: cover;
    border-radius: 50%;
}

limel-icon {
    padding: 0.0625rem;
}

limel-badge {
    pointer-events: none;
}

.text {
    @include mixins.truncate-text;
    line-height: 1.2;
    padding: 0 0.5rem;
}

.trailing-button {
    all: unset;
    @include mixins.is-flat-clickable();

    z-index: 1;
    flex-shrink: 0;
    display: flex;
    align-items: center;
    justify-content: center;

    margin-left: calc(var(--limel-chip-height) * -1 + 0.125rem);
    margin-right: 0.125rem;
    width: calc(var(--limel-chip-height) - 0.25rem);
    height: calc(var(--limel-chip-height) - 0.25rem);

    border-radius: 50%;

    svg {
        transition:
            color 0.2s ease,
            transform 0.2s ease;
        width: 1.25rem;
    }

    &.remove-button:hover {
        color: rgb(var(--color-red-dark));
        svg {
            transform: scale(0.8);
        }
    }
}

limel-menu {
    button[slot='trigger'] {
        @include mixins.visualize-keyboard-focus;
    }

    &[open] {
        button[slot='trigger'] {
            box-shadow: var(--button-shadow-inset);
        }
    }
}

@import './partial-styles/_loading.scss';
@import './partial-styles/_progress.scss';

components.overview:
:::note
**Regarding `click` and `interact` events:**

The `interact` event is emitted when a chip is interacted with, and is
the recommended way to listen for chip interactions.

However, if you need to handle clicks differently depending on which chip
was clicked, or whether the click was on a chip or elsewhere, you need to
listen to the native `click` event instead.

Native `click` events are passed through, and if the click came from
a chip, the chip object is available in the event object under
`<event object>.Lime.chip`.

Example usage:
```ts
private handleClick(event: Event) {
    if (event && 'Lime' in event && (event.Lime as any).chip) {
        if ((event.Lime as { chip: Chip }).chip.href) {
            // Chip has href, so let the browser open the link.
            return;
        }
        // handle click on chip without href
    } else {
        // handle click elsewhere
    }
}
```
:::

