sources.source:
import { Chip, ChipType } from '../chip-set/chip.types';
import { Languages } from '../date-picker/date.types';
import { MDCTextField } from '@material/textfield';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Method,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { handleKeyboardEvent } from './chip-set-input-helpers';
import translate from '../../global/translations';
import { getHref, getTarget } from '../../util/link-helper';
import { isEqual } from 'lodash-es';
import { LimelChipCustomEvent } from '../../components';
import { createRandomString } from '../../util/random-string';

const INPUT_FIELD_TABINDEX = 1;

/**
 * :::note
 * **Regarding `click` and `interact` events:**
 *
 * The `interact` event is emitted when a chip is interacted with, and is
 * the recommended way to listen for chip interactions.
 *
 * However, if you need to handle clicks differently depending on which chip
 * was clicked, or whether the click was on a chip or elsewhere, you need to
 * listen to the native `click` event instead.
 *
 * Native `click` events are passed through, and if the click came from
 * a chip, the chip object is available in the event object under
 * `<event object>.Lime.chip`.
 *
 * Example usage:
 * ```ts
 * private handleClick(event: Event) {
 *     if (event && 'Lime' in event && (event.Lime as any).chip) {
 *         if ((event.Lime as { chip: Chip }).chip.href) {
 *             // Chip has href, so let the browser open the link.
 *             return;
 *         }
 *         // handle click on chip without href
 *     } else {
 *         // handle click elsewhere
 *     }
 * }
 * ```
 * :::
 *
 * @exampleComponent limel-example-chip-set
 * @exampleComponent limel-example-chip-set-choice
 * @exampleComponent limel-example-chip-set-filter
 * @exampleComponent limel-example-chip-set-filter-badge
 * @exampleComponent limel-example-chip-set-input
 * @exampleComponent limel-example-chip-set-input-type-with-menu-items
 * @exampleComponent limel-example-chip-set-input-type-text
 * @exampleComponent limel-example-chip-set-input-type-search
 * @exampleComponent limel-example-chip-icon-color
 * @exampleComponent limel-example-chip-set-image
 * @exampleComponent limel-example-chip-set-composite
 */
@Component({
    tag: 'limel-chip-set',
    shadow: true,
    styleUrl: 'chip-set.scss',
})
export class ChipSet {
    /**
     * List of chips for the set
     */
    @Prop()
    public value: Chip[] = [];

    /**
     * Type of chip set
     *
     * - `choice` renders a set of selectable chips where only one is selectable. The `removable` property is ignored
     * - `filter` renders a set of selectable chips where all are selectable.
     * - `input` renders a set of chips that can be used in conjunction with an input field
     *
     * If no type is set, a basic set of chips without additional functionality will be rendered
     */
    @Prop({ reflect: true })
    public type?: 'choice' | 'filter' | 'input';

    /**
     * Label for the chip-set
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Optional helper text to display below the chipset.
     * When type is `input`, the helper text is displayed below the
     * input field when it has focus.
     * When type is not `input`, the helper text is always displayed
     * if the device is touch screen; otherwise it is shown when chip-set
     * is hovered or focused using keyboard navigation.
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * True if the chip set should be disabled
     */
    @Prop({ reflect: true })
    public disabled: boolean = false;

    /**
     * For chip-sets of type `input`, set to `true` to disable adding and
     * removing chips, but allow interaction with existing chips in the set.
     * For any other types, setting either `readonly` or `disabled` disables
     * the chip-set.
     */
    @Prop({ reflect: true })
    public readonly: boolean = false;

    /**
     * Set to `true` to indicate that the current value of the input field is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * For chip-sets of type `input`. Value to use for the `type` attribute on the
     * input field inside the chip-set.
     */
    @Prop({ reflect: true })
    public inputType: 'search' | 'text' = 'text';

    /**
     * For chip-sets of type `input`. Limits the maximum number of chips.
     * When the value is `0` or not set, no limit is applied.
     */
    @Prop({ reflect: true })
    public maxItems: number;

    /**
     * True if the control requires a value
     */
    @Prop({ reflect: true })
    public required: boolean = false;

    /**
     * Search label to display when type is `input` and component is in search mode
     */
    @Prop({ reflect: true })
    public searchLabel: string;

    /**
     * Whether the input field should be emptied when the chip-set loses focus.
     */
    @Prop({ reflect: true })
    public emptyInputOnBlur: boolean = true;

    /**
     * Whether the "Clear all" buttons should be shown
     */
    @Prop()
    public clearAllButton: boolean = true;

    /**
     * For chip-sets of type `input`. When the value is null, no leading icon is used.
     * Leading icon to show to the far left in the text field
     */
    @Prop({ reflect: true })
    public leadingIcon: string = null;

    /**
     * For chip-set of type `input`. Sets delimiters between chips.
     */
    @Prop({ reflect: true })
    public delimiter: string = null;

    /**
     * For chip-set of type `input`, defines whether the input field should have autocomplete enabled.
     * Read more about the `autocomplete` attribute
     * [here](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/autocomplete).
     */
    @Prop({ reflect: true })
    public autocomplete: string = 'off';

    /**
     * Defines the language for translations.
     * Will translate the translatable strings on the components. For example, the clear all chips label.
     */
    @Prop()
    public language: Languages = 'en';

    /**
     * Dispatched when a chip is interacted with
     */
    @Event()
    private readonly interact: EventEmitter<Chip>;

    /**
     * Dispatched when a chip is selected/deselected
     */
    @Event()
    private readonly change: EventEmitter<Chip | Chip[]>;

    /**
     * Emitted when an input chip set has received focus and editing in the text field has started
     */
    @Event()
    private readonly startEdit: EventEmitter<void>;

    /**
     * Emitted when an input chip set has lost focus and editing in the text field has ended
     */
    @Event()
    private readonly stopEdit: EventEmitter<void>;

    /**
     * Dispatched when the input is changed for type `input`
     */
    @Event()
    private readonly input: EventEmitter<string>;

    @Element()
    private readonly host: HTMLLimelChipSetElement;

    @State()
    private editMode: boolean = false;

    @State()
    private textValue: string = '';

    @State()
    private blurred: boolean = false;

    @State()
    private inputChipIndexSelected: number = null;

    @State()
    private selectedChipIds: Array<string | number>;

    private mdcTextField: MDCTextField;
    private readonly handleKeyDown = handleKeyboardEvent;
    private labelId: string;

    constructor() {
        this.labelId = createRandomString();
        this.renderChip = this.renderChip.bind(this);
        this.renderInputChip = this.renderInputChip.bind(this);
        this.isFull = this.isFull.bind(this);
        this.handleTextFieldFocus = this.handleTextFieldFocus.bind(this);
        this.handleInputBlur = this.handleInputBlur.bind(this);
        this.handleTextInput = this.handleTextInput.bind(this);
        this.inputFieldOnChange = this.inputFieldOnChange.bind(this);
        this.handleKeyDown = this.handleKeyDown.bind(this);
        this.inputHidden = this.inputHidden.bind(this);
        this.handleDeleteAllIconClick =
            this.handleDeleteAllIconClick.bind(this);
        this.renderDelimiter = this.renderDelimiter.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    private initialize() {
        if (this.value.length > 0) {
            this.selectedChipIds = this.value
                .filter((chip) => chip.selected)
                .map((chip) => chip.id);
        }
    }

    /**
     * Used to find out whether the chip-set is in edit mode.
     *
     * @returns `true` if the chip-set is in edit mode, `false` otherwise.
     */
    @Method()
    public async getEditMode(): Promise<boolean> {
        return this.editMode;
    }

    /**
     * Used to set focus to the chip-set input field.
     *
     * @param emptyInput - if `true`, any text in the input is discarded
     * @returns does not return anything, but methods have to be async
     */
    @Method()
    public async setFocus(emptyInput: boolean = false) {
        if (this.disabled || this.readonly) {
            return;
        }

        this.editMode = true;
        if (emptyInput) {
            this.textValue = '';
        }

        this.host.shadowRoot.querySelector('input').focus();
        this.startEdit.emit();
    }

    /**
     * Used to empty the input field. Used in conjunction with `emptyInputOnBlur` to let the
     * consumer control when the input is emptied.
     *
     * @returns does not return anything, but methods have to be async
     */
    @Method()
    public async emptyInput() {
        this.syncEmptyInput();
    }

    public componentDidLoad() {
        this.triggerIconColorWarning(this.value);

        if (this.type === 'input') {
            this.mdcTextField = new MDCTextField(
                this.host.shadowRoot.querySelector('.mdc-text-field')
            );
        }
    }

    public componentDidUpdate() {
        const input = this.host.shadowRoot.querySelector('input');
        if (input && this.editMode) {
            input.focus();
        }
    }

    public disconnectedCallback() {
        if (this.mdcTextField) {
            this.mdcTextField.destroy();
        }
    }

    public render() {
        const classes = {
            'mdc-chip-set': true,
            'mdc-text-field--with-trailing-icon': true,
            disabled: this.disabled || this.readonly,
        };

        if (this.type) {
            classes[`mdc-chip-set--${this.type}`] = true;
        }

        if (this.type === 'input') {
            Object.assign(classes, {
                'mdc-text-field': true,
                'mdc-text-field--outlined': true,
                'mdc-chip-set--input': true,
                'lime-text-field--readonly': this.readonly,
                'has-chips': this.value.length > 0,
                'has-leading-icon': this.leadingIcon !== null,
                'has-clear-all-button': this.clearAllButton,
            });
        }

        const value = this.getValue();

        return [
            <limel-notched-outline
                labelId={this.labelId}
                label={this.label}
                required={this.required}
                invalid={this.invalid || this.isInvalid()}
                disabled={this.disabled}
                readonly={this.readonly}
                hasValue={!!this.value?.length}
                hasLeadingIcon={!!this.leadingIcon}
                hasFloatingLabel={this.floatLabelAbove()}
            >
                <div slot="content" {...this.getContentProps()} class={classes}>
                    {this.renderContent(value)}
                </div>
            </limel-notched-outline>,
            this.renderHelperLine(),
        ];
    }

    private getContentProps() {
        if (this.type === 'input') {
            return {
                onClick: this.handleTextFieldFocus,
            };
        }

        return {
            role: 'grid',
        };
    }

    private renderContent = (value: Chip[]) => {
        if (this.type === 'input') {
            return this.renderInputChips();
        }

        return value.map(this.renderChip);
    };

    private readonly getValue = () => {
        return this.value.map((chip) => ({
            ...chip,
            ...(this.type && {
                selected: this.selectedChipIds.includes(chip.id),
            }),
        }));
    };

    @Watch('value')
    protected handleChangeChips(newValue: Chip[], oldValue: Chip[]) {
        if (isEqual(newValue, oldValue)) {
            return;
        }

        this.syncEmptyInput();
        this.initialize();
    }

    private renderInputChips() {
        return [
            this.value.map(this.renderInputChip),
            <input
                tabIndex={INPUT_FIELD_TABINDEX}
                type={this.inputType}
                id={this.labelId}
                disabled={this.readonly || this.disabled}
                class={{
                    'mdc-text-field__input': true,
                    hidden: this.inputHidden(),
                }}
                value={this.textValue}
                onBlur={this.handleInputBlur}
                onFocus={this.handleTextFieldFocus}
                onKeyDown={this.handleKeyDown}
                onInput={this.handleTextInput}
                // Some browsers emit a change event on input elements, we need to stop
                // that event from propagating since we are emitting our own change event
                onChange={this.inputFieldOnChange}
                placeholder={this.isFull() ? '' : this.searchLabel}
                readonly={this.isFull()}
                autocomplete={this.autocomplete}
            />,
            this.renderLeadingIcon(),
            this.renderClearAllChipsButton(),
        ];
    }

    private readonly floatLabelAbove = () => {
        if (
            this.value.length > 0 ||
            this.editMode ||
            this.readonly ||
            this.textValue
        ) {
            return true;
        }
    };

    private isFull(): boolean {
        return !!this.maxItems && this.value.length >= this.maxItems;
    }

    private isInvalid() {
        if (this.readonly) {
            // A readonly field can never be invalid.
            return false;
        }

        if (this.invalid) {
            return true;
        }

        if (!this.required) {
            return false;
        }

        if (!this.blurred) {
            return false;
        }

        return !this.value?.length;
    }

    private inputFieldOnChange(event) {
        event.stopPropagation();
    }

    /**
     * Enter edit mode when the text field receives focus. When editMode is true, the input element will be visible
     */
    private handleTextFieldFocus() {
        if (this.disabled || this.readonly) {
            return;
        }

        if (this.editMode) {
            return;
        }

        this.editMode = true;
        this.startEdit.emit();
    }

    /**
     * Exit edit mode when the input element loses focus. This makes sure the input element does not take up any
     * additional space when the user it not typing anything
     */
    private handleInputBlur() {
        if (this.emptyInputOnBlur) {
            this.syncEmptyInput();
        }

        this.editMode = false;
        this.blurred = true;
        this.inputChipIndexSelected = null;

        // This timeout is needed in order to let a new element receive focus
        setTimeout(() => {
            this.stopEdit.emit();
        }, 0);
    }

    private syncEmptyInput() {
        this.textValue = '';
    }

    private inputHidden() {
        if (this.editMode) {
            return this.isFull();
        }

        // If there are chips in the picker, hide the input to avoid the input
        // being placed on a new line and adding ugly space beneath the chips.
        // If there are no chips, show the input, or the picker will look weird.
        return !!this.value?.length;
    }

    private handleTextInput(event) {
        event.stopPropagation();
        this.inputChipIndexSelected = null;
        this.textValue = event.target.value;
        this.input.emit(event.target.value?.trim());
    }

    private emitInteraction(chip: Chip) {
        this.interact.emit(chip);
    }

    private renderChip(chip: Chip) {
        const chipType: ChipType =
            this.type === 'filter' ? 'filter' : 'default';

        const chipProps = this.getChipProps(chip, chipType);

        return <limel-chip {...chipProps} />;
    }

    private readonly hasHelperText = () => {
        return this.helperText !== null && this.helperText !== undefined;
    };

    private readonly renderHelperLine = () => {
        const maxItems = this.maxItems === 1 ? undefined : this.maxItems;

        if (!maxItems && !this.hasHelperText()) {
            return;
        }

        return (
            <limel-helper-line
                length={this.value.length}
                maxLength={maxItems}
                helperText={this.helperText}
                invalid={this.isInvalid()}
            />
        );
    };

    private renderInputChip(chip: Chip, index: number, chips: Chip[]) {
        const chipProps = this.getChipProps(chip, 'default');
        const isLastChip = index === chips.length - 1;

        return [
            <limel-chip
                key={chip.id}
                class={{
                    'can-be-removed': this.inputChipIndexSelected === index,
                }}
                {...chipProps}
            />,
            !(isLastChip && this.inputHidden()) && this.renderDelimiter(),
        ];
    }

    private getChipProps(chip: Chip, chipType: ChipType) {
        const removable =
            this.type === 'input' && chip.removable && !this.readonly;
        const readonly = this.readonly && this.type !== 'input';

        return {
            role: 'row',
            identifier: chip.id,
            text: chip.text,
            icon: chip.icon,
            image: chip.image,
            badge: chip.badge,
            selected: chip.selected,
            disabled: this.disabled,
            loading: chip.loading,
            readonly: readonly,
            type: chipType,
            removable: removable,
            menuItems: chip.menuItems,
            onClick: this.catchInputChipClicks(chip),
            onRemove: this.handleRemoveChip,
            ...(chip.href && {
                link: {
                    href: getHref(chip.href),
                    target: getTarget(chip.href),
                },
            }),
        };
    }

    private readonly catchInputChipClicks = (chip: Chip) => (event: Event) => {
        /*
         * We need to add the `chip` to the event object so that the consumer
         * can get the chip object when the chip is clicked.
         * This is necessary for the consumer to be able to handle the click
         * event itself, based on which chip was clicked, or whether the click
         * was on a chip or elsewhere. The reason the consumer can't just look
         * at the event target is that that information is hidden by the
         * shadow DOM.
         *
         * See documentation for the `interact` event for more information.
         */
        (event as any).Lime = { chip: chip };

        if (this.isSelectableChip(chip)) {
            this.updateSelectedChipIds(chip);
            this.change.emit(chip);
        }

        this.emitInteraction(chip);
    };

    private isSelectableChip(chip: Chip): boolean {
        return this.type !== 'input' && 'selected' in chip;
    }

    private updateSelectedChipIds(chip: Chip): void {
        chip.selected = !chip.selected;
        const id = chip.id;
        if (this.type === 'choice') {
            this.updateChoiceTypeSelectedIds(id);
        } else {
            this.updateFilterTypeSelectedIds(id);
        }
    }

    private updateChoiceTypeSelectedIds(id: number | string): void {
        this.selectedChipIds = this.isChipSelected(id) ? [] : [id];
    }

    private isChipSelected(id: number | string): boolean {
        return this.selectedChipIds.includes(id);
    }

    private updateFilterTypeSelectedIds(id: number | string): void {
        if (this.isChipSelected(id)) {
            this.removeChipIdFromSelectedChipIds(id);
        } else {
            this.addChipIdToSelectedChipIds(id);
        }
    }

    private removeChipIdFromSelectedChipIds(id: number | string): void {
        this.selectedChipIds = this.selectedChipIds.filter(
            (chipId) => chipId !== id
        );
    }

    private addChipIdToSelectedChipIds(id: number | string): void {
        this.selectedChipIds = [...this.selectedChipIds, id];
    }

    private readonly handleRemoveChip = (
        event: LimelChipCustomEvent<string | number>
    ) => {
        this.removeChip(event.detail);
    };

    private readonly removeChip = (identifier: string | number) => {
        const newValue = this.value.filter((chip) => {
            return chip.id !== identifier;
        });
        this.change.emit(newValue);
    };

    private renderLeadingIcon() {
        if (!this.leadingIcon) {
            return;
        }

        return (
            <i class="mdc-text-field__icon search-icon">
                <limel-icon name={this.leadingIcon} />
            </i>
        );
    }

    private renderClearAllChipsButton() {
        if (this.disabled || this.readonly || !this.clearAllButton) {
            return;
        }

        return (
            <a
                href=""
                onClick={this.handleDeleteAllIconClick}
                class="mdc-text-field__icon clear-all-button"
                tabindex="0"
                role="button"
                title={this.clearAllChipsLabel()}
                aria-label={this.clearAllChipsLabel()}
            />
        );
    }

    private readonly clearAllChipsLabel = (): string => {
        return translate.get('chip-set.clear-all', this.language);
    };

    private handleDeleteAllIconClick(event: Event) {
        event.preventDefault();
        this.change.emit([]);
    }

    private renderDelimiter() {
        if (!this.delimiter) {
            return;
        }

        return <div class="delimiter">{this.delimiter}</div>;
    }

    private triggerIconColorWarning(value: Chip[]) {
        for (const chip of value) {
            if (
                chip.icon &&
                (chip.iconFillColor ||
                    chip.iconBackgroundColor ||
                    chip.iconTitle)
            ) {
                console.warn(
                    "The `iconFillColor`, `iconBackgroundColor`, and `iconTitle` props are deprecated now! Use the new `Icon` interface and instead of `iconColor: 'color-name', `iconBackgroundColor: 'color-name', and `iconTitle: 'title'`, write `icon { name: 'icon-name', color: 'color-name', backgroundColor: 'color-name', title: 'title' }`."
                );
            }
        }
    }
}

@use '../../style/functions';
@use '../../style/internal/shared_input-select-picker';
@use '../../style/mixins';

@use '@material/textfield';
@use '@material/textfield/icon';

/**
 * @prop --icon-background-color: Background color of the icon. Defaults to transparent.
 * @prop --icon-color: Color of the icon. Defaults to `rgb(var(--contrast-1100))`.
 * @prop --background-color: Background color of the field when type is set to input.
 * @prop --background-color-disabled: Background color of the field when type is set to input and the component is disabled or readonly.
 * @prop --input-chip-set-selected-chip-color: Color of the highlight around selected chips in input chip-sets.
 */

@include textfield.core-styles;
@include icon.icon-core-styles;

@include shared_input-select-picker.leading-icon;

$height-of-chip-set-input: functions.pxToRem(36);
$leading-icon-space: 1.5rem;

:host(limel-chip-set) {
    isolation: isolate;
}

:host(limel-chip-set[type='input']) {
    limel-notched-outline {
        [slot='content'] {
            min-height: shared_input-select-picker.$height-of-mdc-text-field;
        }
    }
}

:host(limel-chip-set:not([type='input'])) {
    .limel-notched-outline {
        --limel-notched-outline-border-color: transparent;
        --limel-notched-outline-background-color: transparent;
    }
}

.mdc-chip-set {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    gap: 0.5rem;
    min-height: shared_input-select-picker.$height-of-mdc-text-field;
    position: relative;

    &.mdc-chip-set--input {
        padding: 0.4rem 0.5rem;
        width: 100%;
    }

    &.has-clear-all-button {
        &.mdc-chip-set--input {
            padding-right: functions.pxToRem(
                32
            ); // This leaves space for "clear all" button and avoids overlapping with chips
        }
    }
}

.mdc-text-field__input {
    .mdc-text-field:not(.mdc-text-field--disabled) & {
        color: shared_input-select-picker.$input-text-color;
    }
    .mdc-text-field.mdc-text-field--disabled & {
        color: shared_input-select-picker.$input-text-color-disabled;
    }
}

.mdc-text-field {
    height: auto;
    cursor: text;
    flex-wrap: wrap;
    row-gap: 0.5rem;

    .mdc-text-field__input {
        @include shared_input-select-picker.input-field-placeholder;

        width: auto;
        padding: 0 0.5rem;

        flex-grow: 1;
        flex-shrink: 0;

        &.hidden {
            // This class is added, as soon as there is a chip selected and displayed
            // This input field should not be visually visible as it breaks the UI in some cases
            // But it should be rendered to be able to tab between fields and do other keyboard commands /Kia
            transition: all 0s;
            opacity: 0;
            position: absolute;
            z-index: -100; // to let users interact with chips, in case they're covered
        }
        &[type='search'] {
            -webkit-appearance: textfield; // Removes the default magnifying glass icon on iOS which appears automatically on input fields with type of search
            background-color: transparent; // overides styles caused by previous line

            &::-webkit-search-cancel-button {
                display: none; // removes the default X button
            }
        }
    }
}

.clear-all-button {
    @include mixins.clear-all-button;
    @include mixins.visualize-keyboard-focus;

    position: absolute;
    right: 0.5rem;
    top: calc(#{shared_input-select-picker.$height-of-mdc-text-field} / 4);

    opacity: 0; // Is hidden, but can receive focus (such as when navigating through tab indexes).

    &:focus,
    .has-chips:not(.disabled):hover &,
    .has-chips:not(.disabled).mdc-text-field--focused & {
        opacity: 1;
        outline: none;
    }

    .mdc-chip-set:not(.has-chips) &,
    .has-chips.disabled & {
        display: none; // Won't receive focus when disabled
    }
}

.has-leading-icon {
    &:not(.has-chips) {
        .mdc-text-field__input {
            padding-left: $leading-icon-space;
        }
    }

    limel-chip {
        &:first-of-type {
            margin-left: 1.5rem;
        }
    }

    .search-icon {
        transition: transform 0.2s ease;
        position: absolute;
        top: functions.pxToRem(9);
        left: 0.25rem;
    }

    limel-icon {
        background-color: transparent;
    }
}

.delimiter {
    opacity: 0.5;
    padding: 0 functions.pxToRem(2);
    color: var(--mdc-theme-on-surface);
}

limel-chip {
    border-radius: 2rem;

    &.can-be-removed {
        // When chip is selected with keyboard (backspace / arrow-keys) to be deleted
        box-shadow: var(--shadow-depth-8-error);
    }
}

@import './partial-styles/_readonly';
@import './partial-styles/_helper-text';

import { Component, h, Prop } from '@stencil/core';
import { CircularProgressSize } from '../circular-progress/circular-progress.types';
import { abbreviate } from '../badge/format';

const PERCENT = 100;

/**
 * The circular progress component can be used to visualize the curent state of
 * a progress in a scale; for example percentage of completion of a task.
 *
 * Its compact UI makes the component suitable when there is not enough screen
 * space available to visualise such information.
 *
 * This component allows you to define your scale, from `0` to a desired
 * `maxValue`; and also lets you chose a proper `suffix` for your scale.
 *
 * :::note
 * The component will round up the value when it is displayed, and only shows
 * one decimal digit.
 * It also abbreviates large numbers. For example 1234 will be displayed as 1.2k.
 * Of course such numbers, if bigger than `maxValue` will be visualized as a
 * full progress.
 * :::
 *
 * @exampleComponent limel-example-circular-progress
 * @exampleComponent limel-example-circular-progress-sizes
 * @exampleComponent limel-example-circular-progress-props
 * @exampleComponent limel-example-circular-progress-css-variables
 * @exampleComponent limel-example-circular-progress-percentage-colors
 */
@Component({
    tag: 'limel-circular-progress',
    shadow: true,
    styleUrl: 'circular-progress.scss',
})
export class CircularProgress {
    /**
     * The value of the progress bar.
     */
    @Prop()
    public value: number = 0;

    /**
     * The maximum value within the scale that the progress bar should visualize. Defaults to `100`.
     */
    @Prop()
    public maxValue: number = PERCENT;

    /**
     * The prefix which is displayed before the `value`, must be a few characters characters long.
     */
    @Prop({ reflect: true })
    public prefix?: string = null;

    /**
     * The suffix which is displayed after the `value`, must be one or two characters long. Defaults to `%`
     */
    @Prop()
    public suffix: string = '%';

    /**
     * When set to `true`, makes the filled section showing the percentage colorful. Colors change with intervals of 10%.
     */
    @Prop()
    public displayPercentageColors: boolean = false;

    /**
     * Determines the visual size of the visualization from a preset size. This property can override the `--circular-progress-size` variable if it is specified.
     */
    @Prop({ reflect: true })
    public size: CircularProgressSize;

    public render() {
        const classList = {
            'lime-circular-progress': true,
            'displays-percentage-colors': this.displayPercentageColors,
        };

        const currentPercentage = (this.value * PERCENT) / this.maxValue + '%';
        const value = Math.round(this.value * 10) / 10;

        return (
            <div
                role="progressbar"
                class={classList}
                aria-label="%"
                aria-valuemin="0"
                aria-valuemax={this.maxValue}
                aria-valuenow={this.value}
                style={{ '--percentage': currentPercentage }}
            >
                {this.renderPrefix()}
                <span class="value">
                    {abbreviate(value)}
                    <span class="suffix">{this.suffix}</span>
                </span>
            </div>
        );
    }
    private renderPrefix = () => {
        if (this.prefix) {
            return <span class="prefix">{this.prefix}</span>;
        }
    };
}

@use '../../style/functions';
@use '../../style/mixins';

/**
 * @prop --circular-progress-size: Determines the visual size of the visualization. This does not override the `size` property if it is specified.
 * @prop --circular-progress-track-color: Determines the color of the circular track. Defaults to `--contrast-400`.
 * @prop --circular-progress-suffix-color: Determines the color of the prefix. Defaults to `--contrast-1000`.
 * @prop --circular-progress-text-color: Determines the color of the value. Defaults to `--contrast-1200`.
 * @prop --circular-progress-prefix-color: Determines the color of the suffix. Defaults to `--contrast-1000`.
 * @prop --circular-progress-fill-color: Determines the color of the progressed section. Defaults to `--lime-primary-color`.
 * @prop --circular-progress-background-color: Determines the background color of the central section. Defaults to `--contrast-100`.
 */

:host {
    display: block;
    box-sizing: border-box;
    isolation: isolate;
}

:host([size='x-small']) {
    --circular-progress-size: 1.5rem;
    font-weight: bold;

    .value {
        letter-spacing: functions.pxToRem(-1);
    }
}

:host([size='small']) {
    --circular-progress-size: 2rem;
    font-weight: bold;

    .value {
        letter-spacing: functions.pxToRem(-0.5);
    }
}

:host([size='medium']) {
    --circular-progress-size: 3rem;
}

:host([size='large']) {
    --circular-progress-size: 4rem;
}

:host([size='x-large']) {
    --circular-progress-size: 5rem;
}

.lime-circular-progress {
    --size: var(--circular-progress-size, 3rem);
    --fill-color: var(--circular-progress-fill-color, var(--mdc-theme-primary));
    --track-color: var(
        --circular-progress-track-color,
        rgb(var(--contrast-400))
    );
    position: relative;

    display: flex;
    align-items: center;
    justify-content: center;

    width: var(--size);
    height: var(--size);
    border-radius: 50%;

    line-height: normal;

    box-shadow: 0 0 0 0.125rem rgb(var(--contrast-100), 0.7);
    background: conic-gradient(
        var(--fill-color) 0% var(--percentage),
        var(--track-color) var(--percentage) 100%
    );

    &:before {
        $size-of-middle-circle: calc(
            var(--size) * 0.75 + 0.25rem
        ); // this calculation ensures that the middle circle is responsive and always perfectly centered

        content: '';
        position: absolute;
        width: $size-of-middle-circle;
        height: $size-of-middle-circle;
        border-radius: 50%;
        background-color: var(
            --circular-progress-background-color,
            rgb(var(--contrast-100))
        );
        box-shadow: var(--button-shadow-pressed);
    }
}

.prefix {
    font-size: clamp(0.5rem, calc(var(--size) * 0.16), 2.25rem);
    @include mixins.truncate-text();
    text-align: center;
    color: var(--circular-progress-prefix-color, rgb(var(--contrast-1000)));

    width: 45%;
    top: 20%;
    position: absolute;
}

.value {
    display: flex;
    font-size: clamp(0.5rem, calc(var(--size) * 0.25), 4rem);
    color: var(--circular-progress-text-color, rgb(var(--contrast-1200)));
    z-index: 1;
    cursor: default;
}

.suffix {
    font-size: clamp(0.5rem, calc(var(--size) * 0.18), 2.5rem);
    color: var(--circular-progress-suffix-color, rgb(var(--contrast-1000)));
    padding-top: 4%;
}

