} from '@stencil/core';
import { dispatchResizeEvent } from '../../util/dispatch-resize-event';
import { Action } from './action';
import {
    makeEnterClickable,
    removeEnterClickable,
} from '../../util/make-enter-clickable';
import { createRandomString } from '../../util/random-string';
import { Icon } from '../../global/shared-types/icon.types';
import {
    getIconColor,
    getIconName,
    getIconTitle,
} from '../icon/get-icon-props';
import translate from '../../global/translations';
import { Languages } from '../date-picker/date.types';

/**
 * A collapsible section can be used to group related content together
 * and hide the group when not needed.
 * Using this component can help to:
 * - Save vertical space by hiding non-essential content
 * - Improve content organization and scannability of the user interface
 * - Reduce cognitive load by displaying only a set of relevant information at a time
 * - Or disclose complex information, progressively to the user
 *
 * @slot - Content to put inside the collapsible section
 * @slot header - Optional slot for custom header content
 *
 * @exampleComponent limel-example-collapsible-section-basic
 * @exampleComponent limel-example-collapsible-section-actions
 * @exampleComponent limel-example-collapsible-section-with-custom-header-component
 * @exampleComponent limel-example-collapsible-section-external-control
 * @exampleComponent limel-example-collapsible-section-with-slider
 * @exampleComponent limel-example-collapsible-section-invalid
 * @exampleComponent limel-example-collapsible-section-icon
 * @exampleComponent limel-example-collapsible-section-css-props
 */
@Component({
    tag: 'limel-collapsible-section',
    shadow: true,
    styleUrl: 'collapsible-section.scss',
})
export class CollapsibleSection {
    /**
     * `true` if the section is expanded, `false` if collapsed.
     */
    @Prop({ mutable: true, reflect: true })
    public isOpen: boolean = false;

    /**
     * Text to display in the header of the section
     */
    @Prop({ reflect: true })
    public header: string;

    /**
     * Icon to display in the header of the section
     */
    @Prop()
    public icon?: string | Icon;

    /**
     * `true` if the section is invalid, `false` if valid.
     * This can be used to indicate that the content inside the section is invalid.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * Actions to place to the far right inside the header
     */
    @Prop()
    public actions: Action[];

    /**
     * Defines the language for translations.
     * Will translate the translatable strings on the components.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * Emitted when the section is expanded
     */
    @Event()
    private open: EventEmitter<void>;

    /**
     * Emitted when the section is collapsed
     */
    @Event()
    private close: EventEmitter<void>;

    /**
     * Emitted when an action is clicked inside the header
     */
    @Event()
    private action: EventEmitter<Action>;

    @Element()
    private host: HTMLElement;

    private bodyId = createRandomString();
    private headingId = createRandomString();

    public componentDidRender() {
        const button = this.host.shadowRoot.querySelector(
            '.open-close-toggle'
        ) as HTMLElement;

        makeEnterClickable(button);
    }

    public disconnectedCallback() {
        const button = this.host.shadowRoot.querySelector(
            '.open-close-toggle'
        ) as HTMLElement;

        removeEnterClickable(button);
    }

    public render() {
        return (
            <section
                class={`${this.isOpen ? 'open' : ''}`}
                aria-invalid={this.invalid}
                aria-labelledby={this.header ? this.headingId : null}
            >
                <header>
                    <button
                        class="open-close-toggle"
                        onClick={this.onClick}
                        aria-controls={this.bodyId}
                        aria-expanded={this.isOpen ? 'true' : 'false'}
                        aria-label={this.getCollapsibleSectionAriaLabel()}
                        type="button"
                    />
                    {this.renderExpandCollapseSign()}
                    {this.renderIcon()}
                    {this.renderHeading()}
                    <div class="divider-line" role="presentation" />
                    {this.renderHeaderSlot()}
                    {this.renderActions()}
                </header>
                <div
                    class="body"
                    aria-hidden={String(!this.isOpen)}
                    id={this.bodyId}
                    role="region"
                >
                    <slot />
                </div>
            </section>
        );
    }

    private onClick = () => {
        this.handleInteraction();
    };

    private handleInteraction = () => {
        this.isOpen = !this.isOpen;

        if (this.isOpen) {
            this.open.emit();
            const waitForUiToRender = 100;
            setTimeout(dispatchResizeEvent, waitForUiToRender);
        } else {
            this.close.emit();
        }
    };

    private renderExpandCollapseSign = () => {
        return (
            <div class="expand-icon" role="presentation" aria-hidden="true">
                <div class="line" />
                <div class="line" />
                <div class="line" />
                <div class="line" />
            </div>
        );
    };

    private renderIcon = () => {
        if (!this.icon) {
            return;
        }

        const name = getIconName(this.icon);
        const color = getIconColor(this.icon);
        const title = getIconTitle(this.icon);

        return (
            <limel-icon
                name={name}
                aria-label={title}
                aria-hidden={title ? null : 'true'}
                style={{
                    color: `${color}`,
                }}
            />
        );
    };

    private renderHeading = () => {
        if (!this.header) {
            return;
        }

        return (
            <h2
                class="title mdc-typography mdc-typography--headline2"
                id={this.headingId}
            >
                {this.header}
            </h2>
        );
    };

    private renderActions = () => {
        if (!this.actions) {
            return;
        }

        return (
            <div class="actions">
                {this.actions.map(this.renderActionButton)}
            </div>
        );
    };

    private renderHeaderSlot() {
        return <slot name="header" />;
    }

    private renderActionButton = (action: Action) => {
        return (
            <limel-icon-button
                icon={action.icon}
                label={action.label}
                disabled={action.disabled}
                onClick={this.handleActionClick(action)}
            />
        );
    };

    private handleActionClick = (action: Action) => (event: MouseEvent) => {
        event.stopPropagation();
        this.action.emit(action);
    };

    private getCollapsibleSectionAriaLabel = (): string => {
        const heading = this.header ? `"${this.header}"` : ' ';

        if (!this.isOpen) {
            return translate.get('collapsible-section.open', this.language, {
                header: heading,
            });
        }

        return translate.get('collapsible-section.close', this.language, {
            header: heading,
        });
    };
}

@use '../../style/mixins';
@use '../../style/internal/shared_input-select-picker';

/**
 * @prop --closed-header-background-color: background color for header when closed
 * @prop --open-header-background-color: background color for header when open
 * @prop --header-stroke-color: color of the animated icons that visualize collapsed or normal states of the headers, as well as the divider line on headers
 * @prop --body-background-color: background color for body
 * @prop --body-padding: space around content of the body
 */

:host(limel-collapsible-section) {
    --border-radius-of-header: 0.75rem;
    display: block;
}

:host([hidden]) {
    display: none;
}

.open-close-toggle {
    all: unset;
    position: absolute;
    inset: 0;
    width: 100%; // for Firefox
    @include mixins.visualize-keyboard-focus;
    transition:
        background-color 0.4s ease,
        border-radius 0.1s ease;
    cursor: pointer;
    z-index: -1;

    background-color: var(
        --closed-header-background-color,
        rgb(var(--contrast-200))
    );
    border-radius: var(--border-radius-of-header);

    &:hover,
    &:focus-visible {
        background-color: var(
            --open-header-background-color,
            rgb(var(--contrast-300))
        );
    }

    section.open & {
        background-color: var(
            --open-header-background-color,
            rgb(var(--contrast-100))
        );
        border-radius: var(--border-radius-of-header)
            var(--border-radius-of-header) 0 0;
        &:hover,
        &:focus-visible {
            background-color: var(
                --open-header-background-color,
                rgb(var(--contrast-300))
            );
        }
    }
}

.title,
.divider-line,
.expand-icon {
    pointer-events: none;
}

section {
    transition: box-shadow 0.4s ease;
    border-radius: var(--border-radius-of-header);

    &[aria-invalid]:not([aria-invalid='false']) {
        --header-stroke-color: rgb(var(--color-red-default)) !important;

        &:not(.open) {
            box-shadow: 0 0 0 1px rgb(var(--color-red-default));
        }
    }
}

header {
    isolation: isolate;
    position: relative;

    align-items: center;
    display: flex;
    justify-content: space-between;
    gap: 0.5rem;

    padding-left: 0.5rem;
    height: shared_input-select-picker.$height-of-mdc-text-field;
}

limel-icon {
    width: 1.5rem;
}

.title {
    font-size: 1rem;
    font-weight: 300;
    color: var(--mdc-theme-on-surface);

    justify-self: flex-start;

    user-select: none; // mostly to improve experience on Android, where tapping on sections selects the text too

    // Below tries to render text in two lines,
    // and then truncate if there is no more space
    height: auto;
    max-height: 3rem;
    line-height: 1.2rem;
    @include mixins.truncate-text-on-line(2);
}

.divider-line {
    transition: opacity 0.3s ease 0.3s;
    flex-grow: 1;
    height: 0.125rem;
    border-radius: 1rem;
    background-color: var(--header-stroke-color, rgb(var(--contrast-900)));

    opacity: 0;
    margin-right: 0.5rem;

    section.open & {
        opacity: 0.16;
    }
}

.actions {
    justify-self: flex-end;
    flex-shrink: 0;
}

::slotted([slot='header']) {
    margin-right: 0.5rem;
}

.body {
    background-color: var(--body-background-color, var(--contrast-100));
    padding-left: var(--body-padding, 1.25rem);
    padding-right: var(--body-padding, 1.25rem);
    border-radius: 0 0 var(--border-radius-of-header)
        var(--border-radius-of-header);
}

// This animates height of the body,
// from `0` to `auto`
.body {
    // All below vars are for internal use only!
    --limel-cs-opacity-transition-speed: 0.1s;
    --limel-cs-opacity-transition-delay: 0s;
    --limel-cs-grid-template-rows-transition-speed: 0.3s;
    transition: grid-template-rows
        var(--limel-cs-grid-template-rows-transition-speed)
        cubic-bezier(1, 0.09, 0, 0.89);
    display: grid;
    grid-template-rows: 0fr;

    slot {
        transition: opacity var(--limel-cs-opacity-transition-speed) ease
            var(--limel-cs-opacity-transition-delay);
        display: block;
        overflow: hidden;
        opacity: 0;
    }
}

section.open {
    .body {
        --limel-cs-opacity-transition-speed: 0.4s;
        --limel-cs-opacity-transition-delay: 0.3s;
        --limel-cs-grid-template-rows-transition-speed: 0.46s;
        grid-template-rows: 1fr;

        slot {
            opacity: 1;
        }
    }
}

header:hover,
header:has(.open-close-toggle:hover),
header:has(.open-close-toggle:focus-visible) {
    + .body {
        will-change: grid-template-rows;

        slot {
            will-change: opacity;
        }
    }
}

// End: animating height

@import './partial-styles/expand-icon.scss';

import { Component, h, Prop, State, Event, EventEmitter } from '@stencil/core';
import { FormComponent } from '../form/form.types';

/**
 * This component enables you to select a swatch from out color palette, simply
 * by clicking on it. You can then copy the css variable name of the chosen color
 * and use it where desired.
 *
 * The color picker can also show you a preview of any valid color name or color value.
 *
 * :::note
 * Make sure to read our [guidelines about usage of colors](/#/DesignGuidelines/color-system.md/) from our palette.
 * :::
 *
 * @exampleComponent limel-example-color-picker
 * @exampleComponent limel-example-color-picker-readonly
 */
@Component({
    tag: 'limel-color-picker',
    shadow: true,
    styleUrl: 'color-picker.scss',
})
export class ColorPicker implements FormComponent {
    /**
     * Name or code of the chosen color
     */
    @Prop({ reflect: true })
    public value: string;

    /**
     * The label of the input field
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Helper text of the input field
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * Displayed as tooltips when picker is hovered.
     */
    @Prop({ reflect: true })
    public tooltipLabel: string;

    /**
     * Set to `true` if a value is required
     */
    @Prop({ reflect: true })
    public required: boolean;

    /**
     * Set to `true` if a value is readonly. This makes the component un-interactive.
     */
    @Prop({ reflect: true })
    public readonly: boolean;

    /**
     * Emits chosen value to the parent component
     */
    @Event()
    public change: EventEmitter<string>;

    @State()
    private isOpen = false;

    public componentDidRender() {
        if (this.shouldFocus && this.isOpen) {
            this.shouldFocus = false;
            this.contentElement?.focus();
        }
    }

    private contentElement?: HTMLLimelColorPickerPaletteElement;

    private shouldFocus = false;

    public render() {
        return [
            this.renderTooltip(),
            <div class="color-picker">
                {this.renderPickerPalette()}

                <limel-input-field
                    label={this.label}
                    helperText={this.helperText}
                    value={this.value}
                    onChange={this.handleChange}
                    required={this.required}
                    readonly={this.readonly}
                    class="chosen-color-input"
                />
            </div>,
        ];
    }
    private renderTooltip = () => {
        if (!this.readonly && this.tooltipLabel) {
            return (
                <limel-tooltip
                    label={this.tooltipLabel}
                    elementId="tooltip-button"
                />
            );
        }
    };

    private renderPickerPalette = () => {
        if (this.readonly) {
            return this.renderPickerTrigger();
        }

        return (
            <limel-popover
                open={this.isOpen}
                openDirection="bottom-start"
                onClose={this.onPopoverClose}
            >
                {this.renderPickerTrigger()}
                <limel-color-picker-palette
                    ref={this.setColorPickerPaletteElement}
                    value={this.value}
                    label={this.label}
                    helperText={this.helperText}
                    onChange={this.handleChange}
                    required={this.required}
                />
            </limel-popover>
        );
    };

    private renderPickerTrigger = () => {
        const background = this.value ? { '--background': this.value } : {};

        return (
            <button
                class="picker-trigger"
                slot="trigger"
                style={background}
                role="button"
                onClick={this.openPopover}
                id="tooltip-button"
            />
        );
    };

    private setColorPickerPaletteElement = (
        element: HTMLLimelColorPickerPaletteElement
    ) => {
        this.contentElement = element;
    };

    private openPopover = (event: MouseEvent) => {
        event.stopPropagation();
        this.isOpen = true;

        this.shouldFocus = this.isOpen;
    };

    private onPopoverClose = (event: CustomEvent) => {
        event.stopPropagation();
        this.isOpen = false;
    };

    private handleChange = (event: CustomEvent<string>) => {
        event.stopPropagation();
        this.change.emit(event.detail);
    };
}

