import { Component, Prop } from '@stencil/core';
import { Config, globalConfig } from '../../global/config';

/**
 * Component used to set global configuration for Lime Elements.
 *
 * :::warning
 * **Building something for Lime CRM?** Then you should _NOT_ use this component.
 * Lime CRM already uses this component to set the global configuration for
 * Lime Elements. No matter what problem you are facing at the moment, using
 * this component will not help, and might cause other problems.
 * :::
 *
 * Building your own software, which is using Lime Elements?
 * Then you _might_ need to use this component.
 *
 * @private
 */
@Component({
    tag: 'limel-config',
    shadow: true,
})
export class ConfigComponent {
    /**
     * Global configuration for Lime Elements.
     */
    @Prop()
    public config: Config;

    public componentDidLoad() {
        this.setGlobalConfig();
    }

    public componentDidUpdate() {
        this.setGlobalConfig();
    }

    /*
     * Copy any config settings to the global config object
     */
    private setGlobalConfig() {
        if (!this.config) {
            return;
        }

        for (const key of Object.keys(this.config)) {
            globalConfig[key] = this.config[key];
        }
    }

    public render() {
        return null;
    }
}

import {
    Component,
    h,
    Prop,
    State,
    Element,
    EventEmitter,
    Event,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { isAndroidDevice, isIOSDevice } from '../../util/device';
import { DateType, Languages } from '../date-picker/date.types';
import { InputType } from '../input-field/input-field.types';
import { DateFormatter } from './date-formatter';
import { MDCTextField } from '@material/textfield';

// tslint:disable:no-duplicate-string
const nativeTypeForConsumerType: { [key: string]: InputType } = {
    date: 'date',
    time: 'time',
    // Mobile Safari feature detects as capable of input type `week`,
    // but it just displays a non-interactive input
    // TODO(ads): remove this when support is decent on iOS!
    week: isIOSDevice() ? 'date' : 'week',
    month: 'month',
    quarter: 'date',
    year: 'date',
    datetime: 'datetime-local',
    default: 'datetime-local',
};
const nativeFormatForType = {
    date: 'Y-MM-DD',
    time: 'HH:mm',
    week: 'GGGG-[W]WW',
    month: 'Y-MM',
    'datetime-local': 'Y-MM-DD[T]HH:mm',
};
// tslint:enable:no-duplicate-string

/**
 * @exampleComponent limel-example-date-picker-datetime
 * @exampleComponent limel-example-date-picker-date
 * @exampleComponent limel-example-date-picker-time
 * @exampleComponent limel-example-date-picker-week
 * @exampleComponent limel-example-date-picker-month
 * @exampleComponent limel-example-date-picker-quarter
 * @exampleComponent limel-example-date-picker-year
 * @exampleComponent limel-example-date-picker-formatted
 * @exampleComponent limel-example-date-picker-programmatic-change
 * @exampleComponent limel-example-date-picker-composite
 * @exampleComponent limel-example-date-picker-custom-formatter
 */
@Component({
    tag: 'limel-date-picker',
    shadow: true,
    styleUrl: 'date-picker.scss',
})
export class DatePicker {
    /**
     * Set to `true` to disable the field.
     * Use `disabled` to indicate that the field can normally be interacted
     * with, but is currently disabled. This tells the user that if certain
     * requirements are met, the field may become enabled again.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Set to `true` to make the field read-only.
     * Use `readonly` when the field is only there to present the data it holds,
     * and will not become possible for the current user to edit.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to indicate that the current value of the date picker is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * Text to display next to the date picker
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * The placeholder text shown inside the input field, when the field is focused and empty
     */
    @Prop({ reflect: true })
    public placeholder: string;

    /**
     * Optional helper text to display below the input field when it has focus
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * Set to `true` to indicate that the field is required.
     */
    @Prop({ reflect: true })
    public required = false;

    /**
     * The value of the field.
     */
    @Prop()
    public value: Date;

    /**
     * Type of date picker.
     */
    @Prop({ reflect: true })
    public type: DateType = 'datetime';

    /**
     * Format to display the selected date in.
     */
    @Prop({ reflect: true })
    public format: string;

    /**
     * Defines the localisation for translations and date formatting.
     * Property `format` customizes the localized date format.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * Custom formatting function. Will be used for date formatting.
     *
     * :::note
     * overrides `format` and `language`
     * :::
     */
    @Prop()
    public formatter?: (date: Date) => string;

    /**
     * Emitted when the date picker value is changed.
     */
    @Event()
    private change: EventEmitter<Date>;

    @Element()
    private host: HTMLLimelDatePickerElement;

    @State()
    private internalFormat: string;
    @State()
    private showPortal = false;

    private useNative: boolean;
    private nativeType: InputType;
    private nativeFormat: string;
    private textField: HTMLElement;
    private datePickerCalendar: HTMLLimelFlatpickrAdapterElement;
    private portalId = `date-picker-calendar-${createRandomString()}`;
    private dateFormatter: DateFormatter;

    constructor() {
        this.handleCalendarChange = this.handleCalendarChange.bind(this);
        this.handleInputElementChange =
            this.handleInputElementChange.bind(this);
        this.showCalendar = this.showCalendar.bind(this);
        this.dateFormatter = new DateFormatter(this.language);
        this.clearValue = this.clearValue.bind(this);
        this.hideCalendar = this.hideCalendar.bind(this);
        this.onInputClick = this.onInputClick.bind(this);
        this.nativeChangeHandler = this.nativeChangeHandler.bind(this);
        this.preventBlurFromCalendarContainer =
            this.preventBlurFromCalendarContainer.bind(this);
    }

    public componentWillLoad() {
        this.useNative = !this.readonly && (isIOSDevice() || isAndroidDevice());

        this.updateInternalFormatAndType();
    }

    public componentWillUpdate() {
        this.updateInternalFormatAndType();
    }

    public disconnectedCallback() {
        this.hideCalendar();
    }

    public render() {
        const inputProps: any = {
            onAction: this.clearValue,
        };

        if (this.value && !this.readonly) {
            inputProps.trailingIcon = 'clear_symbol';
        }

        if (this.useNative) {
            return (
                <limel-input-field
                    disabled={this.disabled}
                    readonly={this.readonly}
                    invalid={this.invalid}
                    label={this.label}
                    helperText={this.helperText}
                    required={this.required}
                    value={this.formatValue(this.value)}
                    type={this.nativeType}
                    onChange={this.nativeChangeHandler}
                />
            );
        }

        const dropdownZIndex = getComputedStyle(this.host).getPropertyValue(
            '--dropdown-z-index'
        );

        const formatter = this.formatter || this.formatValue;

        return [
            <limel-input-field
                disabled={this.disabled}
                readonly={this.readonly}
                invalid={this.invalid}
                label={this.label}
                placeholder={this.placeholder}
                helperText={this.helperText}
                required={this.required}
                value={this.value ? formatter(this.value) : ''}
                onFocus={this.showCalendar}
                onBlur={this.hideCalendar}
                onClick={this.onInputClick}
                onChange={this.handleInputElementChange}
                ref={(el) => (this.textField = el)}
                {...inputProps}
            />,
            <limel-portal
                containerId={this.portalId}
                visible={this.showPortal}
                containerStyle={{ 'z-index': dropdownZIndex }}
            >
                <limel-flatpickr-adapter
                    format={this.internalFormat}
                    language={this.language}
                    type={this.type}
                    value={this.value}
                    ref={(el) => (this.datePickerCalendar = el)}
                    isOpen={this.showPortal}
                    formatter={formatter}
                    onChange={this.handleCalendarChange}
                />
            </limel-portal>,
        ];
    }

    private updateInternalFormatAndType() {
        this.nativeType = nativeTypeForConsumerType[this.type || 'default'];
        this.nativeFormat = nativeFormatForType[this.nativeType];

        if (this.useNative) {
            this.internalFormat = this.nativeFormat;
        } else if (this.formatter || this.format) {
            this.internalFormat = this.format;
        } else {
            this.internalFormat = this.dateFormatter.getDateFormat(this.type);
        }
    }

    private nativeChangeHandler(event: CustomEvent<string>) {
        event.stopPropagation();
        const date = this.dateFormatter.parseDate(
            event.detail,
            this.internalFormat
        );
        this.change.emit(date);
    }

    private showCalendar(event) {
        this.showPortal = true;
        const inputElement = this.textField.shadowRoot.querySelector('input');
        setTimeout(() => {
            this.datePickerCalendar.inputElement = inputElement;
        });
        event.stopPropagation();

        document.addEventListener('mousedown', this.documentClickListener, {
            passive: true,
        });

        document.addEventListener(
            'blur',
            this.preventBlurFromCalendarContainer,
            {
                capture: true,
            }
        );
    }

    private preventBlurFromCalendarContainer(event) {
        // We don't want the input element to lose focus when we pick
        // a date in the calendar container.
        // This is also required in order to not close the non
        // automatically closing pickers (type datetime and time)
        // when you pick a value.
        if (event.relatedTarget === this.datePickerCalendar) {
            event.stopPropagation();
        }
    }

    private hideCalendar() {
        setTimeout(() => {
            this.showPortal = false;
        });
        document.removeEventListener('mousedown', this.documentClickListener);
        document.removeEventListener(
            'blur',
            this.preventBlurFromCalendarContainer
        );

        if (!this.pickerIsAutoClosing()) {
            this.fixFlatpickrFocusBug();
        }
    }

    private fixFlatpickrFocusBug() {
        // Flatpickr removes the focus from the input field
        // but the 'visual focus' is still there
        const mdcTextField = new MDCTextField(
            this.textField.shadowRoot.querySelector('.mdc-text-field')
        );
        mdcTextField.getDefaultFoundation().deactivateFocus();
        mdcTextField.valid = !this.invalid;
    }

    private documentClickListener = (event: MouseEvent) => {
        if (event.composedPath().includes(this.textField)) {
            return;
        }

        const element = document.querySelector(`#${this.portalId}`);
        if (!element.contains(event.target as Node)) {
            this.hideCalendar();
        }
    };

    private handleCalendarChange(event) {
        const date = event.detail;
        event.stopPropagation();
        if (this.pickerIsAutoClosing()) {
            this.hideCalendar();
        }

        this.change.emit(date);
    }

    private onInputClick(event) {
        if (this.disabled || this.readonly) {
            return;
        }

        if (this.showPortal) {
            return;
        }

        this.showCalendar(event);
    }

    private handleInputElementChange(event) {
        if (event.detail === '') {
            this.clearValue();
        }

        event.stopPropagation();
    }

    private pickerIsAutoClosing() {
        return this.type !== 'datetime' && this.type !== 'time';
    }

    private clearValue() {
        this.change.emit(null);
    }

    private formatValue = (value: Date): string =>
        this.dateFormatter.formatDate(value, this.internalFormat);
}

// Note! The `--dropdown-z-index` property is used from `date-picker.tsx`.
/**
 * @prop --dropdown-z-index: z-index of the dropdown menu.
 */

:host(limel-date-picker) {
    position: relative;
}

limel-input-field[disabled],
limel-input-field[readonly] {
    pointer-events: none;
}

import { DialogHeading, ClosingActions } from './dialog.types';
import { MDCDialog } from '@material/dialog';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    Watch,
} from '@stencil/core';
import { isEqual } from 'lodash-es';
import { dispatchResizeEvent } from '../../util/dispatch-resize-event';
import { createRandomString } from '../../util/random-string';

/**
 * :::note
 * Regarding the `close` event: When putting other elements that emit `close`
 * events inside a dialog, those events must be caught and stopped inside the
 * dialog. If not, they will bubble to the event handler listening for `close`
 * events on the dialog, which will close the dialog too.
 *
 * See the example _Nested `close` events_.
 * :::
 *
 * :::important
 * Are you developing for
 * [Lime CRM](https://www.lime-technologies.com/en/lime-crm/)? Please note that
 * you should use the [DialogRenderer](https://lundalogik.github.io/lime-web-components/versions/latest/interfaces/DialogRenderer.html)
 * from Lime Web Components to open dialogs in Lime CRM.
 * :::
 *
 * @exampleComponent limel-example-dialog
 * @exampleComponent limel-example-dialog-nested-close-events
 * @exampleComponent limel-example-dialog-heading
 * @exampleComponent limel-example-dialog-heading-actions
 * @exampleComponent limel-example-dialog-form
 * @exampleComponent limel-example-dialog-size
 * @exampleComponent limel-example-dialog-fullscreen
 * @exampleComponent limel-example-dialog-closing-actions
 * @exampleComponent limel-example-dialog-action-buttons
 * @slot - Content to put inside the dialog
 * @slot header-actions - The dialog header buttons
 * @slot button - The dialog buttons
 */
@Component({
    tag: 'limel-dialog',
    shadow: true,
    styleUrl: 'dialog.scss',
})
export class Dialog {
    /**
     * The heading for the dialog, if any.
     */
    @Prop()
    public heading: string | DialogHeading;

    /**
     * Set to `true` to make the dialog "fullscreen".
     */
    @Prop({ reflect: true })
    public fullscreen = false;

    /**
     * `true` if the dialog is open, `false` otherwise.
     */
    @Prop({ mutable: true, reflect: true })
    public open = false;

    /**
     * Defines which action triggers a close-event.
     */
    @Prop({ reflect: true })
    public closingActions: ClosingActions = {
        escapeKey: true,
        scrimClick: true,
    };
    /**
     * Emitted when the dialog is closed from inside the component.
     * (*Not* emitted when the consumer sets the `open`-property to `false`.)
     */
    @Event()
    private close: EventEmitter<void>;

    /**
     * Emitted when the dialog is in the process of being closed.
     */
    @Event()
    private closing: EventEmitter<void>;

    @Element()
    private host: HTMLLimelDialogElement;

    private mdcDialog: MDCDialog;

    private id: string;

    constructor() {
        this.handleMdcOpened = this.handleMdcOpened.bind(this);
        this.handleMdcClosed = this.handleMdcClosed.bind(this);
        this.handleMdcClosing = this.handleMdcClosing.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentWillLoad() {
        this.id = createRandomString();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.host.shadowRoot.querySelector('.mdc-dialog');
        if (!element) {
            return;
        }

        this.mdcDialog = new MDCDialog(element);
        if (this.open) {
            this.mdcDialog.open();
        }

        this.mdcDialog.listen('MDCDialog:opened', this.handleMdcOpened);
        this.mdcDialog.listen('MDCDialog:closed', this.handleMdcClosed);
        this.mdcDialog.listen('MDCDialog:closing', this.handleMdcClosing);

        this.setClosingActions();
    }

    public disconnectedCallback() {
        this.mdcDialog.unlisten('MDCDialog:opened', this.handleMdcOpened);
        this.mdcDialog.unlisten('MDCDialog:closed', this.handleMdcClosed);
        this.mdcDialog.unlisten('MDCDialog:closing', this.handleMdcClosing);
        this.mdcDialog.destroy();
    }

    public render() {
        return (
            <div
                class={{
                    'mdc-dialog': true,
                    'full-screen': !!this.fullscreen,
                }}
                role="alertdialog"
                aria-modal="true"
                aria-labelledby={'limel-dialog-title-' + this.id}
                aria-describedby={'limel-dialog-content-' + this.id}
            >
                <input hidden={true} id="initialFocusEl" />
                <div class="mdc-dialog__container">
                    <div class="mdc-dialog__surface">
                        {/*
                            The `initialFocusElement` below is needed to make
                            focus trapping work. At the time of writing, the
                            focusable elements inside the slots are not
                            detected, so we supply our own hidden element for
                            the focus trap to use. Read more here:
                            https://github.com/material-components/material-components-web/tree/v11.0.0/packages/mdc-dialog#handling-focus-trapping
                        */}
                        <input type="button" id="initialFocusElement" />
                        {this.renderHeading()}
                        <div
                            class="mdc-dialog__content"
                            id={'limel-dialog-content-' + this.id}
                        >
                            <slot />
                        </div>
                        {this.renderFooter()}
                    </div>
                </div>
                <div class="mdc-dialog__scrim" />
            </div>
        );
    }

    @Watch('open')
    protected watchHandler(newValue: boolean, oldValue: boolean) {
        if (oldValue === newValue) {
            return;
        }

        if (!this.mdcDialog) {
            return;
        }

        if (newValue) {
            this.mdcDialog.open();
        } else {
            this.mdcDialog.close();
        }
    }

    @Watch('closingActions')
    protected closingActionsChanged(
        newValue: ClosingActions,
        oldValue: ClosingActions
    ) {
        if (isEqual(newValue, oldValue)) {
            return;
        }

        this.setClosingActions();
    }

    private handleMdcOpened() {
        // When the opening-animation has completed, dispatch a
        // resize-event so that any content that depends on
        // javascript for layout has a chance to update to the
        // final layout of the dialog. /Ads
        const waitForUiToRender = 100;
        setTimeout(dispatchResizeEvent, waitForUiToRender);
    }

    private handleMdcClosed() {
        if (this.open) {
            this.close.emit();
        }

        this.open = false;
    }

    private handleMdcClosing() {
        this.closing.emit();
    }

    private isBadgeHeading(
        heading: string | DialogHeading
    ): heading is DialogHeading {
        return typeof heading === 'object' && !!heading.title && !!heading.icon;
    }

    private renderHeading() {
        if (this.isBadgeHeading(this.heading)) {
            const { title, subtitle, supportingText, icon } = this.heading;

            return (
                <limel-header
                    icon={icon}
                    heading={title}
                    subheading={subtitle}
                    supportingText={supportingText}
                >
                    <slot name="header-actions" slot="actions" />
                </limel-header>
            );
        } else if (typeof this.heading === 'string') {
            return <limel-header heading={this.heading}></limel-header>;
        }

        return null;
    }

    private renderFooter() {
        return (
            <footer class="mdc-dialog__actions">
                <slot name="button" />
            </footer>
        );
    }

    private setClosingActions() {
        this.mdcDialog.scrimClickAction = '';
        if (this.closingActions.scrimClick) {
            this.mdcDialog.scrimClickAction = 'close';
        }

        this.mdcDialog.escapeKeyAction = '';
        if (this.closingActions.escapeKey) {
            this.mdcDialog.escapeKeyAction = 'close';
        }
    }
}

@use '../../style/functions';
@use '../../style/mixins';

@use '@material/dialog';

/**
 * @prop --dialog-width: Width of the dialog.
 * @prop --dialog-height: Height of the dialog.
 * @prop --dialog-z-index: z-index of the dialog container, including the scrim.
 * @prop --dialog-heading-title-color: Color of the title.
 * @prop --dialog-heading-subtitle-color: Color of the subtitle.
 * @prop --dialog-heading-supporting-text-color: Color of the supporting text.
 * @prop --dialog-heading-icon-color: Color of the icon.
 * @prop --dialog-heading-icon-background-color: Background color of the icon when displayed as a badge.
 * @prop --dialog-max-width: Max width of the dialog.
 * @prop --dialog-max-height: Max height of the dialog.
 * @prop --dialog-padding-top-bottom: Padding on top and bottom of dialog content. Affects the height of fade-out effects on top and bottom edges when the content is scrollable and has overflowed out of the content area. Defaults to `1.5rem`. Note that if you use this variable and set it to numbers smaller than 1rem, you will loose the fade-out effects on the edges. If you have set these paddings to `0`, losing the fade out effects should be however fine for your use case! Because in such a case your intention is to handle the `overflow` internally in the component that is displayed in the dialog's content.
 * @prop --dialog-padding-left-right: Padding on the sides of dialog content. Defaults to `1.25rem`.
 */

:host {
    --dialog-background-color: var(--lime-elevated-surface-background-color);
    --header-heading-color: var(--dialog-heading-title-color);
    --header-subheading-color: var(--dialog-heading-subtitle-color);
    --header-supporting-text-color: var(--dialog-heading-supporting-text-color);
    --header-icon-color: var(--dialog-heading-icon-color);
    --header-icon-background-color: var(--dialog-heading-icon-background-color);
}

$responsive-body-padding: 3vw; // 3% of viewport's width

@include dialog.core-styles;

.mdc-dialog__content {
    // As long as this component is depended on MDC,
    // we need to force it to be font-agnostic.
    // When MDC-dependency is removed, this block can also be removed.
    // However, on removal of MDC-dependency, we should also make sure to check
    // other font-related styles that might be set by MDC,
    // such as `letter-spacing` or `font-size`.
    font-family: inherit;
}

.mdc-dialog {
    @include dialog.max-width(16000px, dialog.$margin);

    z-index: var(--dialog-z-index, dialog.$z-index);
    padding: env(safe-area-inset-top) env(safe-area-inset-right)
        env(safe-area-inset-bottom) env(safe-area-inset-left);

    &.full-screen {
        @include dialog.max-height(16000px, dialog.$margin);
        .mdc-dialog__container {
            height: 100%;
            width: 100%;

            .mdc-dialog__surface {
                height: 100%;
                width: 100%;
            }
        }
    }

    .mdc-dialog__scrim {
        background-color: rgba(var(--color-black), 0.4);
    }

    .mdc-dialog__container {
        height: 100%;
        width: var(--dialog-width, auto);
    }

    .mdc-dialog__surface {
        width: var(--dialog-width, auto);
        height: var(--dialog-height, auto);
        background-color: var(--dialog-background-color);
        box-shadow: var(--shadow-depth-64);

        max-width: var(--dialog-max-width, calc(100vw - 2rem));
        max-height: var(--dialog-max-height, calc(100% - 2rem));
        border-radius: 0.75rem;
    }

    .mdc-dialog__content {
        --limel-top-edge-fade-height: var(--dialog-padding-top-bottom, 1.5rem);
        --limel-bottom-edge-fade-height: var(
            --dialog-padding-top-bottom,
            1.5rem
        );
        @include mixins.fade-out-overflowed-content-on-edges(vertically);

        color: var(--mdc-theme-on-surface);
        padding-left: var(
            --dialog-padding-left-right,
            min(1.25rem, $responsive-body-padding)
        );
        padding-right: var(
            --dialog-padding-left-right,
            min(1.25rem, $responsive-body-padding)
        );
    }
}

#initialFocusElement {
    position: absolute;
    opacity: 0;
    pointer-events: none;
    z-index: -1;
}

slot[name='header'] {
    display: none;
}

slot[name='button'] {
    display: flex;
    gap: 0.5rem; // Makes sure buttons get some default distance
    width: 100%;
    justify-content: flex-end;
}

footer.mdc-dialog__actions {
    min-height: unset;
    padding: 0.375rem; // 6px
}

@media screen and (max-width: #{mixins.$narrow-dialog-breakpoint}) {
    slot[name='button'] {
        flex-direction: column-reverse;
    }
    .mdc-dialog__actions {
        padding: min(1.5rem, $responsive-body-padding);
        padding-top: 1rem;
    }
}

import {
    Component,
    Event,
    EventEmitter,
    h,
    Host,
    Prop,
    State,
} from '@stencil/core';
import { DockItem } from './dock.types';

const DEFAULT_MOBILE_BREAKPOINT = 700;

/**
 * @exampleComponent limel-example-dock-basic
 * @exampleComponent limel-example-dock-custom-component
 * @exampleComponent limel-example-dock-notification
 * @exampleComponent limel-example-dock-mobile
 * @exampleComponent limel-example-dock-expanded
 * @exampleComponent limel-example-dock-colors-css
 */
@Component({
    tag: 'limel-dock',
    shadow: true,
    styleUrl: 'dock.scss',
})
export class Dock {
    /**
     * Items that are placed in the dock.
     */
    @Prop()
    public dockItems: DockItem[] = [];

    /**
     * Items that are placed at the bottom of the dock. (Or at the end in mobile
     * layout.)
     */
    @Prop()
    public dockFooterItems?: DockItem[] = [];

    /**
     * A label used to describe the purpose of the navigation element to users
     * of assistive technologies, like screen readers. Especially useful when
     * there are multiple navigation elements in the user interface.
     * Example value: "Primary navigation"
     */
    @Prop({ reflect: true })
    public accessibleLabel?: string;

    /**
     * Defines the width of the component, when it loads.
     * - `true`: shows both icons and labels of the Dock items.
     * - `false`: only shows icons of the doc items, and displays
     * their labels as tooltip.
     *
     * Note: when `useMobileLayout` is `true`, labels will always
     * be shown as tooltips. Read more belowâ€¦
     */
    @Prop({ reflect: true })
    public expanded? = false;

