import { Component, h, Host, State } from '@stencil/core';

/**
 * Basic Example
 * The component will render a static avatar by default.
 * But you can trigger animations, based on user's interactions,
 * for example on `:hover`.
 *
 * This is easily done by toggling a custom CSS property:
 *
 * ```scss
 * limel-ai-avatar {
 *     &:hover {
 *         --ai-avatar-animation-play-state: running;
 *     }
 * }
 * ```
 *
 */
@Component({
    tag: 'limel-example-ai-avatar-basic',
    shadow: true,
    styleUrl: 'ai-avatar-basic.scss',
})
export class AiAvatarBasicExample {
    @State()
    private isThinking = false;

    public render() {
        return (
            <Host>
                <div class="avatar-container">
                    <limel-ai-avatar isThinking={this.isThinking} />
                </div>
                <limel-example-controls>
                    <limel-checkbox
                        checked={this.isThinking}
                        label="Is thinking"
                        onChange={this.setIsThinking}
                    />
                </limel-example-controls>
            </Host>
        );
    }

    private readonly setIsThinking = (event: CustomEvent<boolean>) => {
        event.stopPropagation();
        this.isThinking = event.detail;
    };
}

:host {
    --example-controls-max-columns-width: 100%;
}

.avatar-container {
    transition: background-color 0.3s ease;

    display: block;
    position: relative;
    resize: horizontal;
    overflow: auto;

    box-sizing: border-box;

    min-width: 3rem;
    max-width: 100%;

    min-height: 2rem;
    height: auto;
    max-height: 20rem;

    padding: 1rem 0.25rem 3rem 0.25rem;
    border: 0.125rem dashed rgb(var(--contrast-500));

    border-radius: 0.5rem;

    &:after {
        content: 'Resize me â¤µ';
        font-size: 0.75rem;
        position: absolute;
        right: 0.25rem;
        bottom: 0.25rem;
    }
}

limel-ai-avatar {
    margin: auto;
    background-color: rgb(var(--color-glaucous-default));

    &:hover {
        --ai-avatar-animation-play-state: running;
    }
}

import { Component, h, Host, State } from '@stencil/core';

/**
 * With background colors
 * To render better, the AI avatar needs to be placed on a colored background.
 */
@Component({
    tag: 'limel-example-ai-avatar-colors',
    shadow: true,
    styleUrl: 'ai-avatar-basic.scss',
})
export class AiAvatarColorsExample {
    @State()
    private isThinking = false;

    @State()
    private backgroundColor = 'rgb(var(--color-blue-default))';

    public render() {
        return (
            <Host>
                <div class="avatar-container">
                    <limel-ai-avatar
                        style={{
                            backgroundColor: this.backgroundColor,
                            borderRadius: '50%',
                        }}
                        isThinking={this.isThinking}
                    />
                </div>
                <limel-example-controls>
                    <limel-checkbox
                        checked={this.isThinking}
                        label="Is thinking"
                        onChange={this.setIsThinking}
                    />
                    <limel-color-picker-palette
                        label="Background Color"
                        helperText="Select a background color for the AI avatar"
                        value={this.backgroundColor}
                        onChange={this.handleColorChange}
                    />
                </limel-example-controls>
            </Host>
        );
    }

    private readonly handleColorChange = (event: CustomEvent<string>) => {
        this.backgroundColor = event.detail;
    };

    private readonly setIsThinking = (event: CustomEvent<boolean>) => {
        event.stopPropagation();
        this.isThinking = event.detail;
    };
}

:host {
    --example-controls-max-columns-width: 100%;
}

.avatar-container {
    transition: background-color 0.3s ease;

    display: block;
