                <limel-button
                    label="Some Action"
                    onClick={this.someCustomAction}
                    slot="buttons"
                />
                <limel-button
                    label="Close"
                    onClick={this.closeBanner}
                    slot="buttons"
                />
            </limel-banner>,
        ];
    }

