    public schema: FormSchema;

    @State()
    private props = {
        disabled: false,
        value: [
            {
                id: '1',
                title: 'Clear sky',
                icon: 'sun',
            },
            {
                id: '2',
                title: 'Partly cloudy',
                icon: 'partly_cloudy_day',
            },
            {
                id: '3',
                title: 'Rain showers',
                icon: 'rain',
            },
            {
                id: '4',
                title: 'Thunderstorms',
                icon: 'cloudshot',
            },
            {
                id: '5',
                title: 'Snow showers',
                icon: 'snowflake',
            },
        ] as Button[],
    };

    private eventPrinter: HTMLLimelExampleEventPrinterElement;

    public render() {
        return [
            <limel-button-group {...this.props} onChange={this.handleChange} />,
            this.renderForm(),
            <limel-example-event-printer
                ref={(el) => (this.eventPrinter = el)}
            />,
        ];
    }

    private renderForm() {
        return (
            <limel-example-controls
                style={{ '--example-controls-column-layout': 'auto-fit' }}
            >
                <limel-form
                    schema={this.schema}
                    value={this.props}
                    onChange={this.handleFormChange}
                />
            </limel-example-controls>
        );
