        this.eventPrinter.writeEvent(event);
        const changedButton = event.detail;

        this.props = {
            ...this.props,
            value: this.props.value.map((button) => {
                return {
                    ...button,
                    selected: button.id === changedButton.id,
                };
            }),
        };
    };

    private handleFormChange = (event: CustomEvent) => {
        this.props = { ...event.detail };
    };
}

