    @State()
    private orientation: 'landscape' | 'portrait' = 'landscape';

    @State()
    private type: 'bar' | 'dot' | 'line' = 'dot';

    private orientations: Option[] = [
        { text: 'landscape', value: 'landscape' },
        { text: 'portrait', value: 'portrait' },
    ];

    private types: Option[] = [
        { text: 'Bar', value: 'bar' },
        { text: 'Dot', value: 'dot' },
        { text: 'Line', value: 'line' },
    ];

    public render() {
        return (
            <Host class="large">
                <h4>Temperature right now</h4>
                <limel-chart
                    type={this.type}
                    items={chartItems}
                    orientation={this.orientation}
                />
                <limel-example-controls>
                    <limel-select
                        label="orientation"
                        value={this.getSelectedOrientation()}
                        options={this.orientations}
                        onChange={this.handleOrientationChange}
                    />
                    <limel-select
                        label="type"
                        value={this.getSelectedType()}
                        options={this.types}
                        onChange={this.handleTypeChange}
                    />
