            value: 1,
        },
        {
            id: 2,
            text: 'Befkadu',
            removable: true,
            icon: 'person_male',
            value: 2,
        },
    ];

    public render() {
        return (
            <Host onMenuItemSelected={this.onMenuItemSelected}>
                <limel-chip-set
                    type="input"
