    private rejectedFiles: FileInfo[] = [];

    public render() {
        const MAX_LENGTH = 500;

        return [
            <limel-file-dropzone
                onFilesSelected={this.handleAcceptedFiles}
                onFilesRejected={this.handleRejectedFiles}
                accept="image/*"
                text="Drop pictures here"
