@Component({
    tag: 'limel-example-form-span-fields',
    shadow: true,
})
export class FormLayoutExample {
    @State()
    private formData: Record<string, string> = {};

    @State()
    private valid = true;

    @State()
    private dense = true;

    @State()
    private schema = schema;

    public render() {
        return [
            <limel-example-controls
                style={{ '--example-controls-column-layout': 'auto-fit' }}
            >
                <limel-switch
                    label="Dense layout"
                    value={this.dense}
                    onChange={this.handleCheckboxChange}
                />
            </limel-example-controls>,
            <limel-form
                onChange={this.handleFormChange}
                onValidate={this.handleFormValidate}
                value={this.formData}
                schema={this.schema}
            />,
            <limel-button
                label="Submit"
                primary={true}
                disabled={!this.valid}
                onClick={this.handleSubmit}
            />,
        ];
    }

    private handleFormChange = (event) => {
        this.formData = event.detail;
    };

    private handleFormValidate = (event: CustomEvent<ValidationStatus>) => {
        this.valid = event.detail.valid;
        console.log(event.detail);
    };

    private handleSubmit = () => {
        const json = JSON.stringify(this.formData, null, '    ');
        alert(`Sending information to villains...\n\n${json}`);
    };

    private handleCheckboxChange = (event: CustomEvent<boolean>) => {
        this.dense = event.detail;
        this.schema = { ...this.schema };
        this.schema.lime.layout.dense = this.dense;
    };
}

import { FormSchema } from '@limetech/lime-elements';

export const schema: FormSchema = {
    title: 'A form with fields that span columns and rows',
    description:
        'This main form has a grid layout with 5 columns. Notice how fields reorder to fill holes when the "Dense layout" is enabled. You may need to resize your browser window to see this responsive layout in effect.',
    type: 'object',
    lime: {
        layout: {
            type: 'grid',
            columns: 5,
            dense: true,
        },
    },
    properties: {
        field1: {
            type: 'string',
            title: 'Nr. 1: spans 1 column',
        },
        field2: {
            type: 'integer',
            title: 'Nr. 2: spans 2 columns',
            lime: {
                layout: {
                    colSpan: 2,
                },
            },
        },
        field3: {
            type: 'string',
            title: 'Nr. 3: spans 2 columns',
            lime: {
                layout: {
                    colSpan: 2,
                },
            },
        },
        field4: {
            type: 'string',
            title: 'Nr. 4: spans 1 columns',
        },
        field5: {
            type: 'number',
            title: 'Nr. 6: spans 2 columns & 3 rows',
            lime: {
                component: {
                    name: 'limel-example-form-map-component',
                },
                layout: {
                    colSpan: 2,
                    rowSpan: 3,
                },
            },
        },
        field6: {
            type: 'string',
            title: 'Nr. 5: spans 2 columns & 2 rows',
            lime: {
                component: {
                    props: {
                        type: 'textarea',
                    },
                },
                layout: {
                    colSpan: 2,
                    rowSpan: 2,
                },
            },
        },
        field7: {
            type: 'number',
            title: 'Nr. 7: spans all columns',
