/**
 * Composite
 *
 * A place to try different combinations of states.
 */
@Component({
    tag: 'limel-example-picker-composite',
    shadow: true,
})
export class PickerCompositeExample {
    @Prop({ mutable: true })
    public schema: FormSchema;

    @State()
    private props: {
        value: ListItem<number | string> | Array<ListItem<number | string>>;
        [key: string]: any;
    } = {
        label: 'My label',
        searchLabel: 'My search label',
        helperText: 'My helper text',
        leadingIcon: 'search',
        emptyResultMessage: 'No matches found',
        delimiter: '',
        value: [],
        required: false,
        disabled: false,
        readonly: false,
        loading: false,
        multiple: true,
        badgeIcons: true,
    };

    private eventPrinter: HTMLLimelExampleEventPrinterElement;

    private allItems: Array<ListItem<number>> = [
        { text: 'Admiral Swiggins', value: 1 },
        { text: 'Ayla', value: 2 },
        { text: 'Clunk', value: 3 },
        { text: 'Coco', value: 4 },
        { text: 'Derpl', value: 5 },
        { text: 'Froggy G', value: 6 },
        { text: 'Gnaw', value: 7 },
        { text: 'Lonestar', value: 8 },
        { text: 'Leon', value: 9 },
        { text: 'Raelynn', value: 10 },
        { text: 'SkÃ¸lldir', value: 11 },
        { text: 'Voltar', value: 12 },
        { text: 'Yuri', value: 13 },
    ];

    private availableItems: Array<ListItem<number>> = [...this.allItems];

    public componentWillLoad() {
        const schema = {
            ...this.schema,
            lime: {
                layout: {
                    type: 'grid',
                },
            },
        };
        delete schema.properties.actionPosition;
        delete schema.properties.actionScrollBehavior;
        delete schema.properties.actions;
        delete schema.properties.allItems;
        delete schema.properties.searcher;
        this.schema = schema;
    }

    public render() {
        return [
            <limel-picker
                {...this.props}
                allItems={this.availableItems}
                onChange={this.handleChange}
                onInteract={this.handleEvent}
            />,
            this.renderForm(),
            <limel-example-event-printer
                ref={(el) => (this.eventPrinter = el)}
            />,
        ];
    }

    private handleChange = (
        event: CustomEvent<
            ListItem<number | string> | Array<ListItem<number | string>>
        >
    ) => {
        this.props = {
            ...this.props,
            value: event.detail as any,
        };
        this.updateAvailableItems();
    };

    private updateAvailableItems = () => {
        this.availableItems = this.allItems.filter((item) => {
            return !(this.props.value as any)?.find?.(
                (selectedItem: ListItem<number | string>) => {
                    return item.value === selectedItem.value;
                }
            );
        });
    };

    private handleEvent = (event: CustomEvent) => {
        this.eventPrinter.writeEvent(event);
    };

    private renderForm() {
        return (
            <limel-example-controls
                style={{ '--example-controls-column-layout': 'auto-fit' }}
            >
                <limel-form
                    schema={this.schema}
                    value={this.props}
                    onChange={this.handleFormChange}
                />
            </limel-example-controls>
        );
    }

    private handleFormChange = (event: CustomEvent) => {
        this.props = event.detail;
    };
}

import { LimelPickerCustomEvent, ListItem } from '@limetech/lime-elements';
import { Component, h, State } from '@stencil/core';

const NETWORK_DELAY = 500;

/**
 * With a custom search function
 *
 * The custom search function returns two suggestions if the query is empty.
 * Otherwise, it filters the items based on the query.
 *
 * :::important
 * This example simulates that searching is done on the server. Because these
 * examples do not _actually_ send requests to the server, we simulate a small
 * delay, using `setTimeout`. **Please do NOT copy that to production code!**
 * See the other examples for code that does not include this artificial delay.
 * :::
 */
@Component({
    tag: 'limel-example-picker-empty-suggestions',
    shadow: true,
})
export class PickerExample {
    @State()
    private selectedItem: ListItem<number>;

    private allItems: Array<ListItem<number>> = [
        { text: 'Admiral Swiggins', value: 1 },
        { text: 'Ayla', value: 2 },
        { text: 'Clunk', value: 3 },
        { text: 'Coco', value: 4 },
        { text: 'Derpl', value: 5 },
        { text: 'Froggy G', value: 6 },
        { text: 'Gnaw', value: 7 },
        { text: 'Lonestar', value: 8 },
        { text: 'Leon', value: 9 },
        { text: 'Raelynn', value: 10 },
        { text: 'SkÃ¸lldir', value: 11 },
        { text: 'Voltar', value: 12 },
        { text: 'Yuri', value: 13 },
    ];

    public render() {
        return [
            <limel-picker
                label="Favorite awesomenaut"
                value={this.selectedItem}
                searcher={this.search}
                emptyResultMessage="No results"
                onChange={this.onChange}
                onInteract={this.onInteract}
            />,
            <limel-example-value value={this.selectedItem} />,
        ];
    }

    private search = async (query: string): Promise<ListItem[]> => {
        // Simulate network delay
        await new Promise((resolve) => setTimeout(resolve, NETWORK_DELAY));

        if (query === '') {
            return this.allItems.slice(8, 10);
        }

        const filteredItems = this.allItems.filter((item) => {
            return item.text.toLowerCase().includes(query.toLowerCase());
        });

        return filteredItems;
    };

    private onChange = (event: LimelPickerCustomEvent<ListItem<number>>) => {
        this.selectedItem = event.detail;
    };

    private onInteract = (event) => {
        console.log('Value interacted with:', event.detail);
    };
}

