import {
    Component,
    h,
    Event,
    EventEmitter,
    Host,
    Prop,
    State,
} from '@stencil/core';
import { FileInfo } from '../../global/shared-types/file.types';
import { createFileInfo, isTypeAccepted } from '../../util/files';
import { partition } from 'lodash-es';

/**
 * This component enables you to seamlessly convert any region of the user interface into
 * a file dropzone area, just by wrapping it inside the `limel-file-dropzone`.
 *
 * The file dropzone can then be used to allow end-users to upload files
 * by dragging and dropping them into the specified area, for example to trigger an upload process.
 *
 * After receiving the files, the component emits a `filesSelected` event. For unsupported
 * files (specified with the `accept` prop) a `filesRejected` event will be emitted.
 *
 * The event detail would be an array of `FileInfo` objects,
 * each representing a file dropped into the dropzone.
 *
 * @exampleComponent limel-example-file-dropzone
 * @exampleComponent limel-example-file-dropzone-type-filtering
 * @private
 */
@Component({
    tag: 'limel-file-dropzone',
    shadow: true,
    styleUrl: 'file-dropzone.scss',
})
export class FileDropzone {
    /**
     * Specifies the types of files that the dropzone will accept. By default, all file types are accepted.
     *
     * For media files, formats can be specified using: `audio/*`, `video/*`, `image/*`.
     * Unique file type specifiers can also be used, for example: `.jpg`, `.pdf`.
     * A comma-separated list of file extensions or MIME types is also acceptable, e.g., `image/png, image/jpeg` or
     * `.png, .jpg, .jpeg`.
     *
     * @see [HTML attribute: accept](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/accept) for more
     * details.
     */
    @Prop({ reflect: true })
    public accept: string = '*';

    /**
     * Set to `true` to disable the file dropzone.
     */
    @Prop()
    public disabled: boolean = false;

    /**
     * Is displayed when the user is dragging a file over the dropzone.
     * A suitable text could for instance be "Drop your files here".
     */
    @Prop()
    public text: string;

    /**
     * Is displayed to provide supplementary information to the end users,
     * for instance, which filetypes or file sizes are accepted.
     */
    @Prop()
    public helperText?: string = '';

    /**
     * Sets to true when there is a file to drop
     */
    @State()
    private hasFileToDrop: boolean = false;

    /**
     * Emitted when files are selected
     */
    @Event()
    filesSelected: EventEmitter<FileInfo[]>;

    /**
     * Emitted when files are selected but do not conform with the `accept` property specifications.
     * This can happen when the file types or formats of the selected files are not among the ones allowed by the dropzone,
     * as defined by the `accept` property.
     *
     * @see `accept` for details on how to specify acceptable file types.
     */
    @Event()
    filesRejected: EventEmitter<FileInfo[]>;

    public render() {
        return (
            <Host
                onDrop={this.handleDrop}
                onDragOver={this.handleDragOver}
                onDragLeave={this.handleDragLeave}
            >
                <slot />
                {this.renderOnDragLayout()}
            </Host>
        );
    }

    private renderOnDragLayout = () => {
        if (this.disabled || !this.hasFileToDrop) {
            return;
        }

        return (
            <div class="has-file-to-drop">
                <limel-icon class="icon" name="upload_2" />
                <div class="text-helpertext">
                    {this.renderText()}
                    {this.renderHelperText()}
                </div>
            </div>
        );
    };

    private renderText = () => {
        if (!this.text) {
            return;
        }

        return <span class="text">{this.text}</span>;
    };

    private renderHelperText = () => {
        if (!this.helperText) {
            return;
        }

        return <span class="helper-text">{this.helperText}</span>;
    };

    private handleDrop = (event: DragEvent) => {
        event.stopPropagation();
        event.preventDefault();
        this.hasFileToDrop = false;

        if (this.disabled) {
            return;
        }

        const files: File[] = [...event.dataTransfer.files];
        const fileInfos: FileInfo[] = files.map(createFileInfo);

        const [acceptedFileInfos, rejectedFileInfos] = partition(
            fileInfos,
            (file) => isTypeAccepted(file, this.accept)
        );

        if (acceptedFileInfos.length > 0) {
            this.filesSelected.emit(acceptedFileInfos);
        }

        if (rejectedFileInfos.length > 0) {
            this.filesRejected.emit(rejectedFileInfos);
        }
    };

    private handleDragOver = (event: DragEvent) => {
        this.hasFileToDrop = true;
        event.preventDefault();
    };

    private handleDragLeave = (event: DragEvent) => {
        this.hasFileToDrop = false;
        event.preventDefault();
    };
}

:host(limel-file-dropzone) {
    display: block;
    position: relative;
}

.has-file-to-drop {
    animation: display-drop-zone 0.6s ease forwards;

    box-sizing: border-box;
    isolation: isolate;
    z-index: 1;
    position: absolute;
    inset: 0.25rem;

    overflow: hidden;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 0.5rem;

    color: rgb(var(--contrast-700));

    border: 0.125rem dashed rgb(var(--color-cyan-light));
    border-radius: 0.75rem;
}

.text-helpertext {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: flex-start;
}

.icon {
    width: clamp(2rem, 5vh, 7rem);
}

.text {
    font-size: clamp(1rem, 2vh, 1.75rem);
}

.helper-text {
    font-size: clamp(0.75rem, 1.5vh, 1rem);
}

@keyframes display-drop-zone {
    0% {
        background-color: rgb(var(--contrast-1100), 0);
        backdrop-filter: blur(0);
        -webkit-backdrop-filter: blur(0);
        scale: 0.9;
        opacity: 0;
    }

    50% {
        scale: 1;
        opacity: 1;
    }

    100% {
        background-color: rgb(var(--contrast-1100), 0.8);
        backdrop-filter: blur(0.25rem);
        -webkit-backdrop-filter: blur(0.25rem);
    }
}

import {
    h,
    Event,
    EventEmitter,
    Host,
    Component,
    Element,
    Prop,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { FileInfo } from '../../global/shared-types/file.types';
import { createFileInfo } from '../../util/files';

/**
 * This component enables you to seamlessly transform any other clickable component that
 * generates a `click` event into a file input selector.
 *
 * To use it, just wrap any clickable component inside the `limel-file-input` component.
 * Upon reception of the `click` event this component will open the native file selection
 * dialog.
 *
 * After receiving the files, the component emits a `filesSelected` event.
 *
 * The event detail would be an array of `FileInfo` objects,
 * each representing a file dropped into the dropzone.
 *
 * @exampleComponent limel-example-file-input
 * @exampleComponent limel-example-file-input-type-filtering
 * @private
 */
@Component({
    tag: 'limel-file-input',
    shadow: true,
})
export class FileInput {
    /**
     * Specifies the types of files that the dropzone will accept. By default, all file types are accepted.
     *
     * For media files, formats can be specified using: `audio/*`, `video/*`, `image/*`.
     * Unique file type specifiers can also be used, for example: `.jpg`, `.pdf`.
     * A comma-separated list of file extensions or MIME types is also acceptable, e.g., `image/png, image/jpeg` or
     * `.png, .jpg, .jpeg`.
     *
     * @see [HTML attribute: accept](https://developer.mozilla.org/en-US/docs/Web/HTML/Attributes/accept) for more
     * details.
     */
    @Prop({ reflect: true })
    public accept: string = '*';

    /**
     * Set to `true` to disable file input selection.
     */
    @Prop({ reflect: true })
    public disabled: boolean = false;

    /**
     * Set to `true` to enable selection of multiple files
     */
    @Prop({ reflect: true })
    public multiple: boolean = false;

    /**
     * Emitted when files are selected
     */
    @Event()
    filesSelected: EventEmitter<FileInfo[]>;

    @Element()
    private element: HTMLLimelFileElement;

    private fileInput: HTMLInputElement;
    private fileInputId = createRandomString();

    public componentDidLoad() {
        // eslint-disable-next-line unicorn/prefer-query-selector
        this.fileInput = this.element.shadowRoot.getElementById(
            this.fileInputId
        ) as HTMLInputElement;
    }

    public render() {
        return (
            <Host
                onClick={this.handleClick}
                onKeyUp={this.handleKeyUp}
                onKeyDown={this.handleKeyDown}
            >
                <input
                    hidden={true}
                    id={this.fileInputId}
                    onChange={this.handleFileChange}
                    type="file"
                    accept={this.accept}
                    disabled={this.disabled}
                    multiple={this.multiple}
                />
                <slot />
            </Host>
        );
    }

    private handleClick = (event: Event) => {
        if (this.disabled) {
            event.stopPropagation();
            event.preventDefault();

            return;
        }

        this.triggerFileDialog();

        event.stopPropagation();
    };

    private handleKeyUp = (event: KeyboardEvent) => {
        event.stopPropagation();
        event.preventDefault();

        if (event.code === 'Enter') {
            this.triggerFileDialog();
        }
    };

    private handleKeyDown(event: KeyboardEvent) {
        if (
            event.code === 'Tab' ||
            event.code === 'Backspace' ||
            event.code === 'Enter'
        ) {
            return;
        }

        event.preventDefault();
        event.stopPropagation();
    }

    private triggerFileDialog() {
        this.fileInput.click();
    }

    private handleFileChange = (event: Event) => {
        const files = [...this.fileInput.files];
        if (files.length > 0) {
            event.stopPropagation();
            this.filesSelected.emit(files.map(createFileInfo));
            this.fileInput.value = '';
        }
    };
}

import {
    Component,
    Element,
    h,
    Prop,
    State,
    Event,
    EventEmitter,
    Watch,
} from '@stencil/core';
import { Languages } from '../date-picker/date.types';
import { ListItem } from '../list/list-item.types';
import translate from '../../global/translations';
import { detectExtension } from './extension-mapping';
import { Fullscreen } from './fullscreen';
import { FileType, OfficeViewer } from './file-viewer.types';
import { LimelMenuCustomEvent } from '../../components';

/**
 * This is a smart component that automatically detects
 * the most common file types such as image, audio, video, and text,
 * and properly displays them in the browser.
 * The component is also capable to render the most common office files.
 *
 * :::note
 * Image files will always be contained in their containers, which means
 * they automatically increase or decrease in size to fill their containing box
 * whilst preserving their aspect-ratio.
 *
 * Text and PDF files will also always respect the width and height of the
 * container in which the `limel-file-viewer` is loaded.
 * :::
 *
 * For some file types such as text and images, the component will display a
 * download button and a button to open the file in a new browser tab.
 * This will allow users to preview the file in a fullscreen mode with the
 * browser and take advantage of for example native zooming and panning
 * functionalities.
 *
 * @exampleComponent limel-example-file-viewer-basic
 * @exampleComponent limel-example-file-viewer-office
 * @exampleComponent limel-example-file-viewer-filename
 * @exampleComponent limel-example-file-viewer-inbuilt-actions
 * @exampleComponent limel-example-file-viewer-custom-actions
 * @exampleComponent limel-example-file-viewer-with-picker
 *
 * @beta
 */

@Component({
    tag: 'limel-file-viewer',
    shadow: true,
    styleUrl: 'file-viewer.scss',
})
export class FileViewer {
    /**
     * Link to the file
     */
    @Prop({ reflect: true })
    public url: string;

    /**
     * The name of the file that must also contains its extension.
     * This overrides the filename that the `url` ends with.
     * Useful when the `url` does not contain the filename.
     * When specified, the `filename` will be used as filename of
     * the downloaded file.
     */
    @Prop({ reflect: true })
    public filename?: string;

    /**
     * An optional alternative text, mainly for assistive technologies and screen readers.
     * It is used for only image files, as an `alt` attribute.
     * Should optimally hold a description of the image,
     * which is also displayed on the page if the image can't be loaded for some reason.
     */
    @Prop({ reflect: true })
    public alt?: string;

    /**
     * Displays a button that allows the user to view the file
     * in fullscreen mode.
     * Not displayed for office files!
     */
    @Prop({ reflect: true })
    public allowFullscreen?: boolean = false;

    /**
     * Displays a button that allows the user to open the file
     * in a new browser tab.
     * Not displayed for office files!
     */
    @Prop({ reflect: true })
    public allowOpenInNewTab?: boolean = false;

    /**
     * Displays a button that allows the user to download the file.
     * Note that due to the browser's security policies,
     * the file should be hosted on the same domain
     * for the download button to work properly.
     * Not displayed for office files!
     */
    @Prop({ reflect: true })
    public allowDownload?: boolean = false;

    /**
     * Defines the localization for translations.
     */
    @Prop()
    public language: Languages = 'en';

    /**
     * Defines the third-party viewer that should be used to render
     * the content of office files, such as word processing documents,
     * presentations, or spreadsheets.
     */
    @Prop({ reflect: true })
    public officeViewer: OfficeViewer = 'microsoft-office';

    /**
     * An array of custom actions that can be displayed
     * as an action menu on the file which is being displayed.
     */
    @Prop()
    public actions: ListItem[];

    /**
     * Emitted when a custom action is selected from the action menu.
     */
    @Event()
    public action: EventEmitter<ListItem>;

    @Element()
    public HostElement: HTMLLimelFileViewerElement;

    private fullscreen: Fullscreen;

    @State()
    private isFullscreen: boolean = false;

    @State()
    private fileType: FileType;

    /**
     * True while the file is being loaded.
     */
    @State()
    private loading: boolean = true;

    @State()
    private fileUrl: string = '';

    constructor() {
        this.fullscreen = new Fullscreen(this.HostElement);
    }

    public async componentWillLoad() {
        this.fileType = detectExtension(this.filename, this.url);
        await this.createURL(this.fileType);
    }

    public render() {
        if (!this.isOfficeFileAccessibleViaURL) {
            return this.renderNoFileSupportMessage();
        }

        if (this.loading) {
            return <limel-spinner size="x-small" limeBranded={false} />;
        }

        return this.renderFileViewer();
    }

    @Watch('url')
    protected async watchUrl(newUrl: string, oldUrl: string) {
        if (newUrl === oldUrl) {
            return;
        }

        this.loading = true;
        this.fileType = detectExtension(this.filename, this.fileUrl);
        await this.createURL(this.fileType);
    }

    private renderFileViewer() {
        const fileViewerFunctions = {
            pdf: this.renderPdf,
            image: this.renderImage,
            video: this.renderVideo,
            audio: this.renderAudio,
            text: this.renderText,
            office: this.renderOffice,
        };
        const fileViewerFunction =
            fileViewerFunctions[this.fileType] ||
            this.renderNoFileSupportMessage;

        return fileViewerFunction();
    }

    private renderPdf = () => {
        return [
            <div class="action-menu-for-pdf-files">
                {this.renderActionMenu()}
            </div>,
            <iframe src={this.fileUrl} loading="lazy" />,
        ];
    };

    private renderImage = () => {
        return [
            this.renderButtons(),
            <img src={this.fileUrl} alt={this.alt} loading="lazy" />,
        ];
    };

    private renderVideo = () => {
        return (
            <video controls>
                <source src={this.fileUrl} />
            </video>
        );
    };

    private renderAudio = () => {
        return (
            <audio controls>
                <source src={this.fileUrl} />
            </audio>
        );
    };

    private renderText = () => {
        return [
            this.renderButtons(),
            <object data={this.fileUrl} type="text/plain">
                {`Text content from: ${this.filename || this.url}`}
                <a
                    href={this.fileUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    Open text file in new tab
                </a>
            </object>,
        ];
    };

    private renderOffice = () => {
        return [
            <div class="action-menu-for-office-files">
                {this.renderActionMenu()}
            </div>,
            <iframe
                src={
                    this.getOfficeViewerUrl() + this.fileUrl + '&embedded=true'
                }
                loading="lazy"
            />,
        ];
    };

    private isOfficeFileAccessibleViaURL = () => {
        return (
            this.fileType === 'office' &&
            !(
                this.fileUrl.startsWith('http://') ||
                this.fileUrl.startsWith('https://')
            )
        );
    };

    private getOfficeViewerUrl = () => {
        const officeViewers = {
            'microsoft-office':
                'https://view.officeapps.live.com/op/embed.aspx?src=',
            'google-drive': 'https://docs.google.com/gview?url=',
        };

        return officeViewers[this.officeViewer];
    };

    private renderNoFileSupportMessage = () => {
        return (
            <div class="no-support" role="alert">
                <limel-icon
                    class="icon--warning"
                    name="brake_warning"
                    size="large"
                    role="presentation"
                />
                <p>
                    {this.getTranslation(
                        'file-viewer.message.unsupported-filetype'
                    )}
                </p>
                {this.renderDownloadButton()}
            </div>
        );
    };

    private renderButtons = () => {
        return (
            <div class="buttons">
                {this.renderActionMenu()}
                {this.renderToggleFullscreenButton()}
                {this.renderDownloadButton()}
                {this.renderOpenInNewTabButton()}
            </div>
        );
    };

    private renderToggleFullscreenButton = () => {
        if (!this.allowFullscreen || !this.fullscreen.isSupported()) {
            return;
        }

        const icon = this.isFullscreen ? 'multiply' : 'fit_to_width';

        const label = this.isFullscreen
            ? this.getTranslation('file-viewer.exit-fullscreen')
            : this.getTranslation('file-viewer.open-in-fullscreen');

        return [
            <button
                class="button--toggle-fullscreen"
                id="tooltip-toggle-fullscreen"
                role="button"
                onClick={this.handleToggleFullscreen}
            >
                <limel-icon name={icon} />
                <limel-tooltip
                    label={label}
                    elementId="tooltip-toggle-fullscreen"
                    openDirection="left"
                />
            </button>,
        ];
    };

    private renderDownloadButton = () => {
        if (!this.allowDownload || this.isFullscreen) {
            return;
        }

        return (
            <a
                class="button--download"
                id="tooltip-download"
                role="button"
                download={this.filename ?? ''}
                href={this.fileUrl}
                target="_blank"
            >
                <limel-icon name="download_2" />
                <limel-tooltip
                    label={this.getTranslation('file-viewer.download')}
                    elementId="tooltip-download"
                    openDirection="left"
                />
            </a>
        );
    };

    private renderOpenInNewTabButton = () => {
        if (!this.allowOpenInNewTab || this.isFullscreen) {
            return;
        }

        return (
            <a
                class="button--new-tab"
                id="tooltip-new-tab"
                role="button"
                href={this.fileUrl}
                target="_blank"
                rel="noopener noreferrer"
            >
                <limel-icon name="external_link" />
                <limel-tooltip
                    label={this.getTranslation('file-viewer.open-in-new-tab')}
                    elementId="tooltip-new-tab"
                    openDirection="left"
                />
            </a>
        );
    };

    private renderActionMenu = () => {
        if (!this.actions || this.isFullscreen) {
            return;
        }

        return (
            <limel-menu
                class="action-menu"
                items={this.actions}
                onSelect={this.emitOnAction}
                open-direction="left"
            >
                <button
                    class="button--action"
                    id="tooltip-more"
                    role="button"
                    slot="trigger"
                >
                    <limel-icon name="menu_2" />
                    <limel-tooltip
                        label={this.getTranslation('file-viewer.more-actions')}
                        elementId="tooltip-more"
                        openDirection="left"
                    />
                </button>
            </limel-menu>
        );
    };

    private createURL = async (fileType: string) => {
        if (['pdf'].includes(fileType)) {
            const response = await fetch(this.url);
            const blob = await response.blob();

            this.fileUrl = URL.createObjectURL(blob);
        } else {
            this.fileUrl = this.url;
        }

        this.loading = false;
    };

    private handleToggleFullscreen = () => {
        if (this.fullscreen.isSupported()) {
            this.fullscreen.toggle();
            this.isFullscreen = !this.isFullscreen;
        }
    };

    private emitOnAction = (event: LimelMenuCustomEvent<ListItem>) => {
        event.stopPropagation();
        this.action.emit(event.detail);
    };

    private getTranslation(key: string) {
        return translate.get(key, this.language);
    }
}

