import { ObjectField as CustomObjectField } from './fields/object-field';
import { widgets } from './widgets';
import { createRandomString } from '../../util/random-string';
import Ajv, { RequiredParams } from 'ajv';
import { isInteger } from './validators';
import { mapValues } from 'lodash-es';

/**
 * @exampleComponent limel-example-form
 * @exampleComponent limel-example-nested-form
 * @exampleComponent limel-example-list-form
 * @exampleComponent limel-example-dynamic-form
 * @exampleComponent limel-example-custom-component-form
 * @exampleComponent limel-example-props-factory-form
 * @exampleComponent limel-example-form-layout
 * @exampleComponent limel-example-form-span-fields
 * @exampleComponent limel-example-custom-error-message
 * @exampleComponent limel-example-server-errors
 * @exampleComponent limel-example-form-with-help
 * @exampleComponent limel-example-form-row-layout
 */
@Component({
    tag: 'limel-form',
    shadow: true,
    styleUrl: 'form.scss',
})
export class Form {
    /**
     * The schema used to render the form
     */
    @Prop()
    public schema: FormSchema = {};

    /**
     * Value of the form
     */
    @Prop()
    public value: object;

    /**
     * Set to `true` to disable the whole form.
     */
    @Prop()
    public disabled = false;

    /**
     * Factory for creating properties for custom form components
     *
     * When using custom components in the form some properties might have to be
     * set dynamically. If this factory is set, it will be called with the
     * current schema for the field for each custom component in the form. The
     * factory must return an object where each key is the name of the property
     * that should be set, along with its value.
     */
    @Prop()
    public propsFactory?: (schema: FormSchema) => Record<string, any>;

    /**
     * Custom function to customize the default error messages
     */
    @Prop()
    public transformErrors?: (errors: FormError[]) => FormError[];

    /**
     * Extra errors to display in the form. Typical use case is asynchronous
     * errors generated server side.
     */
    @Prop()
    public errors: ValidationError;

    /**
     * Emitted when a change is made within the form
     */
    @Event()
    public change: EventEmitter<object>;

    /**
     * Emitted when the validity of the form changes, or when
     * a change is made to an invalid form
     */
    @Event()
    public validate: EventEmitter<ValidationStatus>;

    @Element()
    private host: HTMLLimelFormElement;

    private isValid = true;
    private modifiedSchema: FormSchema;
    private validator: Ajv.ValidateFunction;
    private root: Root;

    public constructor() {
        this.handleChange = this.handleChange.bind(this);
        this.getCustomErrorMessages = this.getCustomErrorMessages.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentWillLoad() {
        this.setSchemaId();
        this.createValidator();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        if (!this.host.shadowRoot.querySelector('.root')) {
            return;
        }

        this.reactRender();
        retargetEvents(this.host.shadowRoot);
        this.validateForm(this.value);
    }

    public componentDidUpdate() {
        this.reactRender();
        this.validateForm(this.value);
    }

    public disconnectedCallback() {
        if (this.root) {
            this.root.unmount();
            this.root = undefined;
        }
    }

    public render() {
        return <div class="root" />;
    }

    private reactRender() {
        if (!this.root) {
            const rootElement = this.host.shadowRoot.querySelector('.root');
            this.root = createRoot(rootElement);
        }

        this.root.render(
            React.createElement(
                JSONSchemaForm,
                {
                    schema: this.modifiedSchema,
                    formData: this.value,
                    onChange: this.handleChange,
                    widgets: widgets,
                    liveValidate: true,
                    showErrorList: false,
                    extraErrors: this.getExtraErrors(this.errors),
                    FieldTemplate: FieldTemplate,
                    ArrayFieldTemplate: ArrayFieldTemplate as any,
                    ObjectFieldTemplate: ObjectFieldTemplate,
                    disabled: this.disabled,
                    transformErrors: this.getCustomErrorMessages,
                    formContext: {
                        schema: this.modifiedSchema,
                        rootValue: this.value,
                        propsFactory: this.propsFactory,
                    },
                    fields: {
                        SchemaField: CustomSchemaField as any,
                        ArrayField: CustomArrayField as any,
                        ObjectField: CustomObjectField as any,
                    },
                },
                []
            )
        );
    }

    private handleChange(event: any) {
        this.change.emit(event.formData);
    }

    private validateForm(value: object) {
        const isValid = this.validator(value) === true;
        const errors: FormError[] = this.getValidationErrors();
        const status: ValidationStatus = {
            valid: isValid,
            errors: errors,
        };

        if (this.isValid !== status.valid || !status.valid) {
            this.validate.emit(status);
        }

        this.isValid = status.valid;
    }

    @Watch('schema')
    public setSchema() {
        this.setSchemaId();
        this.createValidator();
    }

    private setSchemaId() {
        // Due to a bug in react-jsonschema-form, validation will stop working if the schema is updated.
        // A workaround at the moment is to always give it a unique ID
        // https://github.com/rjsf-team/react-jsonschema-form/issues/1563
        const id = `${this.schema.$id}-${createRandomString()}`;
        this.modifiedSchema = {
            ...this.schema,
            id: id,
            $id: id,
        };
    }

    private createValidator() {
        const validator = new Ajv({
            unknownFormats: 'ignore',
            allErrors: true,
            multipleOfPrecision: 2,
        }).addFormat('integer', isInteger);
        this.validator = validator.compile(this.schema);
    }

    private getValidationErrors(): FormError[] {
        const errors = this.validator.errors || [];

        return errors.map((error: Ajv.ErrorObject): FormError => {
            let property = error.dataPath;
            if (error.keyword === 'required') {
                property = (error.params as RequiredParams).missingProperty;
            }

            return {
                name: error.keyword,
                property: property,
                message: error.message,
                schemaPath: error.schemaPath,
            };
        });
    }

    private getExtraErrors(errors: ValidationError): ExtraError | undefined {
        if (!errors) {
            return;
        }

        return mapValues(errors, (error) => {
            if (Array.isArray(error)) {
                return { __errors: error };
            }

            return this.getExtraErrors(error);
        });
    }

    private getCustomErrorMessages(originalErrors: AjvError[]): AjvError[] {
        if (!this.transformErrors) {
            return originalErrors;
        }

        const errors: FormError[] = originalErrors.map((error: AjvError) => {
            return {
                name: error.name,
                params: error.params,
                property: error.property,
                message: error.message,
                // For some reason 'schemaPath' is missing from the AjvError type definition:
                // https://github.com/rjsf-team/react-jsonschema-form/issues/2140

                schemaPath: error['schemaPath'],
            };
        });

        // Use `.call({}, â€¦)` here to bind `this` to an empty object to prevent
        // the consumer submitted `transformErrors` from getting access to our
        // component's internals. /Ads
        return this.transformErrors
            .call({}, errors)
            .map((transformedError: FormError) => {
                const originalError = originalErrors.find((error: AjvError) => {
                    return transformedError.property === error.property;
                });

                return {
                    ...originalError,
                    message: transformedError.message,
                };
            });
    }
}

interface ExtraError {
    [key: string]:
        | ExtraError
        | {
              __errors: string[];
          };
}

@use '../../style/internal/shared_input-select-picker';

/**
 * @prop --form-body-padding: space around content of the form. Defaults to `1rem`.
 * @prop --form-column-gap: defines the vertical space between elements of the form with `grid` and `default` layouts. Defaults to `1rem`.
 * @prop --form-row-gap: defines the horizontal space between elements of the form with `grid` and `default` layouts. Defaults to `1rem`.
 * @prop --form-gap: defines the space between elements of the form with `grid` and `default` layouts. Defaults to `1rem`.
 * @prop --form-background-color-of-even-rows: Background of even rows in the form, when layout type is `row`. Defaults to `transparent`.
 * @prop --form-background-color-of-odd-rows:Background of odd rows in the form, when layout type is `row`. Defaults to `--contrast-200`.
 */

.form-group {
    min-width: 0;
}

.limel-form-array-item--simple {
    display: flex;
    align-items: center;
    padding-bottom: var(--form-row-gap, 1rem);

    *:first-child {
        flex-grow: 1;
    }
}

limel-code-editor {
    margin-bottom: 0.75rem;
}

.limel-form-array-item--object {
    margin-bottom: 0.25rem;
}

.limel-form-layout--default {
    display: grid;
    column-gap: var(--form-column-gap, 1rem);
    row-gap: var(--form-row-gap, 1rem);
    padding: var(--form-body-padding, 1rem);
}

.limel-form-layout--grid {
    --min-height-of-one-row: #{shared_input-select-picker.$height-of-mdc-text-field};
    display: grid;
    column-gap: var(--form-column-gap, 1rem);
    row-gap: var(--form-row-gap, 1rem);
    padding: var(--form-body-padding, 1rem);

    // Using `minmax(0, 1fr)` below, instead of just `1fr` ensures that
    // components that are wider than their column don't make the column grow
    // wider and squash the other columns. /Ads
    grid-template-columns: repeat(var(--number-of-columns), minmax(0, 1fr));

    &.auto-reorder-to-avoid-empty-cells {
        grid-auto-flow: dense;
    }

    &.layout-1-columns {
        .limel-form-layout-colspan--1,
        .limel-form-layout-colspan--2,
        .limel-form-layout-colspan--3,
        .limel-form-layout-colspan--4,
        .limel-form-layout-colspan--5 {
            grid-column: span 1;
        }
    }

    &.layout-2-columns {
        .limel-form-layout-colspan--1 {
            grid-column: span 1;
        }
        .limel-form-layout-colspan--2,
        .limel-form-layout-colspan--3,
        .limel-form-layout-colspan--4,
        .limel-form-layout-colspan--5 {
            grid-column: span 2;
        }
    }

    &.layout-3-columns {
        .limel-form-layout-colspan--1 {
            grid-column: span 1;
        }
        .limel-form-layout-colspan--2 {
            grid-column: span 2;
        }
        .limel-form-layout-colspan--3,
        .limel-form-layout-colspan--4,
        .limel-form-layout-colspan--5 {
            grid-column: span 3;
        }
    }

    &.layout-4-columns {
        .limel-form-layout-colspan--1 {
            grid-column: span 1;
        }
        .limel-form-layout-colspan--2 {
            grid-column: span 2;
        }
        .limel-form-layout-colspan--3 {
            grid-column: span 3;
        }
        .limel-form-layout-colspan--4,
        .limel-form-layout-colspan--5 {
            grid-column: span 4;
        }
    }

    &.layout-5-columns {
        .limel-form-layout-colspan--1 {
            grid-column: span 1;
        }
        .limel-form-layout-colspan--2 {
            grid-column: span 2;
        }
        .limel-form-layout-colspan--3 {
            grid-column: span 3;
        }
        .limel-form-layout-colspan--4 {
            grid-column: span 4;
        }
        .limel-form-layout-colspan--5 {
            grid-column: span 5;
        }
    }

    .limel-form-layout-colspan--all {
        grid-column: 1/-1;
    }
}

.limel-form-layout--grid {
    limel-checkbox,
    limel-switch {
        // This is because we have to align these items to top and
        // make sure they align correctly even in one-columned layouts
        min-height: var(--min-height-of-one-row);
    }
    limel-checkbox {
        //  margin-top: 0.5rem;
        display: block;
    }
    limel-switch {
        margin-left: 0.5rem;
    }
}

.form-error {
    color: var(--mdc-theme-error, #b00020);
    font-size: 0.6875rem;
    line-height: 1.5;
    visibility: inherit;
    padding-right: 1rem;
    padding-left: 1rem;
    padding-top: 0.25rem;
}

.button-add-new {
    margin-top: 0.5rem;
    width: 100%;
}

.mdc-typography--headline1 {
    position: relative;
}

.form-group {
    position: relative;

    limel-help {
        position: absolute;
        top: calc(var(--form-row-gap, 1rem) * -0.5);
        left: calc(var(--form-column-gap, 1rem) * -0.5);
    }

    .mdc-typography--headline1,
    .mdc-typography--body1 {
        color: rgb(var(--contrast-1100));
    }

    .mdc-typography--headline1 {
        --mdc-typography-headline1-font-size: 2rem;
        --mdc-typography-headline1-line-height: 2.25rem;
        --mdc-typography-headline1-letter-spacing: -0.01rem;
        --mdc-typography-headline1-font-weight: 400;
        margin-top: 1.5rem;
        margin-bottom: 0.25rem;
    }

    .mdc-typography--body1 {
        margin-top: 0;
        margin-bottom: 0.5rem;
    }

    .form-group {
        .mdc-typography--headline1,
        .mdc-typography--body1 {
            color: rgb(var(--contrast-1200));
        }

        .mdc-typography--headline1 {
            --mdc-typography-headline1-font-size: 1.625rem;
            --mdc-typography-headline1-line-height: 1.25rem;
            --mdc-typography-headline1-font-weight: 300;
            margin-top: 1rem;

            &:before {
                content: '';
                display: block;
                position: absolute;
                top: 0;
                bottom: 0;
                margin: auto;
                left: -0.75rem;

                background-color: var(--mdc-theme-primary);
                width: 0.125rem;
                height: var(--mdc-typography-headline1-line-height);
                border-radius: 0.125rem;

                opacity: 0.6;
            }
        }

        .form-group {
            .mdc-typography--headline1,
            .mdc-typography--body1 {
                color: rgb(var(--contrast-1300));
            }

            .mdc-typography--headline1 {
                --mdc-typography-headline1-font-size: 1.375rem;
                --mdc-typography-headline1-line-height: 1.5rem;
                margin-top: 0.5rem;

                &:before {
                    display: none;
                }
            }

            .form-group {
                .mdc-typography--headline1,
                .mdc-typography--body1 {
                    color: rgb(var(--contrast-1400));
                }

                .mdc-typography--headline1 {
                    --mdc-typography-headline1-font-size: 1.25rem;
                    --mdc-typography-headline1-line-height: 1.25rem;
                    margin-top: 1rem;

                    &:before {
                        display: none;
                    }
                }
            }
            .form-group {
                .mdc-typography--headline1,
                .mdc-typography--body1 {
                    color: rgb(var(--contrast-1400));
                }

                .mdc-typography--headline1 {
                    --mdc-typography-headline1-font-size: 1.25rem;
                    --mdc-typography-headline1-line-height: 1.25rem;
                    margin-top: 0.25rem;

                    &:before {
                        display: none;
                    }
                }

                .form-group {
                    .mdc-typography--headline1,
                    .mdc-typography--body1 {
                        color: rgb(var(--contrast-1400));
                    }

                    .mdc-typography--headline1 {
                        --mdc-typography-headline1-font-size: 1rem;
                        --mdc-typography-headline1-line-height: 1rem;
                        margin-top: 0.25rem;

                        &:before {
                            display: none;
                        }
                    }
                }
            }
        }
    }
}

@import './row/row.scss';

import { Component, h } from '@stencil/core';

/**
 * The `limel-grid` component creates a grid which can be used to control the
 * layout of other components. It uses CSS [grid-template-areas](https://developer.mozilla.org/en-US/docs/Web/CSS/grid-template-areas)
 * to enable easy configuration of its child-elements.
 *
 * @exampleComponent limel-example-grid
 * @slot - Grid content
 */
@Component({
    tag: 'limel-grid',
    shadow: true,
    styleUrl: 'grid.scss',
})
export class Grid {
    public render() {
        return <slot />;
    }
}

@use '../../style/functions';

/**
 * @prop --lime-grid-area: Grid layout
 * @prop --lime-grid-columns: Number of columns in the grid, defaults to 4
 * @prop --lime-grid-cell-height: Height of grid cells, defaults to `2.5rem` (40px)
 * @prop --lime-grid-gutter: width of the gutter between columns and rows, defaults to `1rem` (16px) - see https://material.io/design/layout/responsive-layout-grid.html#breakpoints for guidelines
 */

:host {
    display: block;
    position: relative;
}

:host([hidden]) {
    display: none;
}

slot {
    display: grid;
    grid-template-areas: var(--lime-grid-area, '');
    grid-template-columns: repeat(var(--lime-grid-columns, 4), minmax(0, 1fr));
    gap: var(--lime-grid-gutter, functions.pxToRem(16));
    grid-auto-flow: row dense;
    grid-auto-rows: var(--lime-grid-cell-height, functions.pxToRem(40));
    margin: 0;
    height: 100%;
    width: 100%;
}

import { Component, h, Prop } from '@stencil/core';
import { Icon } from '../../global/shared-types/icon.types';
import { getIconName } from '../icon/get-icon-props';

/**
 * A header is the top most visual element in a component, page, card, or a view.
 *
 * ## Usage
 * A header is the first thing that clarifies a context for users.
 * Due to their positions in the UI hierarchy, headers are the most
 * prominent elements of a user interface; and because of that, they carry both
 * vital information and fundamental controls for the area of the interface
 * they represent.
 *
 * For example, when a header is placed on top of a card, it should quickly
 * explain the card to the user. When placed on top of a modal, it should easily
 * clarify what the modal is about. When displayed on top of a fullscreen view,
 * it should indicate where in the system users are, and what part of the app
 * they are looking at.
 *
 *
 * ## Layout
 * The vital information in a header is usually manifested in form of an icon,
 * and a heading. A subheading also could be added to provide supplementary
 * information. There is also a third place for displaying supplementary information
 * or "supporting text", which will be rendered as a part of the subheading.
 * Along with this information, headers can also include actions, controls, or
 * menus.
 *
 * :::important
 * Such actions or menus must affect the entire section of the interface
 * which the header is representing. For example, a _Delete_ button on a card
 * header must delete that entire card and its respective contents all together,
 * not for example a selected item which is visible in the content of that card.
 * :::
 *
 *
 * :::warning
 * Do not user background color on icons in the headers. It is much better and
 * much easier for the eye if your icon itself has a color.
 * Background colors behind icons make them look like "call to action" buttons
 * and take a lot of attention from users.
 * :::
 *
 * @exampleComponent limel-example-header
 * @exampleComponent limel-example-header-slot-actions
 * @exampleComponent limel-example-header-colors
 * @exampleComponent limel-example-header-responsive
 * @exampleComponent limel-example-header-narrow
 * @slot actions - Content (actions) to be put inside the far right surface of
 * the header
 * @slot [no name] - DEPRECATED. The `actions` slot used to be unnamed. This
 * behavior has been deprecated, and support will be dropped in a future
 * version. Please add `slot="actions"` to your elements to ensure your code
 * will continue to work with future versions of Lime Elements.
 */
@Component({
    tag: 'limel-header',
    shadow: true,
    styleUrl: 'header.scss',
})
export class Header {
    /**
     * Icon to display
     */
    @Prop()
    public icon?: string | Icon;

    /**
     * Title to display
     */
    @Prop()
    public heading?: string;

    /**
     * Subheading to display
     */
    @Prop()
    public subheading?: string;

    /**
     * An extra string of text to display along with with the Subheading
     */
    @Prop()
    public supportingText?: string;

    /**
     * The visual divider that separates the `subheading` and the `supportingText`.
     * It must be a single character such as `-` or `,`.
     */
    @Prop()
    public subheadingDivider?: string = 'Â·';

    public render() {
        return [
            <div class="information">
                {this.renderIcon()}
                <div class="headings">
                    <h1 class="heading" title={this.heading}>
                        {this.heading}
                    </h1>
                    <h2 class="subheading" title={this.subheading}>
                        {this.subheading}
                        {this.renderSupportingText()}
                    </h2>
                </div>
            </div>,
            <slot name="actions">
                <slot />
            </slot>,
        ];
    }

    private renderIcon() {
        const icon = getIconName(this.icon);

        if (!icon) {
            return;
        }

        return (
            <limel-icon
                class="icon"
                badge={true}
                name={icon}
                style={{
                    '--limel-header-icon-color': `${(this.icon as Icon)?.color ?? ''}`,
                    '--limel-header-icon-background-color': `${
                        (this.icon as Icon)?.backgroundColor ?? ''
                    }`,
                    title: `${(this.icon as Icon)?.title}`,
                }}
            />
        );
    }

    private renderSupportingText() {
        if (!this.supportingText) {
            return;
        }

        return (
            <span class="subheading__supporting-text">
                {this.renderSubheadingDivider()}
                {this.supportingText}
            </span>
        );
    }

    private renderSubheadingDivider() {
        if (!this.subheadingDivider) {
            return;
        }

        return <span>{this.subheadingDivider}</span>;
    }
}

@use '../../style/mixins.scss';
@use '../../style/functions.scss';

/**
 * @prop --header-background-color: Background color of header, defaults to `--contrast-300`.
 * @prop --header-heading-color: Color of heading text, defaults to `--contrast-1100`.
 * @prop --header-subheading-color: Color of subheading text, defaults to `--contrast-900`.
 * @prop --header-supporting-text-color: Color of supporting text in subheading, defaults to `--header-subheading-color`.
 * @prop --header-top-right-left-border-radius: Top-left and top-right border radius of header, defaults to `0.75rem`.
 * @prop --header-responsive-breakpoint: Defines the minimum allowed `width` of both information and actions areas in the header, defaults to `22rem`.
 */

:host(limel-header) {
    display: flex;
    align-items: center;
    box-sizing: border-box;
    width: 100%;
    background-color: var(--header-background-color, rgb(var(--contrast-300)));
    border-top-left-radius: var(--header-top-right-left-border-radius, 0.75rem);
    border-top-right-radius: var(
        --header-top-right-left-border-radius,
        0.75rem
    );
    padding: 0.25rem;
}

.information {
    display: flex;
    flex-grow: 1;
    align-items: center;
    min-width: 0;
}

.icon {
    --limel-icon-svg-margin: 0.25rem;
    flex-shrink: 0;
    color: var(
        --limel-header-icon-color,
        var(--header-icon-color, rgb(var(--contrast-1100)))
    );
    background-color: var(
        --limel-header-icon-background-color,
        var(--header-icon-background-color, transparent)
    );
    width: 2.25rem;
    border-radius: 0.56rem;
}

.headings {
    min-width: 0;
    margin-left: 0.25rem;
}

.heading,
.subheading {
    @include mixins.truncate-text;
    margin: 0;
    padding: 0;
}

.heading {
    color: var(--header-heading-color, rgb(var(--contrast-1100)));
    font-size: 1rem;
    font-weight: 500;
}

.subheading {
    color: var(--header-subheading-color, rgb(var(--contrast-900)));
    font-size: functions.pxToRem(13);
    font-weight: 400;
}

.subheading__supporting-text {
    color: var(--header-supporting-text-color, var(--header-subheading-color));
    span {
        margin: 0 0.125rem;
    }
}

slot[name='actions'] {
    flex-shrink: 0;
}

:host(limel-header.is-narrow) {
    padding: 0.125rem 0.25rem;

    .icon {
        --limel-icon-svg-margin: 0;
        width: 1.25rem;
