import { Component, Prop, h, Element, Host, State } from '@stencil/core';
import { InfoTileProgress } from '../info-tile/info-tile.types';
import { Link } from '../../global/shared-types/link.types';
import { getMouseEventHandlers } from '../../util/3d-tilt-hover-effect';
import { getRel } from '../../util/link-helper';

/**
 * This component can be used on places such as a start page or a dashboard.
 * It offers features for visualizing aggregated data along with supplementary
 * information.
 *
 * If clicking on the component should navigate the user to
 * a new screen or web page, you need to provide a URL,
 * using the `link` property.
 *
 * @exampleComponent limel-example-info-tile
 * @exampleComponent limel-example-info-tile-badge
 * @exampleComponent limel-example-info-tile-progress
 * @exampleComponent limel-example-info-tile-loading
 * @exampleComponent limel-example-info-tile-primary-slot
 * @exampleComponent limel-example-info-tile-styling
 */
@Component({
    tag: 'limel-info-tile',
    shadow: true,
    styleUrl: 'info-tile.scss',
})
export class InfoTile {
    /**
     * A piece of text or number that is the main piece of information
     * which the component is intended to visualize.
     */
    @Prop({ reflect: true })
    public value: number | string;

    /**
     * Name of icon for the info tile.
     */
    @Prop()
    public icon?: string;

    /**
     * The text to show below the info tile. Long labels will be truncated.
     */
    @Prop({ reflect: true })
    public label?: string = null;

    /**
     * A string of text that is visually placed before the value.
     */
    @Prop({ reflect: true })
    public prefix?: string;

    /**
     * A string of text that is visually placed after the value.
     */
    @Prop({ reflect: true })
    public suffix?: string;

    /**
     * Set to `true` if info tile is disabled.
     */
    @Prop({ reflect: true })
    public disabled? = false;

    /**
     * If supplied, the info tile will display a notification badge.
     */
    @Prop({ reflect: true })
    public badge?: number | string;

    /**
     * Set to `true` to put the component in the `loading` state.
     * This does _not_ disable the link. To do so, the
     * `disabled` property should be set to `true` as well.
     */
    @Prop({ reflect: true })
    public loading? = false;

    /**
     * If supplied, the info tile will be a clickable link.
     *
     * Supplying a value also adds an elevated effect using a shadow,
     * as well as `cursor: pointer`, which appears on hover.
     * While we strongly recommend supplying a link whenever the
     * component should act as a link, if this is not possible, and
     * you need to provide interaction through a click handler,
     * you can still get the correct styling by supplying a `Link`
     * object with the `href` property set to `'#'`.
     */
    @Prop()
    public link?: Link;

    /**
     * Properties of the optional circular progress bar.
     *
     * Defaults:
     * - `maxValue`: 100
     * - `suffix`: %
     * - `displayPercentageColors`: false
     *
     * Colors change with intervals of 10 %.
     */
    @Prop()
    public progress?: InfoTileProgress;

    @Element()
    private host: HTMLElement;

    /**
     * `true` when something is assigned to the `primary` slot
     */
    @State()
    private hasPrimarySlot = false;

    private handleMouseEnter: () => void;
    private handleMouseLeave: () => void;

    public componentWillLoad() {
        const { handleMouseEnter, handleMouseLeave } = getMouseEventHandlers(
            this.host
        );
        this.handleMouseEnter = handleMouseEnter;
        this.handleMouseLeave = handleMouseLeave;
        this.updateHasPrimarySlotContent();
    }

    public render() {
        const extendedAriaLabel =
            this.checkProps(this?.prefix) +
            this.value +
            ' ' +
            this.checkProps(this?.suffix) +
            this.checkProps(this?.label) +
            '. ' +
            this.checkProps(this?.progress?.prefix) +
            this.checkProps(this?.progress?.value) +
            this.checkProps(this?.progress?.suffix) +
            this.checkProps(this?.link?.title);

        const link = this.disabled ? '#' : this.link?.href;
        const rel = getRel(this.link?.target, this.link?.rel);

        return (
            <Host
                onMouseEnter={this.handleMouseEnter}
                onMouseLeave={this.handleMouseLeave}
                class={{ 'has-primary-slot-content': this.hasPrimarySlot }}
            >
                <a
                    title={this.link?.title}
                    href={link}
                    target={this.link?.target}
                    rel={rel}
                    tabindex="0"
                    aria-label={extendedAriaLabel}
                    aria-disabled={this.disabled}
                    aria-busy={this.loading ? 'true' : 'false'}
                    aria-live="polite"
                    class={{
                        'is-clickable': !!this.link?.href && !this.disabled,
                    }}
                >
                    {this.renderIcon()}
                    {this.renderProgress()}
                    <slot
                        name="primary"
                        onSlotchange={this.updateHasPrimarySlotContent}
                    />
                    <div class="value-group">
                        {this.renderPrefix()}
                        <div class="value-and-suffix">
                            {this.renderValue()}
                            {this.renderSuffix()}
                        </div>
                        {this.renderSpinner()}
                    </div>
                    {this.renderLabel()}
                    <limel-3d-hover-effect-glow />
                </a>
                {this.renderNotification()}
            </Host>
        );
    }

    private checkProps(propValue) {
        return propValue ? propValue + ' ' : '';
    }

    private renderPrefix = () => {
        if (this.prefix) {
            return <span class="prefix">{this.prefix}</span>;
        }
    };

    private renderValue = () => {
        const characterCount = (this.value ?? '').toString().length;

        if (!this.value && this.loading) {
            return <span class="value">Â·Â·Â·</span>;
        }

        if (this.value) {
            return (
                <span
                    class={{
                        value: true,
                        [`ch-${characterCount}`]: true,
                    }}
                >
                    {this.value}
                </span>
            );
        }
    };

    private renderSuffix = () => {
        if (this.suffix) {
            return <span class="suffix">{this.suffix}</span>;
        }
    };

    private renderIcon = () => {
        if (this.icon) {
            return <limel-icon class="icon" name={this.icon} />;
        }
    };

    private updateHasPrimarySlotContent = (e?: Event) => {
        const slot =
            (e?.target as HTMLSlotElement) ??
            this.host.shadowRoot.querySelector('slot[name="primary"]');
        this.hasPrimarySlot = slot && slot.assignedElements().length > 0;
    };

    private renderProgress = () => {
        if (this.hasPrimarySlot) {
            return;
        }

        if (!this.progress?.value && this.progress?.value !== 0) {
            return;
        }

        return (
            <limel-circular-progress
                class="progress"
                prefix={this.progress.prefix}
                value={this.progress.value}
                suffix={this.progress.suffix}
                maxValue={this.progress.maxValue}
                displayPercentageColors={this.progress.displayPercentageColors}
            />
        );
    };

    private renderLabel = () => {
        if (this.label) {
            return <span class="label">{this.label}</span>;
        }
    };

    private renderNotification = () => {
        if (this.badge) {
            return <limel-badge label={this.badge} />;
        }
    };

    private renderSpinner = () => {
        if (this.loading) {
            return <limel-linear-progress indeterminate={true} />;
        }
    };
}

/**
* @prop --info-tile-border-radius: defines the radius of corners of the info-tile. Defaults to `1rem`
* @prop --info-tile-icon-color: defines the fill color of the info-tile icon. Defaults to `--contrast-1000`
* @prop --info-tile-text-color: defines the color of the info-tile label. Defaults to `--contrast-1100`
* @prop --info-tile-background-color: defines the backgrounds color of the info-tile icon. Defaults to `--contrast-100`
* @prop --info-tile-badge-text-color: Text color of the notification badge. Defaults to `--color-white`
* @prop --info-tile-badge-background-color: Background color of the notification badge. Defaults to `--color-red-default`
* @prop --info-tile-progress-fill-color: Determines the color of the progressed section. Defaults to `--lime-primary-color`.
* @prop --info-tile-progress-background-color: Determines the background color of the central section of the progress bar. Defaults to `--info-tile-background-color`.
* @prop --info-tile-progress-suffix-color: Determines the color of the progress prefix. Defaults to `--contrast-1000`.
 * @prop --info-tile-progress-text-color: Determines the color of the progress value. Defaults to `--info-tile-text-color`.
 * @prop --info-tile-progress-prefix-color: Determines the color of the progress suffix. Defaults to `--contrast-1000`.
*/

@use '../../style/mixins';

:host(limel-info-tile) {
    --badge-text-color: var(
        --info-tile-badge-text-color,
        rgb(var(--color-white))
    );
    --badge-background-color: var(
        --info-tile-badge-background-color,
        rgb(var(--color-red-default))
    );

    --circular-progress-text-color: var(
        --info-tile-progress-text-color,
        var(--info-tile-text-color)
    );
    --circular-progress-suffix-color: var(--info-tile-progress-suffix-color);
    --circular-progress-prefix-color: var(--info-tile-progress-prefix-color);
    --circular-progress-track-color: rgb(var(--contrast-800), 0.3);
    --circular-progress-fill-color: var(--info-tile-progress-fill-color);
    --circular-progress-background-color: var(
        --info-tile-progress-background-color,
        var(--info-tile-background-color)
    );

    --label-min-size: 0.75rem;
    --label-preferred-size: 6cqw;
    --label-max-size: 1rem;

    --value-min-size: 1rem;
    --value-preferred-size: 20cqw;
    --value-max-size: 4rem;

    --suffix-prefix-min-size: 0.75rem;
    --suffix-prefix-preferred-size: 8cqw;
    --suffix-prefix-max-size: 1.5rem;

    --icon-min-size: 2rem;
    --icon-preferred-size: 60cqh;
    --icon-max-size: calc(100cqw - 0.5rem);

    container-type: size;
    position: relative;
    display: flex;

    width: 100%;
    height: 100%;

    * {
        box-sizing: border-box;
    }
}

:host(limel-info-tile[disabled]) {
    a {
        opacity: 0.5;
        cursor: not-allowed;
    }
}

a {
    all: unset;
    overflow: hidden;

    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    align-items: flex-start;

    height: 100%;
    width: 100%;
    flex-grow: 1;

    padding: 0.25rem 1rem 0.5rem 1rem;
    border-radius: var(--info-tile-border-radius, 1rem);
    background-color: var(
        --info-tile-background-color,
        var(--lime-elevated-surface-background-color)
    );
}

:host ::slotted([slot='primary']) {
    width: 100%;
    height: 100%;
}

.icon {
    z-index: 1;
    position: absolute;
    top: 0.5rem;
    right: 0.75rem;
    padding: 0.25rem;

    aspect-ratio: 1/1;

    color: var(--info-tile-icon-color, rgb(var(--contrast-1000)));

    border-radius: 0;
    height: clamp(
        var(--icon-min-size),
        var(--icon-preferred-size),
        var(--icon-max-size)
    );

    @supports not (container-type: size) {
        width: max(10%, 3rem);
    }

    :host(.has-primary-slot-content) &,
    a:has(limel-circular-progress) & {
        top: unset;
        bottom: 0.5rem;
        --icon-min-size: 1.5rem;
        --icon-preferred-size: 20cqh;
    }
}

slot[name='primary'] {
    display: block;
    aspect-ratio: 1;
    width: min(var(--icon-preferred-size), var(--icon-max-size));
}

.progress,
slot[name='primary'] {
    position: absolute;
    top: 0.75rem;
    right: 0.75rem;
}

.progress {
    --circular-progress-size: min(
        var(--icon-preferred-size),
        var(--icon-max-size)
    );
    @supports not (container-type: size) {
        --circular-progress-size: initial;
    }
}

.label {
    z-index: 1;
    color: var(--info-tile-text-color, rgb(var(--contrast-1100)));

    line-height: 1.2;
    font-size: clamp(
        var(--label-min-size),
        var(--label-preferred-size),
        var(--label-max-size)
    );
    @supports not (container-type: size) {
        font-size: 0.875rem;
    }
}

limel-badge {
    position: absolute;
    top: -0.25rem;
    right: -0.25rem;
}

limel-linear-progress {
    --lime-primary-color: var(--info-tile-text-color);
    position: absolute;
    inset: auto 0 0 0;
}

.value-group {
    position: relative;
    z-index: 1;
    display: flex;
    flex-direction: column;
    color: var(--info-tile-text-color, rgb(var(--contrast-1100)));
}

.value-and-suffix,
.label {
    text-shadow:
        0 0 0.5rem var(--info-tile-background-color, rgb(var(--contrast-100))),
        0 0 0.25rem var(--info-tile-background-color, rgb(var(--contrast-100)));
}

.value-and-suffix {
    display: flex;
}

.prefix,
.suffix {
    font-size: clamp(
        var(--suffix-prefix-min-size),
        var(--suffix-prefix-preferred-size),
        var(--suffix-prefix-max-size)
    );
    opacity: 0.7;

    @supports not (container-type: size) {
        font-size: 0.75rem;
    }
}

.prefix {
    align-self: flex-start;
    line-height: normal;
    transform: translateY(40%);
}

.value {
    transition:
        opacity 0.2s ease,
        transform 0.2s cubic-bezier(0.25, 0.46, 0.45, 0.95);
    transform-origin: left;
    transform: translate3d(0, 0, 0) scale(1);

    @include mixins.truncate-text;
    font-weight: bold;
    line-height: normal;

    font-size: clamp(
        var(--value-min-size),
        var(--value-preferred-size),
        var(--value-max-size)
    );
    @supports not (container-type: size) {
        font-size: 1.5rem;
    }

    :host(limel-info-tile[loading]) & {
        opacity: 0.3;
        transform: translate3d(0, 0, 0) scale(0.9);
    }
}

.suffix {
    transform: translateY(10%);
}

$xs: 8rem; //128px:
$s: 18.75rem; //300px
$m: 40.5rem; //648px
$l: 62.5rem; //1000px

@container (width < #{$xs}) {
    .progress,
    slot[name='primary'] {
        top: 0.25rem;
        right: 0.25rem;
    }
    a {
        padding: 0.375rem;
        gap: 0.125rem;
    }
}

@container (width < #{$s}) {
    .progress,
    slot[name='primary'] {
        top: 0.5rem;
        right: 0.5rem;
    }
    .icon {
        top: 0.25rem;
        right: 0.5rem;

        :host(.has-primary-slot-content) &,
        a:has(limel-circular-progress) & {
            right: 0.25rem;
            bottom: 0.25rem;
        }
    }
}

@container (width < #{$m}) {
    .value {
        &.ch-1,
        &.ch-2,
        &.ch-3,
        &.ch-4 {
            --value-preferred-size: 20cqw;
        }
        &.ch-5 {
            --value-preferred-size: 18cqw;
        }
        &.ch-6 {
            --value-preferred-size: 17cqw;
        }
        &.ch-7 {
            --value-preferred-size: 16cqw;
        }
        &.ch-8 {
            --value-preferred-size: 15cqw;
        }
        &.ch-9 {
            --value-preferred-size: 14cqw;
        }
        --value-preferred-size: 13cqw;
    }
}

@container (height > #{$xs}) {
    a {
        padding-top: 0.75rem;
        padding-bottom: 1rem;
    }
}

@container (height < #{$xs}) and (width > #{$xs}) {
    .value {
        --value-preferred-size: 32cqh !important;
    }
    .suffix,
    .prefix {
        --suffix-prefix-preferred-size: 16cqh !important;
    }
}

@container (height > #{$s}) {
    .progress,
    slot[name='primary'],
    .icon {
        position: relative;
        top: unset;
        right: unset;
    }
    a {
        align-items: center;
        justify-content: center;
    }
    .label {
        text-align: center;
        // -webkit-line-clamp: 3;
    }

    :host(.has-primary-slot-content),
    a:has(limel-circular-progress) {
        .icon {
            position: absolute;
            top: 0.5rem;
            right: 0.5rem;
            --icon-max-size: 3rem;
        }
    }
}

// The 3D effect
limel-3d-hover-effect-glow {
    border-radius: var(--info-tile-border-radius, 1rem);
}

:host(limel-info-tile) {
    @include mixins.parent-of-the-3d-element;
}

a {
    @include mixins.the-3d-element;

    &.is-clickable {
        @include mixins.the-3d-element--clickable;
    }
}

import { MDCTextField } from '@material/textfield';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { debounce } from 'lodash-es';
import {
    ARROW_DOWN,
    ARROW_UP,
    ENTER,
    ESCAPE,
    SPACE,
    TAB,
} from '../../util/keycodes';
import { InputType } from '../input-field/input-field.types';
import { ListItem } from '../list/list-item.types';
import { getHref, getTarget, getRel } from '../../util/link-helper';
import { JSXBase } from '@stencil/core/internal';
import { createRandomString } from '../../util/random-string';
import { LimelListCustomEvent } from '../../components';
import { globalConfig } from '../../global/config';

interface LinkProperties {
    href: string;
    target?: string;
    rel?: string;
}

const CHANGE_EVENT_DEBOUNCE_TIMEOUT = 300;
const RESIZE_HANDLER_DEBOUNCE_TIMEOUT = 100;

/**
 * @exampleComponent limel-example-input-field-text
 * @exampleComponent limel-example-input-field-placeholder
 * @exampleComponent limel-example-input-field-text-multiple
 * @exampleComponent limel-example-input-field-number
 * @exampleComponent limel-example-input-field-autocomplete
 * @exampleComponent limel-example-input-field-icon-leading
 * @exampleComponent limel-example-input-field-icon-trailing
 * @exampleComponent limel-example-input-field-icon-both
 * @exampleComponent limel-example-input-field-showlink
 * @exampleComponent limel-example-input-field-error-icon
 * @exampleComponent limel-example-input-field-textarea
 * @exampleComponent limel-example-input-field-suffix
 * @exampleComponent limel-example-input-field-prefix
 * @exampleComponent limel-example-input-field-search
 * @exampleComponent limel-example-input-field-pattern
 * @exampleComponent limel-example-input-field-focus
 */
@Component({
    tag: 'limel-input-field',
    shadow: true,
    styleUrl: 'input-field.scss',
})
export class InputField {
    /**
     * Set to `true` to disable the field.
     * Use `disabled` to indicate that the field can normally be interacted
     * with, but is currently disabled. This tells the user that if certain
     * requirements are met, the field may become enabled again.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Set to `true` to make the field read-only.
     * Use `readonly` when the field is only there to present the data it holds,
     * and will not become possible for the current user to edit.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to indicate that the current value of the input field is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * The input label.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * The placeholder text shown inside the input field, when the field is focused and empty.
     */
    @Prop({ reflect: true })
    public placeholder: string;

    /**
     * Optional helper text to display below the input field when it has focus
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * A short piece of text to display before the value inside the input field.
     * Displayed for all types except `textarea`.
     */
    @Prop({ reflect: true })
    public prefix: string;

    /**
     * A short piece of text to display after the value inside the input field.
     * Displayed for all types except `textarea`.
     */
    @Prop({ reflect: true })
    public suffix: string;

    /**
     * Set to `true` to indicate that the field is required.
     */
    @Prop({ reflect: true })
    public required = false;

    /**
     * The value of the field.
     */
    @Prop({ reflect: true })
    public value: string;

    /**
     * Trailing icon to show to the far right in the field.
     */
    @Prop({ reflect: true })
    public trailingIcon: string;

    /**
     * Leading icon to show to the far left in the field.
     */
    @Prop({ reflect: true })
    public leadingIcon: string;

    /**
     * Regular expression that the current value of the input field must match.
     * No forward slashes should be specified around the pattern.
     * Only used if type is `text`, `tel`, `email`, `url`, `urlAsText`,
     * `password`, or `search`.
     */
    @Prop({ reflect: true })
    public pattern: string;

    /**
     * Type of input.
     *
     * Note** regarding type `url`: `limel-input` uses the native validation
     * built into the browser for many types of input fields. The native
     * validation for `url` is very strict, and does not allow relative urls,
     * nor any other formats that are not a "fully qualified" url. To allow
     * such urls, use the type `urlAsText` instead. `urlAsText` works exactly
     * like `text` in all regards, except that it enables use of the `showLink`
     * property.
     */
    @Prop({ reflect: true })
    public type: InputType = 'text';

    /**
     * Set to `true` to format the current value of the input field only
     * if the field is of type number.
     * The number format is determined by the current language of the browser.
     */
    @Prop({ reflect: true })
    public formatNumber = true;

    /**
     * Incremental values that are valid if the field type is `number`.
     */
    @Prop({ reflect: true })
    public step: number | 'any' = 'any';

    /**
     * Maximum allowed value if input type is `number`.
     */
    @Prop({ reflect: true })
    public max: number;

    /**
     * Minimum allowed value if input type is `number`.
     */
    @Prop({ reflect: true })
    public min: number;

    /**
     * Maximum length of the value if type is `password`, `search`, `tel`,
     * `text`, `url`, or `urlAsText`.
     */
    @Prop({ reflect: true })
    public maxlength: number;

    /**
     * Minimum length of the value if type is `password`, `search`, `tel`,
     * `text`, `url`, or `urlAsText`.
     */
    @Prop({ reflect: true })
    public minlength: number;

    /**
     * list of suggestions `value` can autocomplete to.
     */
    @Prop()
    public completions: string[] = [];

    /**
     * For inputs of type `email`, `tel`, `url`, and `urlAsText`, set this to
     * `true` to show a trailing icon with a `mailto:`,`tel:`, or normal link,
     * respectively. The default icon can be overridden using the `trailingIcon`
     * property.
     */
    @Prop({ reflect: true })
    public showLink = false;

    /**
     * The locale to use for formatting numbers.
     */
    @Prop({ reflect: true })
    public locale: string = globalConfig.defaultLocale;

    /**
     * Emitted when the input value is changed.
     */
    @Event()
    private change: EventEmitter<string>;

    /**
     * Emitted when `trailingIcon` or `leadingIcon` is set
     * and the icon is interacted with.
     */
    @Event()
    private action: EventEmitter<void>;

    @Element()
    private limelInputField: HTMLLimelInputFieldElement;

    @State()
    private isFocused: boolean = false;

    @State()
    private wasInvalid: boolean = false;

    @State()
    public showCompletions: boolean = false;

    private inputElement?: HTMLInputElement | HTMLTextAreaElement;
    private mdcTextField: MDCTextField;
    private completionsList: ListItem[] = [];
    private portalId: string;
    private helperTextId: string;
    private labelId: string;

    private changeWaiting = false;

    constructor() {
        this.portalId = createRandomString();
        this.helperTextId = createRandomString();
        this.labelId = createRandomString();
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    public disconnectedCallback() {
        if (this.mdcTextField) {
            this.mdcTextField.destroy();
        }

        this.restyleCompletionsDropDown.cancel();
        window.removeEventListener('resize', this.layout);
        this.limelInputField.removeEventListener('focus', this.setFocus);
    }

    public componentDidUpdate() {
        if (this.invalid) {
            this.mdcTextField.valid = false;
        }

        this.mdcTextField.disabled = this.disabled || this.readonly;
    }

    public render() {
        const properties = this.getAdditionalProps();
        properties['aria-labelledby'] = this.labelId;
        properties.class = 'mdc-text-field__input';
        properties.ref = this.setInputElement;
        properties.onInput = this.handleInput;
        properties.onChange = this.handleChange;
        properties.onFocus = this.onFocus;
        properties.onBlur = this.onBlur;
        properties.required = this.required;
        properties.readonly = this.readonly;
        properties.disabled = this.disabled || this.readonly;

        let ariaControls = '';

        if (this.hasHelperText()) {
            ariaControls += this.helperTextId;
            properties['aria-describedby'] = this.helperTextId;
        }

        if (this.renderAutocompleteList()) {
            if (ariaControls) {
                ariaControls += ' ';
            }

            ariaControls += this.portalId;
        }

        if (ariaControls) {
            properties['aria-controls'] = ariaControls;
        }

        return [
            <limel-notched-outline
                labelId={this.labelId}
                label={this.label}
                required={this.required}
                invalid={this.invalid || this.isInvalid()}
                disabled={this.disabled}
                readonly={this.readonly}
                hasValue={!!this.value}
                hasLeadingIcon={!!this.leadingIcon}
            >
                <label slot="content" class={this.getContainerClassList()}>
                    {this.renderLeadingIcon()}
                    {this.renderPrefix()}
                    {this.renderFormattedNumber()}
                    {this.renderInput(properties)}
                    {this.renderSuffix()}
                    {this.renderTextarea(properties)}
                    {this.renderTrailingLinkOrButton()}
                </label>
            </limel-notched-outline>,
            this.renderHelperLine(),
            this.renderAutocompleteList(),
        ];
    }

    @Watch('value')
    protected valueWatcher(newValue: string) {
        if (!this.mdcTextField) {
            return;
        }

        if (this.changeWaiting) {
            return;
        }

        if (
            this.type === 'number' &&
            this.isFocused &&
            Number(newValue) === Number(this.mdcTextField.value)
        ) {
            return;
        }

        if (newValue !== this.mdcTextField.value) {
            this.mdcTextField.value = newValue || '';
        }

        if (this.wasInvalid) {
            this.validate();
        }
    }

    @Watch('completions')
    protected completionsWatcher() {
        this.mapCompletions();
    }

    private initialize = () => {
        const element =
            this.limelInputField.shadowRoot.querySelector('.mdc-text-field');
        if (!element) {
            return;
        }

        this.mdcTextField = new MDCTextField(element);
        if (this.value) {
            this.mdcTextField.value = this.value;
        }

        if (this.invalid) {
            this.mdcTextField.valid = false;
        }

        this.mapCompletions();

        window.addEventListener('resize', this.layout, { passive: true });
        this.limelInputField.addEventListener('focus', this.setFocus);
    };

    private mapCompletions = () => {
        this.completionsList = [...this.completions].map((item) => {
            return { text: item };
        });
    };

    private setFocus = () => {
        this.mdcTextField.focus();
    };

    private getContainerClassList = () => {
        const classList = {
            'mdc-text-field': true,
            'mdc-text-field--outlined': true,
            'mdc-text-field--invalid': this.isInvalid(),
            'mdc-text-field--disabled': this.disabled || this.readonly,
            'lime-text-field--readonly': this.readonly,
            'mdc-text-field--required': this.required,
            'lime-text-field--empty': this.isEmpty(),
            'lime-has-prefix': this.hasPrefix(),
            'lime-has-suffix': this.hasSuffix(),
        };

        if (this.type === 'textarea') {
            classList['mdc-text-field--textarea'] = true;
        } else {
            classList['mdc-text-field--with-leading-icon'] = !!this.leadingIcon;
            classList['mdc-text-field--with-trailing-icon'] =
                !!this.getTrailingIcon();
        }

        return classList;
    };

    private isEmpty = () => {
        if (this.type === 'number' && this.inputElement?.validity.badInput) {
            return false;
        }

        return !this.getCurrentValue();
    };

    private getCurrentValue = () => {
        if (this.changeWaiting && this.inputElement) {
            return this.inputElement.value;
        }

        return this.value;
    };

    private renderInput = (
        properties: JSXBase.InputHTMLAttributes<HTMLInputElement>
    ) => {
        if (this.type === 'textarea') {
            return;
        }

        const type = this.type === 'urlAsText' ? 'text' : this.type;

        return (
            <input
                {...properties}
                type={type}
                pattern={this.pattern}
                onWheel={this.handleWheel}
                onKeyDown={this.onKeyDown}
                placeholder={this.placeholder}
            />
        );
    };

    private renderTextarea = (
        properties: JSXBase.TextareaHTMLAttributes<HTMLTextAreaElement>
    ) => {
        if (this.type !== 'textarea') {
            return;
        }

        return (
            <span class="mdc-text-field__resizer">
                <textarea
                    {...properties}
                    placeholder={this.placeholder}
                ></textarea>
            </span>
        );
    };

    private layout = () => {
        this.mdcTextField?.layout();
        this.restyleCompletionsDropDown();
    };

    private restyleCompletionsDropDown = debounce(() => {
        const stateOfShowCompletions = this.showCompletions;
        this.showCompletions = false;
        requestAnimationFrame(() => {
            this.showCompletions = stateOfShowCompletions;
        });
    }, RESIZE_HANDLER_DEBOUNCE_TIMEOUT);

    private getAdditionalProps = () => {
        const props: any = {};

        if (this.type === 'number') {
            props.step = this.step;
        }

        if (this.type === 'number' && Number.isInteger(this.min)) {
            props.min = this.min;
        }

        if (this.type === 'number' && Number.isInteger(this.max)) {
            props.max = this.max;
        }

        if (this.minlength) {
            props.minlength = this.minlength;
        }

        if (this.maxlength) {
            props.maxlength = this.maxlength;
        }

        return props;
    };

    private onFocus = () => {
        this.isFocused = true;
        this.showCompletions = true;
    };

    private onBlur = () => {
        this.isFocused = false;
        this.validate();
        this.changeEmitter.flush();
    };

    private get validationMessage(): string {
        if (this.isInvalid() && !this.invalid) {
            return this.inputElement?.validationMessage || '';
        }

        return '';
    }

    private hasHelperText = () => {
        return !!(this.helperText ?? this.validationMessage);
    };

    private hasHelperLine = () => {
        return this.maxlength > 0 || this.hasHelperText();
    };

    private renderHelperLine = () => {
        const text: string = this.getCurrentValue() || '';
        const length = text.length;

        if (!this.hasHelperLine()) {
            return;
        }

        return (
            <limel-helper-line
                helperTextId={this.helperTextId}
                helperText={this.helperText ?? this.validationMessage}
                length={length}
                maxLength={this.maxlength}
                invalid={this.isInvalid()}
            />
        );
    };

    private renderSuffix = () => {
        if (!this.hasSuffix() || this.type === 'textarea') {
            return;
        }

        const classList = {
            'mdc-text-field__affix': true,
            'mdc-text-field__affix--suffix': true,
        };

        return <span class={classList}>{this.suffix}</span>;
    };

    private hasSuffix = () => {
        return this.suffix !== null && this.suffix !== undefined;
    };

    private renderPrefix = () => {
        if (!this.hasPrefix() || this.type === 'textarea') {
            return;
        }

        const classList = {
            'mdc-text-field__affix': true,
            'mdc-text-field__affix--prefix': true,
        };

        return <span class={classList}>{this.prefix}</span>;
    };

    private hasPrefix = () => {
        return this.prefix !== null && this.prefix !== undefined;
    };

    private isInvalid = () => {
        if (this.readonly) {
            // A readonly field can never be invalid.
            return false;
        }

        if (this.invalid) {
            // `this.invalid` is set by the consumer. If the consumer explicitly
            // told us to consider the field invalid, we consider it invalid
            // regardless of what our internal validation thinks, and regardless
            // of whether the field has been modified.
            return true;
        }

        return this.wasInvalid;
    };

    private validate = () => {
        if (this.readonly || this.invalid) {
            this.wasInvalid = false;

            return;
        }

        if (this.inputElement) {
            this.wasInvalid = !this.inputElement.checkValidity();
        }
    };

    private setInputElement = (
        element?: HTMLInputElement | HTMLTextAreaElement
    ) => {
        if (element) {
            this.inputElement = element;
        }
    };

    private renderLeadingIcon = () => {
        if (this.type === 'textarea') {
            return;
        }

        if (this.leadingIcon) {
            return (
                <i class="material-icons mdc-text-field__icon mdc-text-field__icon--leading">
                    <limel-icon name={this.leadingIcon} />
                </i>
            );
        }
    };

    private renderTrailingLinkOrButton = () => {
        if (this.type === 'textarea') {
            return;
        }

        const trailingIcon = this.getTrailingIcon();

        if (!this.isInvalid() && this.hasLink()) {
            return this.renderLinkIcon(this.getLink(), trailingIcon);
        } else if (trailingIcon) {
            return this.renderTrailingIcon(trailingIcon);
        }
    };

    private hasLink = () => {
        return (
            this.showLink &&
            ['email', 'tel', 'url', 'urlAsText'].includes(this.type)
        );
    };

    private getLink = () => {
        const props: LinkProperties = { href: '' };
        switch (this.type) {
            case 'email': {
                props.href = `mailto:${this.value}`;
                break;
            }
            case 'tel': {
                props.href = `tel:${this.value}`;
                break;
            }
            default: {
                props.href = getHref(this.value);
                props.target = getTarget(this.value);
                props.rel = getRel(props.target);
            }
        }

        return props;
    };

    private renderLinkIcon = (linkProps: LinkProperties, icon: string) => {
        // If the trailing icon uses the class `mdc-text-field__icon--trailing`,
        // MDC attaches a click handler to it, which apparently runs
        // `preventDefault()` on the event. For links, we don't want that,
        // so instead of `mdc-text-field__icon--trailing`, we use our own class
        // `lime-trailing-icon-for-link`, which uses all the same styling. /Ads
        return (
            <a
                {...linkProps}
                class="material-icons mdc-text-field__icon lime-trailing-icon-for-link"
                tabindex={this.disabled || this.isEmpty() ? '-1' : '0'}
                role="button"
            >
                <limel-icon name={icon} />
            </a>
        );
    };

    private renderTrailingIcon = (icon: string) => {
        if (this.isInvalid()) {
            return (
                <i
                    key="invalid"
                    class="material-icons mdc-text-field__icon invalid-icon"
                >
                    <limel-icon name={icon} />
                </i>
            );
        }

        return (
            <i
                key="action"
                class="material-icons mdc-text-field__icon mdc-text-field__icon--trailing"
                tabIndex={0}
                role="button"
                onKeyDown={this.handleIconKeyPress}
                onClick={this.handleIconClick}
            >
                <limel-icon name={icon} />
            </i>
        );
    };

    private getTrailingIcon = () => {
        if (this.isInvalid()) {
            return 'high_importance';
        }

        if (this.trailingIcon) {
            return this.trailingIcon;
        }

        if (this.showLink && this.type === 'email') {
            return 'filled_message';
        }

        if (this.showLink && this.type === 'tel') {
            return 'phone';
        }

        if (
            this.showLink &&
            (this.type === 'url' || this.type === 'urlAsText')
        ) {
            return 'external_link';
        }
    };

    private renderFormattedNumber = () => {
        if (this.type !== 'number') {
            return;
        }

        let renderValue = this.value;
        if (this.formatNumber && this.value) {
            renderValue = new Intl.NumberFormat(this.locale).format(
                Number(this.value)
            );
            if (renderValue === 'NaN') {
                return;
            }
        }

        return (
            <span class="lime-formatted-input lime-looks-like-input-value">
                {renderValue}
            </span>
        );
    };

    /**
     * Key handler for the input field
     * Will change focus to the first/last item in the dropdown list to enable selection with the keyboard
     *
     * @param event - event
     */

    private onKeyDown = (event: KeyboardEvent): void => {
        this.showCompletions = true;
        const isForwardTab =
            event.key === TAB &&
            !event.altKey &&
            !event.metaKey &&
            !event.shiftKey;
        const isUp = event.key === ARROW_UP;
        const isDown = event.key === ARROW_DOWN;

        if (event.key === TAB && event.shiftKey) {
            this.showCompletions = false;
        }

        if (!isForwardTab && !isUp && !isDown) {
            return;
        }

        const list = document.querySelector(` #${this.portalId} limel-list`);

        if (!list) {
            return;
        }

        event.preventDefault();
        if (isForwardTab || isDown) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-deprecated-list-item:first-child'
            );
            listElement.focus();

            return;
        }

        if (isUp) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-deprecated-list-item:last-child'
            );
            listElement.focus();
        }
    };

    private handleCompletionChange = (
        event: LimelListCustomEvent<ListItem>
    ) => {
        event.stopPropagation();
        if (!event.detail) {
            return;
        }

        this.showCompletions = false;

        /*
         This change event doesn't need to be debounced in itself, but we want
         to make absolutely sure that an earlier change event that *has* been
         debounced doesn't emit after this one. Therefore, we run this through
         the same debounced emitter function. /Ads
         */
        this.changeEmitter(event.detail.text);
        this.changeEmitter.flush();
    };

    private renderAutocompleteList = () => {
        if (this.type === 'textarea' || this.completions.length === 0) {
            return;
        }

        const dropdownZIndex = getComputedStyle(
            this.limelInputField
        ).getPropertyValue('--dropdown-z-index');

        return (
            <limel-portal
                visible={this.showCompletions}
                containerId={this.portalId}
                inheritParentWidth={true}
                containerStyle={{ 'z-index': dropdownZIndex }}
            >
                <limel-menu-surface
                    open={this.showCompletions}
                    allowClicksElement={this.limelInputField}
                    style={{
                        '--menu-surface-width': '100%',
                        'max-height': 'inherit',
                        display: 'flex',
                    }}
                    onDismiss={this.handleCloseMenu}
                >
                    {this.renderListResult()}
                </limel-menu-surface>
            </limel-portal>
        );
    };

    private renderListResult = () => {
        const filteredCompletions: ListItem[] = this.filterCompletions(
            this.getCurrentValue()
        );
        if (!filteredCompletions || filteredCompletions.length === 0) {
            return null;
        }

        return (
            <limel-list
                onChange={this.handleCompletionChange}
                onKeyDown={this.handleKeyDownInDropdown}
                type="selectable"
                items={filteredCompletions}
            />
        );
    };

    private handleKeyDownInDropdown = (event: KeyboardEvent) => {
        const keyFound = [TAB, ESCAPE, ENTER].includes(event.key);
        if (keyFound) {
            this.setFocus();
        }
    };

    private handleCloseMenu = () => {
        this.showCompletions = false;
    };

    private filterCompletions = (filter: string) => {
        if (!filter) {
            return this.completionsList;
        }

        return this.completionsList.filter((completion) =>
            completion.text.toLowerCase().includes(filter.toLowerCase())
        );
    };

    private handleInput = (event) => {
        event.stopPropagation();
        let value = event.target.value;

        if (this.type === 'number') {
            if (!value && event.data) {
                event.stopPropagation();

                return;
            }

            if (value) {
                value = Number(value);
            }
        }

        this.changeWaiting = true;
        this.changeEmitter(value);
    };

    private changeEmitter = debounce((value: string) => {
        this.change.emit(value);
        this.changeWaiting = false;
    }, CHANGE_EVENT_DEBOUNCE_TIMEOUT);

    private handleChange = (event: Event) => {
        event.stopPropagation();
        this.changeEmitter.flush();
    };

    private handleIconClick = () => {
        this.action.emit();
    };

    private handleIconKeyPress = (event: KeyboardEvent) => {
        const isEnter = event.key === ENTER;
        const isSpace = event.key === SPACE;

        if (isSpace || isEnter) {
            this.action.emit();
        }
    };

    private handleWheel = () => {
        // This empty event handler is here to circumvent a bug.
        // In some browsers (Chrome for example), hovering the input with
        // the input focused, and scrolling, will both change the value
        // AND scroll the page. We would prefer to never change the value
        // on scroll, instead always scrolling the page, but since we
        // haven't found a way to do that, this is the next best thing, as
        // it prevents the page from being scrolled, but only in the
        // circumstances when the value is changed by the scrolling.
        // Please test THOROUGHLY if you remove this event handler ðŸ˜„
    };
}

@use '../../style/internal/z-index';
@use '../../style/functions';
@use '../../style/internal/shared_input-select-picker';
@use '../../style/mixins';

@use '@material/textfield';
@use '@material/textfield/icon';
@use '@material/list';
@use '@material/elevation';
@use '@material/menu-surface';

/**
 * @prop --textarea-height: Height of the field when type is set to `textarea`
 */

:host(limel-input-field) {
    position: relative;
    display: flex;
    flex-direction: column;
}

:host([hidden]) {
    display: none;
}

:host([type='textarea']) {
    height: var(--textarea-height, 100%);

    limel-notched-outline {
        flex-grow: 1;
    }

    .mdc-text-field.mdc-text-field--textarea {
        height: var(--textarea-height, 100%);

        .mdc-text-field__input {
            @include shared_input-select-picker.input-field-placeholder;
            margin-top: functions.pxToRem(8);
            margin-bottom: 0;
        }
    }
}

@include mixins.hide-helper-line-when-not-needed(limel-input-field);

@include textfield.core-styles;
@include icon.icon-core-styles;

@include shared_input-select-picker.readonly-overrides;
@include shared_input-select-picker.leading-icon;
@include shared_input-select-picker.trailing-icon;
@include shared_input-select-picker.lime-looks-like-input-value;

.lime-text-field--empty {
    .mdc-text-field__icon--trailing {
        @include shared_input-select-picker.looks-disabled;
        pointer-events: none;
        box-shadow: none !important;
    }
}

limel-notched-outline {
    &[has-value],
    &:focus-within {
        .mdc-text-field__affix {
            opacity: 1;
        }
    }
}

:host(limel-input-field:not([type='textarea'])) {
    .limel-notched-outline {
        // Needed only for Safari,
        // to ensure that the outlines are not rendered
        // larger than the input element.
        height: shared_input-select-picker.$height-of-mdc-text-field;
    }
}

.mdc-text-field {
    width: 100%;

    &:not(.mdc-text-field--disabled) {
        .mdc-text-field__affix,
        .mdc-text-field__icon {
            color: rgb(var(--contrast-900));
        }
    }
    &.mdc-text-field--disabled {
        .mdc-text-field__affix,
        .mdc-text-field__icon {
            color: rgb(var(--contrast-700));
        }
    }
}

.lime-formatted-input {
    display: none;
    width: calc(100% - #{functions.pxToRem(20)});
    z-index: z-index.$input-field--formatted-value;

    pointer-events: none;

    .mdc-text-field:not(.mdc-text-field--focused):not(.mdc-text-field--invalid)
        & {
        display: block;
    }

    + .mdc-text-field__input {
        z-index: z-index.$input-field--input-with-formatted-value;
    }
}

.mdc-text-field:not(.mdc-text-field--focused):not(.mdc-text-field--invalid) {
    .mdc-text-field__input[type='number'] {
        color: transparent;
        -webkit-text-fill-color: transparent;
    }

    &.lime-text-field--readonly {
        .mdc-text-field__input[type='number'] {
            visibility: hidden;
            // Not having `visibility: hidden;` makes the unformatted
            // value selectable; which is not so nice when users
            // click drag to select content on the screen.
            // The reason to have it only on `readonly` mode is
            // 1. This is the use case where it makes most sense
            // 2. We don't want to repeat the same thing in the
            // accessibility tree, and make it readable for assistive
            // technologies.
            // 3. When not readonly, clicking on the `<input />`
            // element sets the focus and activates the edit mode.
            // So we cannot always have it `hidden` on.
            // which is why we have instead
            // `color: transparent` a few lines before this.
        }
    }
}

.autocomplete-list-container {
    position: relative;
}

.autocomplete-list {
    background-color: var(--lime-elevated-surface-background-color);
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    max-height: functions.pxToRem(200);
    overflow-y: auto;
}

.mdc-deprecated-list-item {
    cursor: pointer;
}

.mdc-text-field__input {
    .mdc-text-field:not(.mdc-text-field--disabled) & {
        color: shared_input-select-picker.$input-text-color;
    }
    .mdc-text-field.mdc-text-field--disabled & {
        color: shared_input-select-picker.$input-text-color-disabled;
    }
}

input.mdc-text-field__input {
    @include shared_input-select-picker.input-field-placeholder;

    &::-webkit-search-cancel-button {
        @include mixins.clear-all-button;

        -webkit-appearance: none;

        position: absolute;
        right: 0;
        top: 0;
        bottom: 0;
        margin: auto;

        &:active {
            transform: none; //Makes the "clear-all button" work
        }
        .mdc-text-field--label-floating & {
            opacity: 1;
        }
    }
}

@import './partial-styles/trailing-icon.scss';
@import './partial-styles/readonly';

// To make the input field render smaller than the default MDC UI
.mdc-text-field {
    &.mdc-text-field--outlined {
        height: shared_input-select-picker.$height-of-mdc-text-field;
    }
}
.mdc-text-field--with-trailing-icon {
    .mdc-text-field__icon:last-child {
        padding: 0.25rem;
        margin-right: 0.25rem;
    }
}
.mdc-text-field--outlined.mdc-text-field--with-leading-icon {
    .mdc-text-field__icon--leading {
        margin-left: 0.25rem;
    }
    .mdc-floating-label {
        left: 1.5rem;
    }
}

import { Component, Element, h, Host, Prop, Watch } from '@stencil/core';
import { Languages } from '../date-picker/date.types';
import translate from '../../global/translations';

const PERCENT = 100;

/**
 * The linear progress component can be used to visualize the current state of a progress in a scale;
 * for example percentage of completion of a task.
 *
 * @exampleComponent limel-example-linear-progress
 * @exampleComponent limel-example-linear-progress-indeterminate
 * @exampleComponent limel-example-linear-progress-accessible-label
 * @exampleComponent limel-example-linear-progress-color
 */
@Component({
    tag: 'limel-linear-progress',
    shadow: true,
    styleUrl: 'linear-progress.scss',
})
export class LinearProgress {
    /**
     * Defines the language for translations.
     * Will translate the translatable strings on the components.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * The value of the progress bar. Should be between `0` and `1`.
     */
    @Prop({ reflect: true })
    public value: number = 0;

    /**
     * Puts the progress bar in an indeterminate state
     */
    @Prop({ reflect: true })
    public indeterminate: boolean = false;

    /**
     * A label used to describe the purpose of the element to users
     * of assistive technologies, like screen readers.
     * If not provided, the generic word of "Progress bar" will be used.
     */
    @Prop({ reflect: true })
    public accessibleLabel?: string;

    @Element()
    private host: HTMLLimelLinearProgressElement;

    public render() {
        if (!this.isFinite(this.value)) {
            return;
        }

        const loadingText = translate.get('loading', this.language);
        const ariaLabel = translate.get('progress-bar', this.language);
        const ariaValueNow = this.indeterminate ? undefined : this.value;
        const ariaValueText = this.indeterminate ? loadingText : undefined;

        return (
            <Host
                role="progressbar"
                aria-label={this.accessibleLabel || ariaLabel}
                aria-live="polite"
                aria-valuemin="0"
                aria-valuemax="1"
                aria-valuenow={ariaValueNow}
                aria-valuetext={ariaValueText}
                style={{ '--percentage': `${this.value * PERCENT}%` }}
            >
                <div class="progress" />
            </Host>
        );
    }

    @Watch('value')
    protected watchValue(newValue: number) {
        if (!this.isFinite(newValue)) {
            return;
        }

        this.updateProgress(newValue);
    }

    private updateProgress(value: number): void {
        if (this.host) {
            this.host.style.setProperty('--percentage', `${value * PERCENT}%`);
        }
    }

    private isFinite(value: unknown) {
        return Number.isFinite(value);
    }
}

/**
 * @prop --background-color: Color to use for progress-bar track.
 */

:host(limel-linear-progress) {
    isolation: isolate;
    position: relative;
    overflow: hidden;

    display: flex;
    width: 100%;
    height: 0.25rem;
    border-radius: 0.25rem;

    background-color: var(--background-color, rgb(var(--contrast-800), 0.5));
}

.progress {
    height: 100%;
    width: var(--percentage, 0%);

    border-radius: 0.25rem;
    background-color: var(--mdc-theme-primary);
}

:host(limel-linear-progress[indeterminate]) {
    .progress {
        background-color: transparent;
        width: 100%;

        &:before,
        &:after {
            will-change: left, right, opacity;
            content: '';
            position: absolute;
            inset: 0;
            height: 100%;
            border-radius: 0.25rem;
            opacity: 0;
            background-color: var(--mdc-theme-primary);
        }

        &:before {
            animation: indeterminate 2.1s
                cubic-bezier(0.65, 0.815, 0.735, 0.395) infinite;
        }

        &:after {
            animation: indeterminate-short 2.1s
                cubic-bezier(0.165, 0.84, 0.44, 1) infinite 1.15s;
        }
    }
}

@keyframes indeterminate {
    0% {
        left: -35%;
        right: 100%;
        opacity: 0;
    }
    10%,
    80% {
        opacity: 1;
    }
    60% {
        left: 100%;
        right: -90%;
    }
    100% {
        left: 100%;
        right: -90%;
        opacity: 0;
    }
}

@keyframes indeterminate-short {
    0% {
        left: -200%;
        right: 100%;
        opacity: 0;
    }
    10%,
    80% {
        opacity: 1;
    }
    60% {
        left: 107%;
        right: -8%;
    }
    100% {
        left: 107%;
        right: -8%;
        opacity: 0;
    }
}

import { IconSize } from '../icon/icon.types';
import { ListItem, ListSeparator } from './list-item.types';
import { ListType } from './list.types';
import { MDCList, MDCListActionEvent } from '@material/list';
import { strings as listStrings } from '@material/list/constants';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Host,
    Prop,
    Watch,
} from '@stencil/core';
import { ListRenderer } from './list-renderer';
import { ListRendererConfig } from './list-renderer-config';

const { ACTION_EVENT } = listStrings;

/**
 * @exampleComponent limel-example-list
 * @exampleComponent limel-example-list-secondary
 * @exampleComponent limel-example-list-separator
 * @exampleComponent limel-example-list-selectable
 * @exampleComponent limel-example-list-icons
 * @exampleComponent limel-example-list-badge-icons
 * @exampleComponent limel-example-list-pictures
 * @exampleComponent limel-example-list-checkbox
 * @exampleComponent limel-example-list-checkbox-icons
 * @exampleComponent limel-example-list-radio-button
 * @exampleComponent limel-example-list-radio-button-icons
 * @exampleComponent limel-example-list-action
 * @exampleComponent limel-example-list-striped
 * @exampleComponent limel-example-list-badge-icons-with-multiple-lines
 * @exampleComponent limel-example-list-grid
 * @exampleComponent limel-example-list-primary-component
 */
@Component({
    tag: 'limel-list',
    shadow: { delegatesFocus: true },
    styleUrl: 'list.scss',
})
export class List {
    /**
     * List of items to display
     */
    @Prop()
    public items: Array<ListItem | ListSeparator>;

    /**
     * Set to `true` if the list should display larger icons with a background
     */
    @Prop()
    public badgeIcons: boolean;

    /**
     * Size of the icons in the list
     */
    @Prop()
    public iconSize: IconSize = 'small';

    /**
     * The type of the list, omit to get a regular list. Available types are:
     * `selectable`: regular list with single selection.
     * `radio`: radio button list with single selection.
     * `checkbox`: checkbox list with multiple selection.
     */
    @Prop()
    public type: ListType;

    /**
     * By default, lists will display 3 lines of text, and then truncate the rest.
     * Consumers can increase or decrease this number by specifying
     * `maxLinesSecondaryText`. If consumer enters zero or negative
     * numbers we default to 1; and if they type decimals we round up.
     */

    @Prop() maxLinesSecondaryText: number = 3;

    @Element()
    private element: HTMLLimelListElement;

    private config: ListRendererConfig;
    private listRenderer = new ListRenderer();
    private mdcList: MDCList;
    private multiple: boolean;
    private selectable: boolean;

    /**
     * Fired when a new value has been selected from the list.
     * Only fired if `type` is set to `selectable`, `radio` or `checkbox`.
     */
    @Event()
    private change: EventEmitter<ListItem | ListItem[]>;

    /**
     * Fired when an action has been selected from the action menu of a list item
     */
    @Event()
    protected select: EventEmitter<ListItem | ListItem[]>;

    /**
     * Fires when a user interacts with an item in the list (e.g., click,
     * keyboard select).
     */
    @Event()
    interact: EventEmitter<ListItem>;

    public connectedCallback() {
        this.setup();
    }

    public disconnectedCallback() {
        this.teardown();
    }

    public componentDidLoad() {
        this.setup();
        this.triggerIconColorWarning();
    }

    public render() {
        this.config = {
            badgeIcons: this.badgeIcons,
            type: this.type,
            iconSize: this.iconSize,
        };
        let maxLinesSecondaryText = +this.maxLinesSecondaryText?.toFixed();
        if (this.maxLinesSecondaryText < 1) {
            maxLinesSecondaryText = 1;
        }

        const html = this.listRenderer.render(this.items, this.config);

        return (
            <Host
                style={{
                    '--maxLinesSecondaryText': `${maxLinesSecondaryText}`,
                }}
            >
                {html}
            </Host>
        );
    }

    @Watch('type')
    protected handleType() {
        this.setupListeners();
    }

    @Watch('items')
    protected itemsChanged() {
        if (!this.mdcList) {
            return;
        }

        setTimeout(() => {
            this.setup();

            const listItems = this.items.filter(this.isListItem);

            if (this.multiple) {
                this.mdcList.selectedIndex = listItems
                    .filter((item: ListItem) => item.selected)
                    .map((item: ListItem) => listItems.indexOf(item));
            } else {
                const selectedIndex = listItems.findIndex(
                    (item: ListItem) => item.selected
                );

                if (selectedIndex === -1) {
                    this.mdcList.initializeListType();
                } else {
                    this.mdcList.selectedIndex = selectedIndex;
                }
            }
        }, 0);
    }

    private setup = () => {
        this.setupList();

        this.setupListeners();
    };

    private setupList = () => {
        if (this.mdcList) {
            this.teardown();
            this.mdcList = null;
        }

        const element = this.element.shadowRoot.querySelector(
            '.mdc-deprecated-list'
        );
        if (!element) {
            return;
        }

        this.mdcList = new MDCList(element);
        this.mdcList.hasTypeahead = true;
    };

    private setupListeners = () => {
        if (!this.mdcList) {
            return;
        }

        this.mdcList.unlisten(ACTION_EVENT, this.handleAction);

        this.selectable = ['selectable', 'radio', 'checkbox'].includes(
            this.type
        );
        this.multiple = this.type === 'checkbox';

        if (!this.selectable) {
            return;
        }

        this.mdcList.listen(ACTION_EVENT, this.handleAction);
        this.mdcList.singleSelection = !this.multiple;
    };

    private teardown = () => {
        this.mdcList?.unlisten(ACTION_EVENT, this.handleAction);
        this.mdcList?.destroy();
    };

    private handleAction = (event: MDCListActionEvent) => {
        if (!this.multiple) {
            this.handleSingleSelect(event.detail.index);

            return;
        }

        this.handleMultiSelect(event.detail.index);
    };

    private handleSingleSelect = (index: number) => {
        const listItems = this.items.filter(this.isListItem) as ListItem[];
        if (listItems[index].disabled) {
            return;
        }

        const selectedItem: ListItem = listItems.find((item: ListItem) => {
            return !!item.selected;
        });

        let interactedItem: ListItem;

        if (selectedItem) {
            if (this.type !== 'radio') {
                this.mdcList.selectedIndex = -1;
            }

            interactedItem = { ...selectedItem, selected: false };
            this.change.emit(interactedItem);
        }

        if (listItems[index] !== selectedItem) {
            interactedItem = { ...listItems[index], selected: true };
            this.change.emit(interactedItem);
        }

        this.interact.emit(interactedItem);
    };

    private handleMultiSelect = (index: number) => {
        const listItems = this.items.filter(this.isListItem) as ListItem[];
        if (listItems[index].disabled) {
            return;
        }

        const selectedItems: ListItem[] = listItems
            .filter((item: ListItem, listIndex: number) => {
                if (listIndex === index) {
                    // This is the item that was selected or deselected,
                    // so we negate its previous selection status.
                    return !item.selected;
                }

                // This is an item that didn't change, so we keep its selection status.
                return item.selected;
            })
            .map((item: ListItem) => {
                return { ...item, selected: true };
            });

        this.change.emit(selectedItems);
        this.interact.emit({ ...selectedItems[index] });
    };

    private isListItem = (item: ListItem): boolean => {
        return !('separator' in item);
    };

    private triggerIconColorWarning() {
        if (this.items?.some((item) => 'iconColor' in item)) {
            console.warn(
                "The `iconColor` prop is deprecated now! Use the new `Icon` interface and instead of `iconColor: 'color-name'` write `icon {name: 'icon-name', color: 'color-name'}`."
            );
        }
    }
}

