import { Component, Prop, h } from '@stencil/core';

/**
 * This is a private component, used to render a notched outline
 * around all input elements that can have a floating label.
 * Inspired by Material Design's styles for input fields.
 * We use it in various components to unify styles and avoid
 * repeating code.
 *
 * :::note
 * The component has `shadow: false`. This is to improve performance,
 * and ensure that its internal elements are considered as internal parts
 * of the consumer's DOM. This way, the value `for` in `<label for="id-of-input-element">`
 * would be correctly associated with the input element's `id`, in the consumer component.
 * :::
 * @exampleComponent limel-example-notched-outline-basic
 * @private
 */
@Component({
    tag: 'limel-notched-outline',
    styleUrl: 'notched-outline.scss',
    shadow: false,
})
export class NotchedOutline {
    /**
     * Set to `true` when the input element is required.
     * This applies proper visual styles, such as inclusion of an asterisk
     * beside the label.
     */
    @Prop({ reflect: true })
    public required = false;

    /**
     * Set to `true` when the input element is readonly.
     * This applies proper visual styles, such as making the outline transparent.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to indicate that the current value of the input element is
     * invalid. This applies proper visual styles, such as making the outlines red.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * Set to `true` to indicate that the input element is
     * disabled. This applies proper visual styles, such as making the outlines
     * and the label transparent.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Label to display for the input element.
     * :::important
     * Note that the input element of the consumer component will be
     * labeled by this label, using the `labelId` prop.
     * :::
     */
    @Prop({ reflect: true })
    public label?: string;

    /**
     * The `id` of the input element which should be
     * labeled by the provided label.
     */
    @Prop({ reflect: true })
    public labelId?: string;

    /**
     * Set to `true` when the user has entered a value for the input element,
     * shrinking the label in size, and visually rendering it above the entered value.
     */
    @Prop({ reflect: true })
    public hasValue = false;

    /**
     * Set to `true` when the consumer element displays a leading icon.
     * This applies proper visual styles, such as rendering the label
     * correctly placed beside the leading icon.
     */
    @Prop({ reflect: true })
    public hasLeadingIcon = false;

    /**
     * Set to `true` when the consumer element needs to render the
     * label above the input element, despite existence of a `value`.
     * For example in the `text-editor` or `limel-select`,
     * where the default layout requires a floating label.
     */
    @Prop({ reflect: true })
    public hasFloatingLabel = false;

    public render() {
        return (
            <div class="limel-notched-outline">
                <slot name="content" />
                <span
                    class="limel-notched-outline--outlines"
                    aria-hidden="true"
                >
                    <span class="limel-notched-outline--leading-outline" />
                    {this.renderLabel()}
                    <span class="limel-notched-outline--trailing-outline" />
                    {this.renderEmptyReadonlyValue()}
                </span>
            </div>
        );
    }

    private renderLabel() {
        if (!this.label) {
            return;
        }

        return (
            <span class="limel-notched-outline--notch">
                <label htmlFor={this.labelId}>{this.label}</label>
            </span>
        );
    }

    private renderEmptyReadonlyValue() {
        if (!this.readonly || this.hasValue) {
            return;
        }

        return (
            <span
                class="limel-notched-outline--empty-readonly-value"
                aria-hidden="true"
            >
                â€“
            </span>
        );
    }
}

@use '../../style/mixins.scss';
@use '../../style/internal/shared_input-select-picker';
/**
 * @prop --limel-notched-outline-z-index: Defines the `z-index` of the outlines & the label, since they are absolutely positioned. Useful if there are other elements with z-indexes in the consumer.
 */

$border-radius: 0.25rem;
$value-top: 0.62rem;

*,
*:before,
*:after {
    box-sizing: border-box;
}

limel-notched-outline {
    --limel-notched-outline-border-color: #{shared_input-select-picker.$lime-text-field-outline-color};
    --limel-notched-outline-background-color: #{shared_input-select-picker.$background-color-normal};

    display: block;
    width: 100%;
    height: fit-content;
}

.limel-notched-outline {
    position: relative;
    width: 100%;
    height: 100%;

    [slot='content'] {
        background-color: var(--limel-notched-outline-background-color);
        border-radius: var(
            --limel-notched-outline-border-radius,
            $border-radius
        );
    }

    // Why is everything prefixed?
    // Because the component has `shadow: false;`
    // and this ensures that we are not inheriting styles.
    &--outlines {
        pointer-events: none;
        position: absolute;
        inset: 0;
        z-index: var(--limel-notched-outline-z-index, 0);
        display: flex;
    }

    &--leading-outline,
    &--notch,
    &--trailing-outline {
        transition: border-color 0.2s ease;
        border-width: 1px;
        border-style: solid;
        border-color: var(--limel-notched-outline-border-color);
    }

    &--leading-outline {
        flex-shrink: 0;
        width: 0.75rem;
        border-right-width: 0;
        border-top-left-radius: var(
            --limel-notched-outline-border-radius,
            $border-radius
        );
        border-bottom-left-radius: var(
            --limel-notched-outline-border-radius,
            $border-radius
        );
    }

    &--notch {
        flex-shrink: 0;

        position: relative;
        z-index: 2;

        border-top-color: var(
            --limel-notched-outline-notch-border-top-color,
            var(--limel-notched-outline-border-color)
        );
        border-right-width: 0;
        border-left-width: 0;

        max-width: calc(100% - 1.5rem);

        label {
            all: unset;
            @include mixins.truncate-text;
            position: relative;
            transition:
                color 0.2s ease,
                font-size 0.2s ease,
                transform 0.12s cubic-bezier(0.4, 0, 0.2, 1);

            transform: translate3d(
                var(--limel-notched-outline-label-transform-x, 0),
                var(--limel-notched-outline-label-transform-y, $value-top),
                0
            );
            display: block;
            padding: 0 0.25rem;

            color: var(
                --limel-notched-outline-label-color,
                #{shared_input-select-picker.$label-color}
            );
            font-size: var(
                --limel-notched-outline-label-font-size,
                #{shared_input-select-picker.$cropped-label-hack--font-size}
            );
            letter-spacing: var(
                --mdc-typography-subtitle1-letter-spacing,
                0.009375em
            );
            line-height: normal;

            &:after {
                position: absolute;
                right: 0;
                padding: 0 0.25rem;
            }
        }
    }

    &--trailing-outline {
        flex-grow: 1;
        border-left-width: 0;
        border-top-right-radius: var(
            --limel-notched-outline-border-radius,
            $border-radius
        );
        border-bottom-right-radius: var(
            --limel-notched-outline-border-radius,
            $border-radius
        );
    }

    &--empty-readonly-value {
        @include shared_input-select-picker.lime-looks-like-input-value;
        position: absolute;
        top: $value-top;
        left: 1rem;
    }
}

limel-notched-outline {
    &:not([disabled]:not([disabled='false'])) {
        &:hover {
            --limel-notched-outline-border-color: #{shared_input-select-picker.$lime-text-field-outline-color--hovered};
            --limel-notched-outline-background-color: #{shared_input-select-picker.$background-color-hovered};
        }

        &:has([slot='content']:focus-visible),
        &:has([slot='content']:focus-within) {
            --limel-notched-outline-border-color: #{shared_input-select-picker.$lime-text-field-outline-color--focused};
            --limel-notched-outline-background-color: #{shared_input-select-picker.$background-color-focused};
        }
    }

    &[disabled]:not([disabled='false']) {
        --limel-notched-outline-label-color: #{shared_input-select-picker.$label-color-disabled};
    }

    &[required]:not([required='false']) {
        .limel-notched-outline--notch {
            label {
                padding-right: 0.75rem;

                &:after {
                    content: '*';
                    scale: 1.3;
                }
            }
        }
    }

    &[invalid]:not([invalid='false']) {
        &:not([disabled]:not([disabled='false'])) {
            --limel-notched-outline-border-color: var(--lime-error-text-color);
            &:hover {
                --limel-notched-outline-border-color: var(
                    --lime-error-text-color
                );
            }
        }

        .limel-notched-outline--notch {
            label {
                &:after {
                    color: var(--lime-error-text-color);
                }
            }
        }
    }

    &[readonly]:not([readonly='false']) {
        --limel-notched-outline-border-color: transparent !important;
        --limel-notched-outline-background-color: transparent !important;

        .limel-notched-outline--notch {
            label {
                transition-duration: 0s;
            }
        }
    }

    &[has-leading-icon] {
        &:not([has-leading-icon='false']):not([has-floating-label]):not(
                [has-value]
            ) {
            --limel-notched-outline-label-transform-x: 1.25rem;
        }

        .limel-notched-outline--empty-readonly-value {
            left: 2.25rem;
        }
    }
}

// Transitioning the floating label
@mixin float-label {
    --limel-notched-outline-label-font-size: 0.65rem; // `10.4px` similar to MDC's floating label
    --limel-notched-outline-label-transform-x: 0;
    --limel-notched-outline-label-transform-y: calc(-50% - 0.09375rem);
    --limel-notched-outline-notch-border-top-color: transparent;
}

limel-notched-outline {
    &:not([disabled]:not([disabled='false'])) {
        &:hover,
        &:focus,
        &:focus-within {
            label {
                will-change: color, transform, font-size;
            }
        }

        &:has([slot='content']:focus-visible),
        &:has([slot='content']:focus-within) {
            @include float-label;
        }
    }

    &[has-floating-label],
    &[has-value]:not([has-value='false']),
    &[readonly]:not([has-value]:not([has-value='true'])) {
        @include float-label;
    }
}

import { Action } from '../collapsible-section/action';
import { ActionPosition, ActionScrollBehavior } from '../picker/actions.types';
import { Chip } from '../chip-set/chip.types';
import { ListItem } from '../list/list-item.types';
import { Searcher } from '../picker/searcher.types';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { isDescendant } from '../../util/dom';
import { ARROW_DOWN, ARROW_UP, ENTER, ESCAPE, TAB } from '../../util/keycodes';
import { createRandomString } from '../../util/random-string';
import {
    LimelChipSetCustomEvent,
    LimelListCustomEvent,
} from '../../components';
import { getIconFillColor, getIconName } from '../icon/get-icon-props';
import { PickerValue } from './value.types';
import { DebouncedFunc, debounce } from 'lodash-es';

const SEARCH_DEBOUNCE = 300;
const CHIP_SET_TAG_NAME = 'limel-chip-set';
const DEFAULT_SEARCHER_MAX_RESULTS = 20;

/**
 * @exampleComponent limel-example-picker-basic
 * @exampleComponent limel-example-picker-multiple
 * @exampleComponent limel-example-picker-icons
 * @exampleComponent limel-example-picker-pictures
 * @exampleComponent limel-example-picker-value-as-object
 * @exampleComponent limel-example-picker-value-as-object-with-actions
 * @exampleComponent limel-example-picker-empty-suggestions
 * @exampleComponent limel-example-picker-leading-icon
 * @exampleComponent limel-example-picker-static-actions
 * @exampleComponent limel-example-picker-composite
 */
@Component({
    tag: 'limel-picker',
    shadow: true,
    styleUrl: 'picker.scss',
})
export class Picker {
    /**
     * True if the picker should be disabled
     */
    @Prop()
    public disabled: boolean = false;

    /**
     * Set to `true` to disable adding and removing items,
     * but allow interaction with existing items.
     */
    @Prop({ reflect: true })
    public readonly: boolean = false;

    /**
     * Text to display for the input field of the picker
     */
    @Prop()
    public label: string;

    /**
     * Search label to display in the input field when searching
     */
    @Prop()
    public searchLabel: string;

    /**
     * Optional helper text to display below the input field when it has focus
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * Leading icon to show to the far left in the text field
     */
    @Prop()
    public leadingIcon: string;

    /**
     * A message to display when the search returned an empty result
     */
    @Prop()
    public emptyResultMessage: string;

    /**
     * True if the control requires a value
     */
    @Prop()
    public required: boolean = false;

    /**
     * Set to `true` to indicate that the current value of the input field is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * Currently selected value or values. Where the value can be an object.
     */
    @Prop()
    public value: ListItem<PickerValue> | Array<ListItem<PickerValue>>;

    /**
     * A search function that takes a search-string as an argument,
     * and returns a promise that will eventually be resolved with
     * an array of `ListItem`:s.
     *
     * See the docs for the type `Searcher` for type information on
     * the searcher function itself.
     */
    @Prop()
    public searcher?: Searcher;

    /**
     * Only used if no `searcher` is provided. The picker will then use a
     * default search function that filters the `allItems` based on the
     * `text` and `secondaryText` properties of the items.
     * This way, custom search functions are typically only needed when the
     * search is done on the server.
     * For performance reasons, the default searcher will never return more
     * than 20 items, but if there are more than 20 items, the rest can be
     * found by typing more characters in the search field.
     */
    @Prop()
    public allItems?: Array<ListItem<PickerValue>> = [];

    /**
     * True if multiple values are allowed
     */
    @Prop()
    public multiple: boolean = false;

    /**
     * Sets delimiters between chips. Works only when `multiple` is `true`.
     */
    @Prop({ reflect: true })
    public delimiter: string = null;

    /**
     * Static actions that can be clicked by the user.
     */
    @Prop()
    public actions: Array<ListItem<Action>> = [];

    /**
     * Position of the custom static actions in the picker's results dropdown.
     * Can be set to `'top'` or `'bottom'`.
     */
    @Prop()
    public actionPosition: ActionPosition = 'bottom';

    /**
     * Scroll behavior of the custom static actions, when user scrolls
     * in the picker's results dropdown. Can be set to `'scroll'` which means
     * the action items will scroll together with the list, or `'sticky'` which
     * retains their position at the top or bottom of the drop down while
     * scrolling.
     */
    @Prop()
    public actionScrollBehavior: ActionScrollBehavior = 'sticky';

    /**
     * Whether badge icons should be used in the result list or not
     */
    @Prop({ reflect: true })
    public badgeIcons: boolean = false;

    /**
     * Fired when a new value has been selected from the picker
     */
    @Event()
    private change: EventEmitter<
        ListItem<PickerValue> | Array<ListItem<PickerValue>>
    >;

    /**
     * Fired when clicking on a selected value
     */
    @Event()
    private interact: EventEmitter<ListItem<PickerValue>>;

    /**
     * Emitted when the user selects an action.
     */
    @Event()
    private action: EventEmitter<Action>;

    @State()
    private items: Array<ListItem<number | string>>;

    @State()
    private textValue: string = '';

    @State()
    private loading: boolean = false;

    @State()
    private chips: Chip[] = [];

    @Element()
    private host: HTMLLimelPickerElement;

    // Should NOT be decorated with State(), since this
    // should not trigger a re-render by itself.
    private chipSetEditMode = false;

    private debouncedSearch: DebouncedFunc<(query: string) => Promise<void>>;
    private chipSet: HTMLLimelChipSetElement;
    private portalId: string;

    constructor() {
        this.handleTextInput = this.handleTextInput.bind(this);
        this.handleInputKeyDown = this.handleInputKeyDown.bind(this);
        this.handleDropdownKeyDown = this.handleDropdownKeyDown.bind(this);
        this.handleInputFieldFocus = this.handleInputFieldFocus.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleInteract = this.handleInteract.bind(this);
        this.handleListChange = this.handleListChange.bind(this);
        this.handleActionListChange = this.handleActionListChange.bind(this);
        this.handleStopEditAndBlur = this.handleStopEditAndBlur.bind(this);
        this.handleCloseMenu = this.handleCloseMenu.bind(this);
        this.onListKeyDown = this.onListKeyDown.bind(this);

        this.portalId = createRandomString();
        this.debouncedSearch = debounce(this.search, SEARCH_DEBOUNCE);
    }

    public componentWillLoad() {
        this.chips = this.createChips(this.value);
    }

    public componentDidLoad() {
        this.chipSet = this.host.shadowRoot.querySelector(CHIP_SET_TAG_NAME);
    }

    public disconnectedCallback() {
        this.debouncedSearch.cancel();
    }

    public async componentWillUpdate() {
        this.chipSetEditMode = false;
        if (this.chipSet) {
            this.chipSetEditMode = await this.chipSet.getEditMode();
        }
    }

    public render() {
        const props: {
            maxItems?: number;
        } = {};

        if (!this.multiple) {
            props.maxItems = 1;
        }

        return [
            <limel-chip-set
                type="input"
                inputType="search"
                label={this.label}
                helperText={this.helperText}
                leadingIcon={this.leadingIcon}
                value={this.chips}
                disabled={this.disabled}
                invalid={this.invalid}
                delimiter={this.renderDelimiter()}
                readonly={this.readonly}
                required={this.required}
                searchLabel={this.searchLabel}
                onInput={this.handleTextInput}
                onKeyDown={this.handleInputKeyDown}
                onChange={this.handleChange}
                onInteract={this.handleInteract}
                onStartEdit={this.handleInputFieldFocus}
                onStopEdit={this.handleStopEditAndBlur}
                emptyInputOnBlur={false}
                clearAllButton={this.multiple && !this.chipSetEditMode}
                {...props}
            />,
            this.renderDropdown(),
        ];
    }

    @Watch('value')
    protected onChangeValue() {
        this.chips = this.createChips(this.value);
    }

    private renderDelimiter() {
        if (this.multiple) {
            return this.delimiter;
        }

        return null;
    }

    private getValueId = (item: ListItem) => {
        const value = item.value;
        if (!!value && typeof value === 'object') {
            return value.id;
        }

        return value;
    };

    private createChips = (value: ListItem | ListItem[]): Chip[] => {
        if (!value) {
            return [];
        }

        if (this.multiple) {
            const listItems: ListItem[] = value as ListItem[];

            return listItems.map(this.createChip);
        }

        const listItem: ListItem = value as ListItem;

        return [this.createChip(listItem)];
    };

    private createChip = (listItem: ListItem): Chip => {
        const name = getIconName(listItem.icon);

        const color = getIconFillColor(listItem.icon, listItem.iconColor);
        const valueId = this.getValueId(listItem);

        return {
            id: `${valueId}`,
            text: listItem.text,
            removable: true,
            icon: name ? { name: name, color: color } : undefined,
            image: listItem.image,
            value: listItem,
            menuItems: listItem.actions,
        };
    };

    /**
     * Renders the dropdown with the items to pick from, or a spinner if the picker
     * is waiting for items to be received
     *
     * @returns picker dropdown
     */
    private renderDropdown() {
        const dropDownContent = this.getDropdownContent();

        const content = [];

        if (this.shouldShowDropDownContent()) {
            const actionContent = this.getActionContent();
            if (this.actionPosition === 'top') {
                content.push(actionContent);
            }

            if (dropDownContent) {
                content.push(dropDownContent);
            }

            if (this.actionPosition === 'bottom') {
                content.push(actionContent);
            }
        }

        return this.renderPortal(content);
    }

    private getActionContent() {
        const actionCount = this.actions?.length ?? 0;
        if (actionCount === 0) {
            return null;
        }

        return [
            <limel-list
                class={{
                    'static-actions-list': true,
                    'is-on-top': this.actionPosition === 'top',
                    'is-at-bottom': this.actionPosition === 'bottom',
                    'has-position-sticky':
                        this.actionScrollBehavior === 'sticky',
                }}
                badgeIcons={true}
                type={'selectable'}
                onChange={this.handleActionListChange}
                items={this.actions.map(this.removeUnusedPropertiesOnAction)}
            />,
        ];
    }

    private removeUnusedPropertiesOnAction(
        action: ListItem<Action>
    ): ListItem<Action> {
        return {
            ...action,
            actions: [],
        };
    }

    private shouldShowDropDownContent() {
        if (this.isFull()) {
            return false;
        }

        return !!this.chipSetEditMode;
    }

    private getDropdownContent() {
        if (!this.shouldShowDropDownContent()) {
            return;
        }

        if (this.loading) {
            return this.renderSpinner();
        }

        if (!this.items?.length) {
            return this.renderEmptyMessage();
        }

        return this.renderListResult();
    }

    /**
     * Returns true if the picker is "full"
     * The picker is considered to be full if it has a value and only one is allowed
     *
     * @returns true if the picker is full
     */
    private isFull(): boolean {
        return !this.multiple && !!this.value;
    }

    private renderSpinner() {
        return (
            <div
                style={{
                    width: '100%',
                    display: 'flex',
                    'align-items': 'center',
                    'justify-content': 'center',
                    padding: '1rem 0',
                }}
            >
                <limel-spinner limeBranded={false} />
            </div>
        );
    }

    private renderEmptyMessage() {
        if (!this.emptyResultMessage) {
            return;
        }

        const style = {
            color: 'rgb(var(--contrast-1100))',
            'text-align': 'center',
            margin: '0.5rem 1rem',
        };

        return <p style={style}>{this.emptyResultMessage}</p>;
    }

    private renderListResult() {
        return (
            <limel-list
                badgeIcons={this.badgeIcons}
                onChange={this.handleListChange}
                onKeyDown={this.onListKeyDown}
                type="selectable"
                items={this.items}
            />
        );
    }

    private onListKeyDown(event: KeyboardEvent) {
        const keyFound = [TAB, ESCAPE, ENTER].includes(event.key);
        if (keyFound) {
            this.chipSet.setFocus();
        }
    }

    private renderPortal(content: any[] = []) {
        const dropdownZIndex = getComputedStyle(this.host).getPropertyValue(
            '--dropdown-z-index'
        );

        return (
            <limel-portal
                visible={content.length > 0}
                containerId={this.portalId}
                inheritParentWidth={true}
                containerStyle={{ 'z-index': dropdownZIndex }}
            >
                <limel-menu-surface
                    open={content.length > 0}
                    allowClicksElement={this.host}
                    style={{
                        '--menu-surface-width': '100%',
                        'max-height': 'inherit',
                        display: 'flex',
                    }}
                    onDismiss={this.handleCloseMenu}
                >
                    {content}
                </limel-menu-surface>
            </limel-portal>
        );
    }

    /**
     * Check if a descendant still has focus. If not, reset text value and search result.
     */
    private handleStopEditAndBlur() {
        // In browsers where shadow DOM is not supported activeElement on shadowRoot will return null
        // However, document.activeElement will return the actual focused element instead of the outermost shadow host
        const element =
            this.host.shadowRoot.activeElement || document.activeElement;
        const portalElement = document.querySelector(`#${this.portalId}`);
        if (
            isDescendant(element as HTMLElement, this.host) ||
            isDescendant(element as HTMLElement, portalElement as HTMLElement)
        ) {
            return;
        }

        this.clearInputField();
    }

    /**
     * Input handler for the input field
     *
     * @param event - event
     */
    private async handleTextInput(event) {
        event.stopPropagation();

        const query = event.detail;
        this.textValue = query;

        this.debouncedSearch(query);
        // If the search-query is an empty string, bypass debouncing.
        if (query === '') {
            this.debouncedSearch.flush();
        }
    }

    private search = async (query: string) => {
        const timeoutId = setTimeout(() => {
            this.loading = true;
        });
        const searcher = this.searcher || this.defaultSearcher;
        const result = (await searcher(this.textValue)) as Array<
            ListItem<PickerValue>
        >;

        // If the search function resolves immediately,
        // the loading spinner will not be shown.
        clearTimeout(timeoutId);

        this.handleSearchResult(query, result);
    };

    private defaultSearcher: Searcher = async (
        query: string
    ): Promise<ListItem[]> => {
        if (query === '') {
            return this.allItems.slice(0, DEFAULT_SEARCHER_MAX_RESULTS);
        }

        const filteredItems = this.allItems.filter((item) => {
            let searchText = item.text.toLowerCase();
            if (item.secondaryText) {
                searchText =
                    searchText + ' ' + item.secondaryText.toLowerCase();
            }

            return searchText.includes(query.toLowerCase());
        });

        return filteredItems.slice(0, DEFAULT_SEARCHER_MAX_RESULTS);
    };

    /**
     * Change handler for the list
     *
     * @param event - event
     */
    private handleListChange(
        event: LimelListCustomEvent<ListItem<PickerValue>>
    ) {
        event.stopPropagation();
        if (!this.value || this.value !== event.detail) {
            let newValue: ListItem<PickerValue> | Array<ListItem<PickerValue>> =
                event.detail;
            if (this.multiple) {
                newValue = [
                    ...(this.value as Array<ListItem<PickerValue>>),
                    event.detail,
                ];
            }

            this.change.emit(newValue);
            this.items = [];
        }

        if (this.multiple) {
            this.textValue = '';
            this.chipSet?.setFocus(true);
        }
    }

    /**
     * Change handler for the list
     *
     * @param event - event
     */
    private handleActionListChange(
        event: LimelListCustomEvent<ListItem<Action>>
    ) {
        event.stopPropagation();
        if (!event.detail) {
            return;
        }

        this.action.emit(event.detail.value);
        this.items = [];
    }

    /**
     * Focus handler for the chip set
     * Prevent focus if the picker has a value and does not support multiple values
     */
    private handleInputFieldFocus() {
        const query = this.textValue;
        this.debouncedSearch(query);
    }

    private handleChange(event: LimelChipSetCustomEvent<Chip | Chip[]>) {
        event.stopPropagation();

        let newValue = null;
        if (this.multiple) {
            const chips = event.detail as Chip[];
            newValue = chips.map((chip) => {
                return (this.value as ListItem[]).find((item) => {
                    const valueId = this.getValueId(item);

                    return `${valueId}` === chip.id;
                });
            });
        }

        this.change.emit(newValue);
    }

    private handleInteract(event: LimelChipSetCustomEvent<Chip>) {
        event.stopPropagation();
        this.interact.emit(event.detail ? event.detail.value : event.detail);
    }

    /**
     * Key handler for the input field
     * Will change focus to the first/last item in the dropdown list to enable selection with the keyboard
     *
     * @param event - event
     */
    private handleInputKeyDown(event: KeyboardEvent) {
        const isForwardTab =
            event.key === TAB &&
            !event.altKey &&
            !event.metaKey &&
            !event.shiftKey;
        const isUp = event.key === ARROW_UP;
        const isDown = event.key === ARROW_DOWN;

        if (!isForwardTab && !isUp && !isDown) {
            return;
        }

        const list = document.querySelector(` #${this.portalId} limel-list`);
        if (!list) {
            return;
        }

        event.preventDefault();

        if (isForwardTab || isDown) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-deprecated-list-item:first-child'
            );
            listElement.focus();

            return;
        }

        if (isUp) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-deprecated-list-item:last-child'
            );
            listElement.focus();
        }
    }

    /**
     * Key handler for the dropdown
     *
     * @param event - event
     */
    private handleDropdownKeyDown(event: KeyboardEvent) {
        const isEscape = event.key === ESCAPE;

        if (isEscape) {
            event.preventDefault();
            this.textValue = '';
            this.chipSet.setFocus(true);
        }
    }

    private handleSearchResult(query: string, result: ListItem[]) {
        if (query === this.textValue) {
            this.items = result;
            if (this.multiple) {
                const values = this.value as ListItem[];
                this.items = result.filter((item) => {
                    return !values.includes(item);
                });
            }

            this.loading = false;
        }
    }

    private handleCloseMenu() {
        if (this.items.length > 0) {
            return;
        }

        this.clearInputField();
    }

    private clearInputField() {
        this.chipSet.emptyInput();
        this.textValue = '';
        this.handleSearchResult('', []);
        this.debouncedSearch.cancel();
    }
}

// Note! The `--dropdown-z-index` property is used from `picker.tsx`.
/**
  * @prop --dropdown-z-index: z-index of the dropdown menu.
  */

:host {
    position: relative;
    display: block;
}

:host([hidden]) {
    display: none;
}

import {
    Component,
    h,
    Prop,
    Element,
    Event,
    EventEmitter,
    Watch,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { zipObject } from 'lodash-es';
import { portalContains } from '../portal/contains';
import { ESCAPE } from '../../util/keycodes';
import { OpenDirection } from '../menu/menu.types';

/**
 * A popover is an impermanent layer that is displayed on top of other content
 * when user taps an element that triggers the popover. This element can be
 * practically anything, a button, piece of text, and icon, etc.
 *
 * Popovers are nonmodal, thus they don't have a semi-transparent backdrop layer
 * that darkens the content below them. Also for the same reason, they can be
 * dismissed by tapping or clicking another part of the screen, but not by a
 * clicking a button or link on the popover itself.
 *
 * ## Usage
 * Use a popover to show **options** or **information** related to the trigger
 * onscreen. A typical use case for popovers is a tooltip, in which you show
 * help text or contextual information to users.
 *
 * Popovers are most appropriate on larger screens but can be used on smaller
 * screens too, as long as their content is responsive and takes into account
 * the context they are displayed on.
 *
 * When a popover is displayed, interactions with other controls are blocked,
 * until user dismisses the popover.
 *
 *
 * ## Layout
 * Popovers has only one slot in which you can import a custom web-component.
 *
 * :::note
 * You must make sure that web-components that you import into the slot has
 * a `width: 100%;` style so that it can horizontally stretch across the popover.
 *
 * However, `width` of the popover can be controlled by specifying a CSS variable
 * of `--popover-surface-width`. If you don't specify any width, the popover
 * will grow as wide as its content.
 * :::
 *
 * :::important
 * Do not make a popover too big. They should never take over the entire screen.
 * If your content is that big, you should probably be using a Modal instead.
 * :::
 *
 * @slot - Content to put inside the surface
 * @exampleComponent limel-example-popover-basic
 * @exampleComponent limel-example-popover-trigger-interaction
 * @exampleComponent limel-example-popover-styling
 */
@Component({
    tag: 'limel-popover',
    shadow: true,
    styleUrl: 'popover.scss',
})
export class Popover {
    /**
     * True if the content within the popover should be visible
     */
    @Prop()
    public open = false;

    /**
     * Decides the popover's location in relation to its trigger
     */
    @Prop({ reflect: true })
    public openDirection: OpenDirection;

    /**
     * Emits an event when the component is closing
     */
    @Event()
    private close: EventEmitter<void>;

    @Element()
    private host: HTMLLimelPopoverElement;

    private portalId: string;

    constructor() {
        this.portalId = createRandomString();
        this.globalClickListener = this.globalClickListener.bind(this);
    }

    @Watch('open')
    protected watchOpen() {
        this.setupGlobalHandlers();
    }

    public componentWillLoad() {
        this.setupGlobalHandlers();
    }

    public componentDidRender() {
        const slotElement = this.host.shadowRoot.querySelector('slot');
        // eslint-disable-next-line unicorn/no-array-for-each
        slotElement.assignedElements().forEach(this.setTriggerAttributes);
    }

    private setupGlobalHandlers() {
        if (this.open) {
            document.addEventListener('click', this.globalClickListener, {
                capture: true,
            });
            document.addEventListener('keyup', this.handleGlobalKeyPress);
        } else {
            document.removeEventListener('click', this.globalClickListener);
            document.removeEventListener('keyup', this.handleGlobalKeyPress);
        }
    }

    public render() {
        const cssProperties = this.getCssProperties();
        const popoverZIndex = getComputedStyle(this.host).getPropertyValue(
            '--popover-z-index'
        );

        return (
            <div class="trigger-anchor">
                <slot name="trigger" />
                <limel-portal
                    visible={this.open}
                    containerId={this.portalId}
                    containerStyle={{ 'z-index': popoverZIndex }}
                    openDirection={this.openDirection}
                >
                    <limel-popover-surface
                        contentCollection={this.host.children}
                        style={cssProperties}
                    />
                </limel-portal>
            </div>
        );
    }

    private globalClickListener(event: MouseEvent) {
        const element: HTMLElement = event.target as HTMLElement;
        const clickedInside = portalContains(this.host, element);
        if (this.open && !clickedInside) {
            event.stopPropagation();
            event.preventDefault();
            this.close.emit();
        }
    }

    private getCssProperties() {
        const propertyNames = [
            '--popover-surface-width',
            '--popover-body-background-color',
            '--popover-border-radius',
            '--popover-box-shadow',
        ];
        const style = getComputedStyle(this.host);
        const values = propertyNames.map((property) => {
            return style.getPropertyValue(property);
        });

        return zipObject(propertyNames, values);
    }

    private handleGlobalKeyPress = (event: KeyboardEvent) => {
        if (event.key !== ESCAPE) {
            return;
        }

        event.stopPropagation();
        event.preventDefault();
        this.close.emit();
    };

    private setTriggerAttributes = (element: HTMLElement) => {
        const attributes = {
            'aria-haspopup': true,
            'aria-expanded': this.open,
            'aria-controls': this.portalId,
            role: 'button',
        };

        for (const [key, value] of Object.entries(attributes)) {
            if (value) {
                element.setAttribute(key, String(value));
            } else {
                element.removeAttribute(key);
            }
        }
    };
}

/**
 * @prop --popover-surface-width: Width of the popover surface. Defaults to `auto`
 * @prop --popover-body-background-color: Background color of popover body. Defaults to `--lime-elevated-surface-background-color`.
 * @prop --popover-border-radius: Border radius of popover. Defaults to `0.75rem`.
 * @prop --popover-z-index: z-index of the popover.
 * @prop --popover-box-shadow: Defines the shadow of the popover surface. Defaults to `--shadow-depth-16`.
 */

.trigger-anchor {
    display: inline-block;
    position: relative;
}

import { Component, h, Prop, Element } from '@stencil/core';

/**
 * @slot - Content to put inside the surface
 * @private
 */

@Component({
    tag: 'limel-popover-surface',
    shadow: true,
    styleUrl: 'popover-surface.scss',
})
export class PopoverSurface {
    /**
     * Content to render
     */
    @Prop()
    public contentCollection: HTMLCollection;

    @Element()
    private host: HTMLLimelPopoverSurfaceElement;

    public componentDidLoad() {
        this.appendElement();
    }

    public render() {
        return <div class="limel-popover-surface" tabindex="0" />;
    }

    private appendElement() {
        const portalContainer = this.host.shadowRoot.querySelector(
            '.limel-popover-surface'
        );

        for (const child of this.contentCollection) {
            if (child.slot === 'trigger') {
                continue;
            }

            portalContainer.append(child);
        }
    }
}

@use '../../style/functions';
@use '../../style/mixins';

:host(limel-popover-surface) {
    isolation: isolate;

    position: relative;
    display: flex;

    width: var(--popover-surface-width, auto);
    max-height: inherit; // inherits it from the dynamically calculated `max-height` of `limel-portal--container`
    max-width: calc(100vw - 2rem);
    margin: 0 0.25rem;
}

.limel-popover-surface {
    flex: 1;
    min-width: 0;
    min-height: 0;
    border-radius: var(--popover-border-radius, functions.pxToRem(12));
    box-shadow: var(--popover-box-shadow, var(--shadow-depth-16));

    backdrop-filter: blur(functions.pxToRem(5));
    -webkit-backdrop-filter: blur(functions.pxToRem(5));

    &:after {
        // allows using `--popover-body-background-color` while
        // getting the blurred backdrop effect
        transition: opacity 0.4s ease;
        pointer-events: none;

        content: '';
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        z-index: -1;

        opacity: 0.95; //temperary change due to not supporting `backdrop-filter` in Chromium browsers

        border-radius: var(--popover-border-radius, functions.pxToRem(12));
        background-color: var(
            --popover-body-background-color,
            var(--lime-elevated-surface-background-color)
        );
    }

    @include mixins.visualize-keyboard-focus;

    &:focus,
    &:focus-within {
        &:after {
            opacity: 1;
        }
    }

    & > * {
        // this fixes some unwanted layout issues
        // which might be very hard for consumers to realize
        // and fix by themselves. Without this code,
        // if the consumer sets for example paddings in
        // what they send to the popover, their element
        // can grow larger than the popover surface
        box-sizing: border-box;
    }
}

import { Component, Element, h, Prop, Watch } from '@stencil/core';
import { OpenDirection } from '../menu/menu.types';
import {
    createPopper,
    Instance,
    OptionsGeneric,
    Placement,
} from '@popperjs/core';
import { FlipModifier } from '@popperjs/core/lib/modifiers/flip';

const IS_VISIBLE_CLASS = 'is-visible';
const IS_HIDING_CLASS = 'is-hiding';
const hideAnimationDuration = 300;

/**
 * The portal component provides a way to render children into a DOM node that
 * exist outside the DOM hierarchy of the parent component.
 *
 * When the limel-portal component is used, it creates a new DOM node (a div element)
 * and appends it to a parent element (by default, the body of the document).
 * The child elements of the limel-portal are then moved from
 * their original location in the DOM to this new div element.
 *
 * This technique is often used to overcome CSS stacking context issues,
 * or to render UI elements like modals, dropdowns, tooltips, etc.,
 * that need to visually "break out" of their container.
 *
 * Using this component, we ensure that the content is always rendered in the
 * correct position, and never covers its own trigger, or another component
 * that is opened in the stacking layer. This way, we don't need to worry about
 * z-indexes, or other stacking context issues.
 *
 * :::important
 * There are some caveats when using this component
 *
 * 1. Events might not bubble up as expected since the content is moved out to
 * another DOM node.
 * 2. Any styling that is applied to content from the parent will be lost, if the
 * content is just another web-component it will work without any issues.
 * Alternatively, use the `style=""` html attribute.
 * 3. Any component that is placed inside the container must have a style of
 * `max-height: inherit`. This ensures that its placement is calculated
 * correctly in relation to the trigger, and that it never covers its own
 * trigger.
 * 4. When the node is moved in the DOM, `disconnectedCallback` and
 * `connectedCallback` will be invoked, so if `disconnectedCallback` is used
 * to do any tear-down, the appropriate setup will have to be done again on
 * `connectedCallback`.
 * :::
 *
 * @slot - Content to put inside the portal
 * @private
 * @exampleComponent limel-example-portal-basic
 */
@Component({
    tag: 'limel-portal',
    shadow: true,
    styleUrl: 'portal.scss',
})
export class Portal {
    /**
     * Decides which direction the portal content should open.
     */
    @Prop({ reflect: true })
    public openDirection: OpenDirection = 'bottom';

    /**
     * Position of the content.
     */
    @Prop({ reflect: true })
    public position: 'fixed' | 'absolute' = 'absolute';

    /**
     * A unique ID.
     */
    @Prop({ reflect: true })
    public containerId: string;

    /**
     * Dynamic styling that can be applied to the container holding the content.
     */
    @Prop()
    public containerStyle: object = {};

    /**
     * Used to make a dropdown have the same width as the trigger, for example
     * in `limel-picker`.
     */
    @Prop({ reflect: true })
    public inheritParentWidth = false;

    /**
     * True if the content within the portal should be visible.
     *
     * If the content is from within a dialog for instance, this can be set to
     * true from false when the dialog opens to position the content properly.
     */
    @Prop({ reflect: true })
    public visible = false;

    /**
     * The element that the content should be positioned relative to.
     * Defaults to the limel-portal element.
     */
    @Prop()
    public anchor?: HTMLElement = null;

    @Element()
    private host: HTMLLimelPortalElement;

    private parents: WeakMap<HTMLElement, HTMLElement>;
    private container: HTMLElement;
    private popperInstance: Instance;
    private loaded = false;
    private observer: ResizeObserver;

    constructor() {
        this.parents = new WeakMap();
    }

    public disconnectedCallback() {
        this.removeContainer();
        this.destroyPopper();
        if (this.observer && this.container) {
            this.observer.unobserve(this.container);
        }

        this.container = null;
    }

    public connectedCallback() {
        if (!this.loaded) {
            return;
        }

        if (this.visible) {
            this.init();
        }
    }

    public componentDidLoad() {
        this.loaded = true;
        this.connectedCallback();
    }

    private init() {
        if (!this.host.isConnected) {
            return;
        }

        this.createContainer();
        this.hideContainer();
        this.attachContainer();
        this.styleContainer();

        if (this.visible) {
            this.createPopper();
            this.showContainer();
        }

        if ('ResizeObserver' in window) {
            this.observer = new ResizeObserver(() => {
                if (this.popperInstance) {
                    this.styleContainer();
                    this.popperInstance.update();
                }
            });
            this.observer.observe(this.container);
        }
    }

    public render() {
        return <slot />;
    }

    @Watch('visible')
    protected onVisible() {
        if (!this.container && this.visible) {
            this.init();

            return;
        }

        if (!this.visible) {
            this.animateHideAndCleanup();

            return;
        }

        this.styleContainer();
        this.createPopper();
        requestAnimationFrame(() => {
            this.showContainer();
        });
    }

    private createContainer() {
        const slot: HTMLSlotElement =
            this.host.shadowRoot.querySelector('slot');
        const content =
            (slot.assignedElements && slot.assignedElements()) || [];

        this.container = document.createElement('div');
        this.container.setAttribute('id', this.containerId);
        this.container.setAttribute('class', 'limel-portal--container');
        Object.assign(this.container, {
            portalSource: this.host,
        });

        // eslint-disable-next-line unicorn/no-array-for-each
        content.forEach((element: HTMLElement) => {
            this.parents.set(element, element.parentElement);
            this.container.append(element);
        });
    }

    private attachContainer() {
        this.getParent().append(this.container);
    }

    private removeContainer() {
        if (!this.container) {
            return;
        }

        // eslint-disable-next-line unicorn/no-array-for-each
        [...this.container.children].forEach((element: HTMLElement) => {
            const parent = this.parents.get(element);
            if (!parent) {
                return;
            }

            parent.append(element);
        });

        this.container.remove();
    }

    private hideContainer() {
        if (!this.container) {
            return;
        }

        this.container.classList.remove(IS_VISIBLE_CLASS);
    }

    private showContainer() {
        this.container.classList.add(IS_VISIBLE_CLASS);
    }

    private animateHideAndCleanup() {
        if (!this.container) {
            return;
        }

        this.container.classList.add(IS_HIDING_CLASS);
        this.styleContainer();

        setTimeout(() => {
            this.container.classList.remove(IS_HIDING_CLASS);
            if (!this.visible) {
                this.container.classList.remove(IS_VISIBLE_CLASS);
                this.destroyPopper();
            }
        }, hideAnimationDuration);
    }

    private styleContainer() {
        this.setContainerWidth();
        this.setContainerHeight();
        this.setContainerStyles();
    }

    private setContainerWidth() {
        const hostWidth = this.host.getBoundingClientRect().width;

        if (this.inheritParentWidth) {
            const containerWidth = this.getContentWidth(this.container);
            let width = containerWidth;
            if (hostWidth > 0) {
                width = hostWidth;
            }

            this.container.style.width = `${width}px`;
        }
    }

    private getContentWidth(element: HTMLElement | Element) {
        if (!element) {
            return null;
        }

        const width = element.getBoundingClientRect().width;
        if (width !== 0) {
            return width;
        }

        const elementContent = element.querySelector('*');

        return this.getContentWidth(elementContent);
    }

    private setContainerStyles() {
        for (const property of Object.keys(this.containerStyle)) {
            this.container.style[property] = this.containerStyle[property];
        }
    }

    private createPopper() {
        const config = this.createPopperConfig();

        this.popperInstance = createPopper(
            this.anchor || this.host,
            this.container,
            config
        );
    }

    private destroyPopper() {
        this.popperInstance?.destroy();
        this.popperInstance = null;
    }

    private createPopperConfig(): Partial<
        OptionsGeneric<Partial<FlipModifier>>
    > {
        const placement = this.getPlacement(this.openDirection);
        const flipPlacement = this.getFlipPlacement(this.openDirection);

        return {
            strategy: this.position,
            placement: placement,
            modifiers: [
                {
                    name: 'flip',
                    options: {
                        fallbackPlacements: [flipPlacement],
                    },
                },
            ],
        };
    }

    private getPlacement(direction: OpenDirection): Placement {
        const placements: Record<OpenDirection, Placement> = {
            'left-start': 'left-start',
            left: 'left',
            'left-end': 'left-end',
            'right-start': 'right-start',
            right: 'right',
            'right-end': 'right-end',
            'top-start': 'top-start',
            top: 'top',
            'top-end': 'top-end',
            'bottom-start': 'bottom-start',
            bottom: 'bottom',
            'bottom-end': 'bottom-end',
        };

        return placements[direction];
    }

    private getFlipPlacement(direction: OpenDirection): Placement {
        const flipPlacements: Record<OpenDirection, Placement> = {
            'left-start': 'right-start',
            left: 'right',
            'left-end': 'right-end',
            'right-start': 'left-start',
            right: 'left',
            'right-end': 'left-end',
            'top-start': 'bottom-start',
            top: 'bottom',
            'top-end': 'bottom-end',
            'bottom-start': 'top-start',
            bottom: 'top',
            'bottom-end': 'top-end',
        };

        return flipPlacements[direction];
    }

    private setContainerHeight() {
        const viewHeight = Math.max(
            document.documentElement.clientHeight || 0,
            window.innerHeight || 0
        );

        const { top, bottom } = this.host.getBoundingClientRect();
        const spaceAboveTopOfSurface = Math.max(top, 0);
        const spaceBelowTopOfSurface = Math.max(viewHeight - bottom, 0);
        const extraCosmeticSpace = 16;
        const maxHeight =
            Math.max(spaceAboveTopOfSurface, spaceBelowTopOfSurface) -
            extraCosmeticSpace;

        this.container.style.maxHeight = `${maxHeight}px`;
    }

    // Returns the parent element where the content of the portal will be moved to.
    // It needs to have styling of the portal container.
    private getParent() {
        let element: Element | undefined = this.anchor || this.host;

        while (element) {
            const parent = element.closest('.limel-portal--parent');
            if (parent) {
                return parent;
            }

            element = (element.getRootNode() as ShadowRoot).host;
        }

        return document.body;
    }
}

