import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
} from '@stencil/core';
import { FlowItem } from '../progress-flow.types';
import { getIconName } from '../../icon/get-icon-props';

/**
 * @private
 */
@Component({
    tag: 'limel-progress-flow-item',
    shadow: false,
    styleUrl: 'progress-flow-item.scss',
})
export class ProgressFlowItem {
    @Element()
    public element: HTMLLimelProgressFlowItemElement;

    /**
     * The flow item that should be rendered
     */
    @Prop()
    public item: FlowItem = null;

    /**
     * True if the flow item should be disabled
     */
    @Prop()
    public disabled = false;

    /**
     * True if the flow item should be readonly
     */
    @Prop()
    public readonly = false;

    /**
     * True for current step
     */
    @Prop()
    public currentStep: boolean = false;

    /**
     * Fired when clicking on the flow item
     */
    @Event()
    public interact: EventEmitter<void>;

    public render() {
        if (!this.item) {
            return;
        }

        return [
            <button
                tabindex="0"
                title={this.getToolTipText()}
                type="button"
                class={{
                    step: true,
                    selected: this.item?.selected,
                    disabled: this.isDisabled(),
                    readonly: this.readonly,
                }}
                onClick={this.handleClick}
                disabled={this.isDisabled()}
                aria-current={this.currentStep ? 'step' : null}
            >
                {this.renderIcon()}
                <span class="text">{this.item.text}</span>
                {this.renderDivider()}
                {this.renderLockIcon()}
            </button>,
            this.renderSecondaryText(),
        ];
    }

    private isDisabled() {
        return this.item?.disabled || this.readonly || this.disabled;
    }

    private getToolTipText() {
        if (!this.item.secondaryText) {
            return this.item.text;
        }

        return [this.item.text, this.item.secondaryText].join(' Â· ');
    }

    private handleClick = () => {
        this.interact.emit();
    };

    private renderSecondaryText() {
        if (!this.item?.secondaryText) {
            return;
        }

        return <div class="secondary-text">{this.item.secondaryText}</div>;
    }

    private renderIcon() {
        if (!this.item.icon) {
            return;
        }

        const name = getIconName(this.item.icon);

        return <limel-icon name={name} size="small" class="icon" />;
    }

    private renderDivider() {
        if (this.item.isOffProgress) {
            return;
        }

        return <div class="divider" />;
    }

    private renderLockIcon() {
        if (!this.isDisabled()) {
            return;
        }

        return <limel-icon name="lock" class="lock-icon" />;
    }
}

@use '../../../style/mixins';
@use '../../../style/functions';

$limel-progress-flow-step-content: 2;
$limel-progress-flow-divider: 1;

.flow-item {
    --step-background: var(
        --progress-flow-step-background-color,
        rgb(var(--contrast-600))
    );
    --step-background--selected: var(
        --progress-flow-step-background-color--selected,
        var(--mdc-theme-primary)
    );
    --step-background--passed: var(
        --progress-flow-step-background-color--passed,
        var(--step-background--selected)
    );

    --step-text: var(
        --progress-flow-step-text-color,
        rgb(var(--contrast-1200))
    );
    --step-text--selected: var(
        --progress-flow-step-text-color--selected,
        var(--mdc-theme-on-primary)
    );
    --step-text--passed: var(
        --progress-flow-step-text-color--passed,
        var(--step-text--selected)
    );

    --step-divider-color: var(
        --progress-flow-step-divider-color,
        rgb(var(--contrast-100))
    );

    position: relative;
    width: 100%;

    display: flex;
    flex-direction: column;
    align-items: stretch;

    &:not(.off-progress-item, .last) {
        .divider {
            &:after {
                content: '';
            }
        }
    }

    &.off-progress-item {
        padding-left: functions.pxToRem(8);
    }

    &.first-off-progress-item {
        padding-left: functions.pxToRem(16);
    }
}

.step {
    transition:
        background-color 0.2s ease,
        box-shadow 0.2s ease;

    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    width: 100%;
    height: var(--step-height);
    border: none;
    font-size: functions.pxToRem(14);

    &.disabled {
        cursor: not-allowed;

        &.readonly {
            opacity: 1;
            cursor: default;
        }
    }

    &:focus {
        outline: none;
    }

    &:focus-visible {
        box-shadow: var(--shadow-depth-8-focused);
    }

    .flow-item:not(.off-progress-item, .first) & {
        padding-left: calc(var(--step-height) / 2);
    }

    .flow-item:not(.selected) & {
        &:not(.disabled) {
            cursor: pointer;
            &:hover {
                box-shadow: var(--button-shadow-normal);
            }
            &:active {
                box-shadow: var(--button-shadow-pressed);
            }
        }
    }

    .off-progress-item & {
        border-radius: functions.pxToRem(4);
        padding: 0 functions.pxToRem(12);

        .icon {
            margin-left: 0;
        }
    }
    .flow-item.first & {
        border-top-left-radius: var(--step-height);
        border-bottom-left-radius: var(--step-height);
        padding-left: functions.pxToRem(20);
    }
    .flow-item.last & {
        border-top-right-radius: var(--step-height);
        border-bottom-right-radius: var(--step-height);
        padding-right: functions.pxToRem(20);
    }
}

.divider {
    display: flex;
    align-items: center;
    justify-content: center;
    width: var(--step-height);
    height: var(--step-height);

    position: absolute;
    z-index: $limel-progress-flow-divider;
    right: calc(var(--step-height) / 2 * -1);
    overflow: hidden;

    &:after {
        position: absolute;
        display: block;

        box-sizing: border-box;
        transition: background-color 0.2s ease;

        width: 100%;
        height: 100%;
        right: calc(var(--step-height) / 5);
        transform: rotate(45deg);
        border: {
            style: solid;
            width: functions.pxToRem(2) functions.pxToRem(2) 0 0;
            radius: 0 functions.pxToRem(8) 0 0;
            color: var(--step-divider-color);
        }
    }
    .flow-item.last & {
        width: 0;
    }
}

.text,
.secondary-text {
    @include mixins.truncate-text();
    max-width: var(--max-text-width);
    z-index: $limel-progress-flow-step-content;
}

.secondary-text {
    font-size: functions.pxToRem(12);
    margin: auto;
    padding-left: calc(var(--step-height) / 2);
}

.icon {
    margin: 0 functions.pxToRem(8) 0 functions.pxToRem(4);
    z-index: $limel-progress-flow-step-content;
}

