import { Component, Event, EventEmitter, Host, Prop, h } from '@stencil/core';
import { FormComponent } from '../form/form.types';
import { Languages } from '../date-picker/date.types';
import { createRandomString } from '../../util/random-string';
import { CustomElementDefinition } from '../../global/shared-types/custom-element.types';
import {
    TriggerCharacter,
    TriggerEventDetail,
    ImageInserter,
    EditorImage,
    EditorMetadata,
} from './text-editor.types';
import { EditorUiType } from './types';

/**
 * A rich text editor that offers a rich text editing experience with markdown support,
 * in the sense that you can easily type markdown syntax and see the rendered
 * result as rich text in real-time. For instance, you can type `# Hello, world!`
 * and see it directly turning to a heading 1 (an `<h1>` HTML element).
 *
 * Naturally, you can use standard keyboard hotkeys such as <kbd>Ctrl</kbd> + <kbd>B</kbd>
 * to toggle bold text, <kbd>Ctrl</kbd> + <kbd>I</kbd> to toggle italic text, and so on.
 *
 * @exampleComponent limel-example-text-editor-basic
 * @exampleComponent limel-example-text-editor-as-form-component
 * @exampleComponent limel-example-text-editor-with-markdown
 * @exampleComponent limel-example-text-editor-with-html
 * @exampleComponent limel-example-text-editor-with-tables
 * @exampleComponent limel-example-text-editor-with-inline-images-file-storage
 * @exampleComponent limel-example-text-editor-with-inline-images-base64
 * @exampleComponent limel-example-text-editor-allow-resize
 * @exampleComponent limel-example-text-editor-size
 * @exampleComponent limel-example-text-editor-ui
 * @exampleComponent limel-example-text-editor-custom-element
 * @exampleComponent limel-example-text-editor-triggers
 * @exampleComponent limel-example-text-editor-composite
 * @beta
 */
@Component({
    tag: 'limel-text-editor',
    shadow: { delegatesFocus: true },
    styleUrl: 'text-editor.scss',
})
export class TextEditor implements FormComponent<string> {
    /**
     * The type of content that the editor should handle and emit, defaults to `markdown`
     *
     * Assumed to be set only once, so not reactive to changes
     */
    @Prop()
    public contentType: 'markdown' | 'html' = 'markdown';

    /**
     * Defines the language for translations.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * Set to `true` to disable the field.
     * Use `disabled` to indicate that the field can normally be interacted
     * with, but is currently disabled. This tells the user that if certain
     * requirements are met, the field may become enabled again.
     */
    @Prop({ reflect: true })
    public disabled?: boolean = false;

    /**
     * Set to `true` to make the component read-only.
     * Use `readonly` when the field is only there to present the data it holds,
     * and will not become possible for the current user to edit.
     * :::note
     * Consider that it might be better to use `limel-markdown`
     * instead of `limel-text-editor` when the goal is visualizing data.
     * :::
     */
    @Prop({ reflect: true })
    public readonly?: boolean = false;

    /**
     * Optional helper text to display below the input field when it has focus
     */
    @Prop({ reflect: true })
    public helperText?: string;

    /**
     * The placeholder text shown inside the input field,
     * when the field is empty.
     */
    @Prop({ reflect: true })
    public placeholder?: string;

    /**
     * The label of the editor
     */
    @Prop({ reflect: true })
    public label?: string;

    /**
     * Set to `true` to indicate that the current value of the editor is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid?: boolean = false;

    /**
     * Description of the text inside the editor as markdown
     */
    @Prop({ reflect: true })
    public value: string;

    /**
     * A list of custom elements
     *
     * Any `CustomElement` that should be used inside the text editor needs
     * to be defined here.
     *
     * @private
     * @alpha
     */
    @Prop()
    public customElements: CustomElementDefinition[] = [];

    /**
     * A set of trigger characters
     *
     * Defining a character here will enable trigger events to be sent if the
     * character is detected in the editor.
     *
     * @private
     * @alpha
     */
    @Prop()
    public triggers: TriggerCharacter[] = [];

    /**
     * Set to `true` to indicate that the field is required.
     *
     * :::important
     * An empty but required field is not automatically considered invalid.
     * You must make sure to check the validity of the field on your own,
     * and properly handle the `invalid` state.
     * :::
     */
    @Prop({ reflect: true })
    public required?: boolean = false;

    /**
     * Set to `true` to allow the user to vertically resize the editor.
     * Set to `false` to disable the resize functionality.
     */
    @Prop({ reflect: true })
    public allowResize: boolean = true;

    /**
     * Specifies the visual appearance of the editor.
     *
     * - `standard`: The default editor appearance with a full toolbar and
     *    standard layout.
     * - `minimal`: A compact editor appearance, ideal for limited space
     *    scenarios such as mobile devices. In this mode, the toolbar is hidden
     *    until the editor is focused.
     * - `no-toolbar`: A basic textarea appearance without any text styling toolbar.
     *    This mode is suitable for scenarios where you want to provide a simple
     *    text input without any visible formatting options; but still provide
     *    support for markdown syntax and rich text, using hotkeys or when pasting.
     */
    @Prop({ reflect: true })
    public ui?: EditorUiType = 'standard';

    /**
     * Dispatched when a change is made to the editor
     */
    @Event()
    public change: EventEmitter<string>;

    /**
     * Dispatched when a image is pasted into the editor
     *
     * @private
     * @alpha
     */
    @Event()
    private readonly imagePasted: EventEmitter<ImageInserter>;

    /**
     * Dispatched when a image is removed from the editor
     *
     * @private
     * @alpha
     * @deprecated - This event is deprecated and will be removed in a future version.
     * Use the `metadataChange` event instead to track image removals.
     */
    @Event()
    private readonly imageRemoved: EventEmitter<EditorImage>;

    /**
     * Dispatched when the metadata of the editor changes
     *
     * @private
     * @alpha
     */
    @Event()
    private readonly metadataChange: EventEmitter<EditorMetadata>;

    /**
     * Dispatched if a trigger character is detected.
     *
     * @private
     * @alpha
     */
    @Event()
    public triggerStart: EventEmitter<TriggerEventDetail>;

    /**
     * Dispatched if a trigger session is ended. That is if the selection
     * goes outside the trigger input or if something is inserted using the
     * supplied `TextEditor` insert function.
     *
     * @private
     * @alpha
     */
    @Event()
    public triggerStop: EventEmitter<TriggerEventDetail>;

    /**
     * Dispatched if a input is changed during an active trigger.
     *
     * @private
     * @alpha
     */
    @Event()
    public triggerChange: EventEmitter<TriggerEventDetail>;

    private readonly helperTextId: string;
    private readonly editorId: string;

    public constructor() {
        this.helperTextId = createRandomString();
        this.editorId = createRandomString();
    }

    public render() {
        return (
            <Host>
                <limel-notched-outline
                    labelId={this.editorId}
                    label={this.label}
                    required={this.required}
                    invalid={this.invalid}
                    disabled={this.disabled}
                    readonly={this.readonly}
                    hasValue={!!this.value}
                    hasFloatingLabel={true}
                >
                    {this.renderEditor()}
                    {this.renderPlaceholder()}
                </limel-notched-outline>
                {this.renderHelperLine()}
            </Host>
        );
    }

    private renderEditor() {
        if (this.readonly) {
            return (
                <limel-markdown
                    slot="content"
                    value={this.value}
                    aria-controls={this.helperTextId}
                    id={this.editorId}
                />
            );
        }

        return (
            <limel-prosemirror-adapter
                slot="content"
                aria-placeholder={this.placeholder}
                contentType={this.contentType}
                onChange={this.handleChange}
                onImagePasted={this.handleImagePasted}
                onImageRemoved={this.handleImageRemoved}
                onMetadataChange={this.handleMetadataChange}
                customElements={this.customElements}
                value={this.value}
                aria-controls={this.helperTextId}
                id={this.editorId}
                aria-disabled={this.disabled}
                aria-invalid={this.invalid}
                aria-required={this.required}
                language={this.language}
                triggerCharacters={this.triggers}
                disabled={this.disabled}
                ui={this.ui}
            />
        );
    }

    private renderPlaceholder() {
        if (!this.placeholder || this.value) {
            return;
        }

        return (
            <span class="placeholder" aria-hidden="true" slot="content">
                {this.placeholder}
            </span>
        );
    }

    private renderHelperLine = () => {
        if (!this.helperText) {
            return;
        }

        return (
            <limel-helper-line
                helperText={this.helperText}
                helperTextId={this.helperTextId}
                invalid={this.isInvalid()}
            />
        );
    };

    private isInvalid = () => {
        if (this.readonly) {
            // A readonly field can never be invalid.
            return false;
        }

        if (this.invalid) {
            return true;
        }
    };

    private handleChange = (event: CustomEvent<string>) => {
        event.stopPropagation();
        this.change.emit(event.detail);
    };

    private handleImagePasted = (event: CustomEvent<ImageInserter>) => {
        event.stopPropagation();
        this.imagePasted.emit(event.detail);
    };

    private handleMetadataChange = (event: CustomEvent<EditorMetadata>) => {
        event.stopPropagation();
        this.metadataChange.emit(event.detail);
    };

    private handleImageRemoved = (event: CustomEvent<EditorImage>) => {
        event.stopPropagation();

        this.imageRemoved.emit(event.detail);
    };
}

@use '../../style/internal/shared_input-select-picker';
@use '../../style/mixins.scss';

/**
 * @prop --text-editor-max-height: the tallest height the text editor can become when auto-resizing itself. Defaults to `calc(100vh - (env(safe-area-inset-top) + env(safe-area-inset-bottom)) - 4rem)`.
 * @prop --text-editor-fade-out-background-color: the color of the fade-out effect at the top and bottom of the text editor, when the text-editor is in readonly state. Defaults to rgb(var(--contrast-100)).
 */

* {
    box-sizing: border-box;
}

$min-height: 5rem;
$min-height-condensed: calc($min-height / 2);

:host(limel-text-editor) {
    --limel-notched-outline-z-index: 2; // since `div.toolbar` has `z-index: 1;`
    --limel-prosemirror-adapter-toolbar-opacity: 0.6;
    --limel-text-editor-padding: 0.25rem 1rem 0.75rem 1rem;
    --limel-prosemirror-adapter-toolbar-grid-template-rows: 1fr;
    --limel-prosemirror-adapter-toolbar-grid-template-rows-transition-duration: 0.3s;
    --limel-prosemirror-adapter-toolbar-transition-timing-function: cubic-bezier(
        0.19,
        0.23,
        0.26,
        0.89
    );

    position: relative;
    isolation: isolate;
    display: flex;
    flex-direction: column;

    width: 100%;
    min-width: 5rem;
    min-height: $min-height;
    height: 100%;
    max-height: var(
        --text-editor-max-height,
        calc(
            100vh - (env(safe-area-inset-top) + env(safe-area-inset-bottom)) -
                4rem
        )
    );
    padding: 1px; // prevents visual defects that can appear due to the backdrop-filter and closeness to borders

    limel-notched-outline {
        height: 100%; // overrides the default `height: fit-content;`
    }
}

:host(limel-text-editor:focus-within),
:host(limel-text-editor:not([ui='minimal']):hover) {
    --limel-prosemirror-adapter-toolbar-opacity: 1;
}

:host(limel-text-editor[ui='minimal']:not(:focus-within)) {
    --limel-prosemirror-adapter-toolbar-grid-template-rows: 0fr;
    --limel-prosemirror-adapter-toolbar-grid-template-rows-transition-duration: 0.46s;
    --limel-prosemirror-adapter-action-bar-padding-top-bottom: 0;
    --limel-prosemirror-adapter-toolbar-opacity: 0;
}

:host(limel-text-editor[ui='minimal']:not(:focus-within)),
:host(limel-text-editor[ui='no-toolbar']) {
    --limel-text-editor-padding: 0.75rem 1rem 0.75rem 1rem;
    --limel-text-editor-placeholder-top: 0;

    min-height: $min-height-condensed;
    limel-prosemirror-adapter {
        min-height: $min-height-condensed;
    }
}

:host(limel-text-editor:focus-within),
:host(limel-text-editor:focus) {
    .placeholder {
        opacity: 0;
    }
}

:host(limel-text-editor[disabled]:not([disabled='false'])) {
    limel-prosemirror-adapter {
        @include shared_input-select-picker.looks-disabled;
        pointer-events: none;
    }
}

:host(limel-text-editor[readonly]:not([readonly='false'])) {
    --limel-text-editor-padding: 0.75rem 1rem 0.75rem 1rem;
    --limel-text-editor-placeholder-top: 0;

    limel-markdown {
        // displayed when `readonly` instead of the adapter
        display: block;
        padding: var(--limel-text-editor-padding);

        overflow-y: auto;
        -webkit-overflow-scrolling: touch;
        height: 100%;

        &:before,
        &:after {
            z-index: 1;
            pointer-events: none;
            content: '';
            display: block;
            position: absolute;
            width: 100%;
        }
        &:after {
            height: 1.75rem;
            top: 0;
            background: linear-gradient(
                var(
                    --text-editor-fade-out-background-color,
                    rgb(var(--contrast-100))
                ),
                transparent
            );
        }

        &:before {
            height: 2rem;
            bottom: -0.25rem;
            background: linear-gradient(
                transparent,
                var(
                    --text-editor-fade-out-background-color,
                    rgb(var(--contrast-100))
                )
            );
        }
    }
}

.placeholder {
    transition-property: top;
    transition-duration: var(
        --limel-prosemirror-adapter-toolbar-grid-template-rows-transition-duration
    );
    transition-timing-function: var(
        --limel-prosemirror-adapter-toolbar-transition-timing-function
    );
    @include mixins.truncate-text;
    pointer-events: none;
    position: absolute;
    top: var(--limel-text-editor-placeholder-top, 2.25rem);
    left: 0;
    right: 0;

    padding: var(--limel-text-editor-padding);
    font-style: italic;
    font-size: 0.875rem;
    color: shared_input-select-picker.$input-placeholder-color;
}

limel-prosemirror-adapter {
    flex-grow: 1;

    min-width: 0;
    min-height: $min-height;
    height: 100%; // this can be overwritten by user's manual resizing
    max-height: 100%;
    overflow: hidden auto;
    -webkit-overflow-scrolling: touch;
}

@include mixins.hide-helper-line-when-not-needed(limel-text-editor);

:host(limel-text-editor[allow-resize]) {
    limel-prosemirror-adapter {
        resize: vertical;
    }
}

import { Component, Prop, h, Event, EventEmitter } from '@stencil/core';
import { EditorTextLink } from '../prosemirror-adapter/menu/types';
import { Languages } from '../../date-picker/date.types';
import translate from '../../../global/translations';
import { LimelInputFieldCustomEvent } from '../../../components';
import { ENTER, ESCAPE } from '../../../util/keycodes';

/**
 * This component is a menu for editing a link in the text editor.
 * It allows the user to input the text and url for the link.
 * @beta
 * @private
 */
@Component({
    tag: 'limel-text-editor-link-menu',
    shadow: true,
    styleUrl: 'editor-link-menu.scss',
})
export class TextEditorLinkMenu {
    /**
     * The link
     */
    @Prop({ reflect: true })
    public link: EditorTextLink;

    /**
     * Defines the language for translations.
     */
    @Prop({ reflect: true })
    public language: Languages = 'en';

    /**
     * Open state of the link-menu dialog
     */
    @Prop({ reflect: true })
    public isOpen: boolean = false;

    /**
     * Emitted when the menu is closed from inside the component.
     * (*Not* emitted when the consumer sets the `open`-property to `false`.)
     */
    @Event()
    private cancel: EventEmitter<void>;

    /**
     * Emitted when the menu is saved from inside the component.
     */
    @Event()
    private save: EventEmitter<void>;

    /**
     * Emitted when the user inputs new values for the link
     */
    @Event()
    private linkChange: EventEmitter<EditorTextLink>;

    private textInput: HTMLLimelInputFieldElement;
    private saveButton: HTMLLimelButtonElement;

    public connectedCallback() {
        this.setupGlobalHandlers();
    }

    public disconnectedCallback() {
        this.teardownGlobalHandlers();
    }

    private setupGlobalHandlers() {
        if (this.isOpen) {
            document.addEventListener('keyup', this.handleCancel);
        }
    }

    private teardownGlobalHandlers() {
        document.removeEventListener('keyup', this.handleCancel);
    }

    public componentDidLoad() {
        this.focusOnTextInput();
    }

    private focusOnTextInput() {
        if (this.textInput) {
            const inputField = this.textInput.shadowRoot.querySelector('input');
            if (inputField) {
                requestAnimationFrame(() => {
                    inputField.focus();
                });
            }
        }
    }

    public render() {
        const isValid = this.isValid(this.link.href);

        return [
            <limel-input-field
                label={this.getTranslation('editor-link-menu.text')}
                value={this.link?.text || ''}
                leadingIcon="text_cursor"
                onChange={this.handleLinkTitleChange}
                onKeyDown={this.handleKeyDown}
                ref={(el) =>
                    (this.textInput = el as HTMLLimelInputFieldElement)
                }
            />,
            <limel-input-field
                label={this.getTranslation('editor-link-menu.link')}
                value={this.link?.href || ''}
                type="text"
                leadingIcon="-lime-text-link"
                trailingIcon="external_link"
                invalid={!isValid}
                onChange={this.handleLinkValueChange}
                onAction={this.handleLinkInputAction}
                onKeyDown={this.handleKeyDown}
            />,
            <div class="actions">
                <limel-button
                    label={this.getTranslation('cancel')}
                    onClick={this.handleCancel}
                />
                <limel-button
                    primary={true}
                    label={this.getTranslation('save')}
                    disabled={!isValid}
                    onClick={this.handleSave}
                    ref={(el) =>
                        (this.saveButton = el as HTMLLimelButtonElement)
                    }
                    slot="button"
                />
            </div>,
        ];
    }

    private getTranslation = (key: string) => {
        return translate.get(key, this.language);
    };

    private isValid = (href: string): boolean => {
        try {
            new URL(href);
        } catch {
            return false;
        }

        return true;
    };

    private handleKeyDown = (event: KeyboardEvent) => {
        if (event.key !== ENTER) {
            return;
        }

        if (this.saveButton) {
            this.saveButton.focus();
        }

        event.preventDefault();
        if (this.isValid(this.link?.href)) {
            this.handleSave(event);
        }
    };

    private handleCancel = (event: MouseEvent | KeyboardEvent) => {
        if (event instanceof KeyboardEvent && event.key !== ESCAPE) {
            return;
        }

        event.stopPropagation();
        event.preventDefault();
        this.cancel.emit();
    };

    private handleSave = (event: MouseEvent | KeyboardEvent) => {
        event.stopPropagation();

        this.save.emit();
    };

    private handleLinkInputAction = (
        event: LimelInputFieldCustomEvent<void>
    ) => {
        window.open(this.link.href, '_blank');
        event.stopPropagation();
    };

    private handleLinkTitleChange = (event: CustomEvent<string>) => {
        this.emitLinkChange(event.detail, this.link?.href);
    };

    private handleLinkValueChange = (event: CustomEvent<string>) => {
        const href = event.detail;

        this.emitLinkChange(this.link?.text, href);
    };

    private emitLinkChange = (text: string, href: string) => {
        const newLink: EditorTextLink = {
            text: text,
            href: href,
        };

        this.linkChange.emit(newLink);
    };
}

:host(limel-text-editor-link-menu) {
    animation: fade 0.2s ease forwards;
